/**
 * API Endpoint : M√©triques de Cache
 *
 * GET /api/cache-metrics
 *
 * Retourne les statistiques du cache Redis en temps r√©el :
 * - Hit rate
 * - Latence moyenne
 * - Erreurs
 *
 * S√©curit√© : √Ä prot√©ger avec authentification admin en prod
 *
 * @example
 * fetch('/api/cache-metrics')
 *   .then(res => res.json())
 *   .then(data => console.log(data.metrics))
 */

import { NextRequest, NextResponse } from 'next/server';
import { CacheMetrics } from '@/lib/cache/advanced-patterns';
import { createClient } from '@/utils/supabase/server';

export async function GET(_request: NextRequest) {
  try {
    // S√âCURIT√â : V√©rifier que c'est un admin
    // √Ä d√©commenter en production
    /*
    const supabase = await createClient();
    const { data: { user } } = await supabase.auth.getUser();

    if (!user) {
      return NextResponse.json(
        { error: 'Non authentifi√©' },
        { status: 401 }
      );
    }

    const { data: profile } = await supabase
      .from('profiles')
      .select('role')
      .eq('id', user.id)
      .single();

    if (profile?.role !== 'admin') {
      return NextResponse.json(
        { error: 'Acc√®s refus√©' },
        { status: 403 }
      );
    }
    */

    // R√©cup√©rer les m√©triques
    const stats = CacheMetrics.getStats();

    return NextResponse.json({
      success: true,
      metrics: stats,
      timestamp: new Date().toISOString(),
      env: process.env.NODE_ENV,
    });
  } catch (error: unknown) {
    console.error('Error fetching cache metrics:', error);

    return NextResponse.json(
      {
        success: false,
        error: 'Erreur lors de la r√©cup√©ration des m√©triques',
        details: error instanceof Error ? error.message : 'Unknown error',
      },
      { status: 500 }
    );
  }
}

/**
 * POST : Reset des m√©triques (dev/testing only)
 */
export async function POST(_request: NextRequest) {
  try {
    // S√âCURIT√â : Bloquer en production
    if (process.env.NODE_ENV === 'production') {
      return NextResponse.json(
        { error: 'Reset interdit en production' },
        { status: 403 }
      );
    }

    CacheMetrics.reset();

    return NextResponse.json({
      success: true,
      message: 'M√©triques r√©initialis√©es',
      timestamp: new Date().toISOString(),
    });
  } catch (error: unknown) {
    console.error('Error resetting cache metrics:', error);

    return NextResponse.json(
      {
        success: false,
        error: 'Erreur lors de la r√©initialisation',
        details: error instanceof Error ? error.message : 'Unknown error',
      },
      { status: 500 }
    );
  }
}

import { getKKiaPayConfig, type KKiaPayWebhookPayload } from "@/lib/kkiapay";
import { createClient } from "@/utils/supabase/server";
import { sendEmail } from "@/lib/mail";
import { invalidateRentalCaches } from "@/lib/cache/invalidation";
import { invalidateCacheBatch } from "@/lib/cache/cache-aside";

/**
 * Webhook KKiaPay pour les notifications serveur-√†-serveur
 * URL √† configurer dans le dashboard KKiaPay: https://votredomaine.com/api/kkiapay/webhook
 */
export async function POST(request: Request) {
  try {
    const signature = request.headers.get("x-kkiapay-signature");
    const secret = request.headers.get("x-kkiapay-secret");
    const rawBody = await request.text();

    // Log pour debugging
    console.log("üì• Webhook KKiaPay re√ßu:", {
      hasSignature: !!signature,
      hasSecret: !!secret,
      bodyLength: rawBody.length,
    });

    // KKiaPay peut envoyer soit x-kkiapay-signature soit x-kkiapay-secret
    const receivedSecret = signature || secret;

    const isProduction = process.env.NODE_ENV === "production";

    // V√©rifier le secret
    const config = getKKiaPayConfig();
    const secretFromEnv = config.secret;

    if (isProduction && !receivedSecret) {
      console.error("Webhook KKiaPay: Authentification manquante en production");
      return Response.json(
        { error: "Authentification requise" },
        { status: 401 }
      );
    }

    if (receivedSecret && receivedSecret !== secretFromEnv) {
      console.error("Webhook KKiaPay: Secret invalide");
      return Response.json(
        { error: "Secret invalide" },
        { status: 401 }
      );
    }

    if (!receivedSecret && !isProduction) {
      console.warn("‚ö†Ô∏è Webhook KKiaPay re√ßu sans authentification (mode test)");
    }

    const payload: KKiaPayWebhookPayload = JSON.parse(rawBody);

    console.log("‚úÖ Webhook KKiaPay valid√©:", {
      transactionId: payload.transactionId,
      status: payload.status,
      amount: payload.amount,
      metadata: payload.metadata,
    });

    // G√©rer uniquement les paiements r√©ussis
    if (payload.status === "SUCCESS") {
      const metadata = payload.metadata as {
        type?: string;
        lease_id?: string;
        period_month?: number;
        period_year?: number;
      } | undefined;

      // V√©rifier si c'est un paiement de loyer
      if (metadata?.type === "rent" && metadata.lease_id && metadata.period_month && metadata.period_year) {
        // Utiliser un client admin pour les op√©rations de webhook (fiabilit√©)
        const { createClient: createSupabaseAdmin } = await import('@supabase/supabase-js');
        const supabaseAdmin = createSupabaseAdmin(
          process.env.NEXT_PUBLIC_SUPABASE_URL!,
          process.env.SUPABASE_SERVICE_ROLE_KEY!
        );

        // R√©cup√©rer infos du bail
        const { data: lease } = await supabaseAdmin
          .from("leases")
          .select("tenant_email, tenant_name, monthly_amount, owner_id, property_address, owner:profiles(email, full_name, company_address, address)")
          .eq("id", metadata.lease_id)
          .single();

        if (!lease) {
          console.error("‚ùå Webhook KKiaPay: Bail introuvable", metadata.lease_id);
          return Response.json({ success: true });
        }

        // Marquer le loyer comme pay√©
        const { data: updatedTx, error: rentError } = await supabaseAdmin
          .from("rental_transactions")
          .update({
            status: "paid",
            paid_at: new Date().toISOString(),
            payment_ref: payload.transactionId,
            payment_method: "kkiapay",
          })
          .eq("lease_id", metadata.lease_id)
          .eq("period_month", metadata.period_month)
          .eq("period_year", metadata.period_year)
          .select('id')
          .single();

        if (rentError || !updatedTx) {
          console.error("Erreur maj loyer:", rentError);
        } else {
          console.log(`‚úÖ Loyer pay√© via KKiaPay webhook: Bail ${metadata.lease_id}`);

          // --- G√âN√âRATION ET STOCKAGE QUITTANCE PDF ---
          try {
            const { createQuittanceDocument } = await import('@/components/pdf/QuittancePDF_v2');
            const { renderToStream } = await import('@react-pdf/renderer'); // Corrected import for renderToStream
            const { storeDocumentInGED } = await import('@/lib/ged-utils');

            const owner = lease.owner as { email?: string; full_name?: string; company_address?: string; address?: string } | undefined;

            const receiptData = {
              leaseId: metadata.lease_id,
              tenantName: lease.tenant_name,
              tenantEmail: lease.tenant_email,
              amount: lease.monthly_amount,
              periodMonth: `${String(metadata.period_month).padStart(2, '0')}/${metadata.period_year}`,
              periodStart: new Date(metadata.period_year, metadata.period_month - 1, 1).toLocaleDateString('fr-FR'),
              periodEnd: new Date(metadata.period_year, metadata.period_month, 0).toLocaleDateString('fr-FR'),
              receiptNumber: `QUITT-${Date.now().toString().slice(-6)}`,
              ownerName: owner?.full_name || 'Propri√©taire',
              ownerEmail: owner?.email,
              ownerAddress: owner?.company_address || owner?.address || 'Adresse non renseign√©e',
              propertyAddress: lease.property_address || 'Adresse non renseign√©e'
            };

            const pdfDocument = createQuittanceDocument(receiptData);
            // ReactPDF might be a module with named exports or default depending on how it's built.
            // But usually renderToStream is a top level export or on the default instance.
            // If the previous code was require('@react-pdf/renderer'), it got the module.
            // If typical usage is import { renderToStream } ...
            // Let's assume renderToStream is available on the module (or default)
            const stream = await renderToStream(pdfDocument);
            const chunks: Uint8Array[] = [];
            const pdfBuffer = await new Promise<Buffer>((resolve, reject) => {
              stream.on('data', (chunk: any) => chunks.push(chunk));
              stream.on('end', () => resolve(Buffer.concat(chunks)));
              stream.on('error', reject);
            });

            await storeDocumentInGED({
              userId: lease.owner_id,
              fileBuffer: new Uint8Array(pdfBuffer),
              fileName: `Quittance_${receiptData.receiptNumber}_KKIA_${lease.tenant_name.replace(/\s+/g, '_')}.pdf`,
              bucketName: 'receipts',
              documentType: 'quittance',
              metadata: {
                leaseId: metadata.lease_id,
                transactionId: updatedTx.id,
                tenantName: lease.tenant_name,
                description: `Quittance - ${receiptData.periodMonth} - ${lease.tenant_name}`
              }
            }, supabaseAdmin);
            console.log("‚úÖ Quittance PDF g√©n√©r√©e et stock√©e via utility (KKiaPay)");

          } catch (pdfError) {
            console.error("‚ùå Erreur g√©n√©ration PDF Quittance KKiaPay:", pdfError);
          }
          // -------------------------------------------

          // Invalidation du cache
          if (lease?.owner_id) {
            await invalidateRentalCaches(lease.owner_id, metadata.lease_id, {
              invalidateLeases: true,
              invalidateTransactions: true,
              invalidateStats: true,
            });
          }

          if (lease?.tenant_email) {
            await invalidateCacheBatch([
              `tenant_dashboard:${lease.tenant_email}`,
              `tenant_payments:${metadata.lease_id}`,
            ], "rentals");
          }

          // Envoyer email de confirmation
          if (lease && lease.tenant_email) {
            const subject = `Re√ßu de paiement - Loyer ${metadata.period_month}/${metadata.period_year}`;
            const amountFmt = lease.monthly_amount?.toLocaleString("fr-FR");

            const html = `
              <h1>Paiement re√ßu !</h1>
              <p>Bonjour ${lease.tenant_name || "Locataire"},</p>
              <p>Nous confirmons la r√©ception de votre paiement de <strong>${amountFmt} FCFA</strong> pour le loyer de <strong>${metadata.period_month}/${metadata.period_year}</strong>.</p>
              <p>R√©f√©rence de transaction: <code>${payload.transactionId}</code></p>
              <p>Votre quittance est d√©sormais disponible dans votre espace locataire.</p>
              <br/>
              <p>Cordialement,<br/>L'√©quipe Doussel Immo</p>
            `;

            await sendEmail({
              to: lease.tenant_email,
              subject,
              html,
            });

            // Notification propri√©taire
            const owner = lease.owner as { email?: string; full_name?: string } | undefined;
            if (owner?.email) {
              await sendEmail({
                to: owner.email,
                subject: `[Paiement] Loyer re√ßu - ${lease.tenant_name}`,
                html: `<p>Le locataire ${lease.tenant_name} a r√©gl√© son loyer de ${amountFmt} FCFA via KKiaPay.</p><p>R√©f√©rence: ${payload.transactionId}</p>`,
              });
            }
          }
        }
      }
    } else if (payload.status === "FAILED") {
      console.warn("‚ùå Paiement KKiaPay √©chou√©:", {
        transactionId: payload.transactionId,
        metadata: payload.metadata,
      });
    }

    // Toujours retourner 200 pour confirmer la r√©ception
    return Response.json({ success: true });
  } catch (error) {
    console.error("Erreur lors du traitement du webhook KKiaPay:", error);
    return Response.json(
      { error: "Erreur de traitement" },
      { status: 200 } // Retourner 200 pour √©viter les retries excessifs
    );
  }
}

import { createPayDunyaInvoice, getPayDunyaCheckoutUrl, getPayDunyaConfig } from "@/lib/paydunya";
import { createClient } from "@/utils/supabase/server";
import { getBaseUrl } from "@/lib/utils";

export async function POST(request: Request) {
  try {
    const supabase = await createClient();

    // V√©rifier l'authentification
    const {
      data: { user },
      error: userError,
    } = await supabase.auth.getUser();

    if (userError || !user) {
      return Response.json(
        { error: "Non authentifi√©" },
        { status: 401 }
      );
    }

    const body = await request.json();
    const { serviceType, description, propertyId, returnUrl, cancelUrl } = body;

    if (!serviceType || !description) {
      return Response.json(
        { error: "Type de service et description requis" },
        { status: 400 }
      );
    }

    let amount = 0;
    let service: { name: string; price: number; code: string } | null = null;
    let customData: Record<string, unknown> = {};

    if (serviceType === "rent_payment") {
      // Pour le loyer, le montant est variable et vient du body
      const requestedAmount = Number(body.amount);
      if (!requestedAmount || requestedAmount <= 0) {
        return Response.json(
          { error: "Montant invalide pour le paiement du loyer" },
          { status: 400 }
        );
      }
      amount = requestedAmount;
      service = { name: description, price: amount, code: "rent_payment" };

      // Custom data sp√©cifique pour le loyer
      customData = {
        lease_id: propertyId, // propertyId sert de leaseId dans ce contexte
        type: "rent_payment",
      };
    } else {
      // Pour les autres services, on r√©cup√®re le prix depuis la base de donn√©es
      const { data: dbService, error: serviceError } = await supabase
        .from("services")
        .select("*")
        .eq("code", serviceType)
        .single();

      if (serviceError || !dbService) {
        console.error("Erreur r√©cup√©ration service:", serviceError);
        return Response.json(
          { error: "Service invalide ou introuvable" },
          { status: 400 }
        );
      }
      amount = dbService.price;
      service = dbService;

      customData = {
        property_id: propertyId || null,
        type: "listing_payment",
        service_code: dbService.code,
      };
    }

    // Si le montant est 0, on ne cr√©e pas de facture PayDunya (ou on g√®re diff√©remment)
    // Mais ici on suppose que cette route est appel√©e pour payer.
    // Si c'est gratuit, le frontend ne devrait pas appeler cette route de paiement.

    const config = getPayDunyaConfig();

    const baseUrl = getBaseUrl();
    const callbackUrl =
      process.env.PAYDUNYA_CALLBACK_URL || `${baseUrl}/api/paydunya/webhook`;

    // TypeScript check
    if (!service) {
      return Response.json(
        { error: "Erreur interne: Service non d√©fini" },
        { status: 500 }
      );
    }

    // Cr√©er la facture PayDunya
    const invoice = await createPayDunyaInvoice({
      invoice: {
        items: [
          {
            name: service.name, // Utiliser le nom du service officiel
            quantity: 1,
            unit_price: amount,
            total_price: amount,
            description: description, // Garder la description personnalis√©e (ex: "Diffusion Simple - Titre")
          },
        ],
        total_amount: amount,
        description,
      },
      store: {
        name: "Dousell Immo",
        tagline: "Votre partenaire immobilier √† Dakar",
        phone: "+221 77 138 52 81", // Num√©ro mis √† jour
        website_url: baseUrl,
      },
      actions: {
        cancel_url: cancelUrl || `${baseUrl}/compte/deposer?payment=canceled`,
        return_url: returnUrl || `${baseUrl}/compte/deposer?payment=success`,
        callback_url: callbackUrl,
      },
      custom_data: {
        user_id: user.id,
        property_id: propertyId || null,
        type: "listing_payment",
        service_code: service.code, // On garde une trace du code service
      },
    });

    // Retourner l'URL de redirection
    let checkoutUrl = invoice.response_url;

    // PayDunya renvoie parfois l'URL dans 'response_text'
    if (!checkoutUrl && invoice.response_text && invoice.response_text.startsWith("http")) {
      checkoutUrl = invoice.response_text;
    }

    if (!checkoutUrl) {
      checkoutUrl = getPayDunyaCheckoutUrl(invoice.token, config.mode);
    }

    return Response.json({
      success: true,
      token: invoice.token,
      checkout_url: checkoutUrl,
    });
  } catch (error) {
    console.error("Erreur lors de la cr√©ation de la facture PayDunya:", error);
    return Response.json(
      {
        error: error instanceof Error ? error.message : "Erreur lors de la cr√©ation de la facture",
      },
      { status: 500 }
    );
  }
}

import { createClient } from "@/utils/supabase/server";
import { NextResponse } from "next/server";
import { getSmartRedirectPath } from "@/lib/auth-redirect";

export async function GET(request: Request) {
  // 1. On r√©cup√®re l'URL actuelle (que ce soit localhost ou vercel)
  const { searchParams, origin } = new URL(request.url);
  const code = searchParams.get("code");
  const token_hash = searchParams.get("token_hash");
  const type = searchParams.get("type"); // email, signup, recovery, etc.
  const error = searchParams.get("error");
  const errorDescription = searchParams.get("error_description");
  const next = searchParams.get("next"); // Peut √™tre null, on utilisera la redirection intelligente

  // Log pour d√©bugger
  console.log("üîç Auth Callback Debug:", {
    code: code ? "‚úì pr√©sent" : "‚úó manquant",
    token_hash: token_hash ? "‚úì pr√©sent" : "‚úó manquant",
    type,
    error,
    errorDescription,
    next,
    origin,
  });

  // Si Google renvoie une erreur dans les query params
  if (error) {
    console.error("‚ùå OAuth Error from Google:", {
      error,
      errorDescription,
    });
    // Rediriger vers la page d'erreur avec un message sp√©cifique
    const errorUrl = new URL(`${origin}/auth/auth-code-error`);
    if (errorDescription) {
      errorUrl.searchParams.set("reason", decodeURIComponent(errorDescription));
    }
    return NextResponse.redirect(errorUrl.toString());
  }

  const supabase = await createClient();

  // FLUX 1 : V√©rification Email via Token Hash (Email Confirmation)
  // üí° C'est ce flux qui permet le cross-browser (clic sur mobile, ouverture sur PC)
  if (token_hash && type) {
    console.log("üîê Email confirmation flow (token_hash) - Cross-browser compatible");
    try {
      const { data, error: verifyError } = await supabase.auth.verifyOtp({
        type: type === 'email' || type === 'signup' ? 'email' : type as any,
        token_hash,
      });

      if (verifyError) {
        console.error("‚ùå Error verifying OTP:", verifyError);
        const errorUrl = new URL(`${origin}/auth/auth-code-error`);
        errorUrl.searchParams.set("reason", verifyError.message);
        return NextResponse.redirect(errorUrl.toString());
      }

      if (data.session) {
        console.log("‚úÖ Email verified, session created");
        // Utiliser la redirection intelligente avec le param√®tre next si fourni
        const smartRedirect = await getSmartRedirectPath(next || undefined);
        return NextResponse.redirect(`${origin}${smartRedirect}${smartRedirect.includes('?') ? '&' : '?'}verified=true`);
      }

      console.error("‚ùå No session after OTP verification");
      return NextResponse.redirect(`${origin}/auth/auth-code-error`);
    } catch (err) {
      console.error("‚ùå Unexpected error in OTP verification:", err);
      return NextResponse.redirect(`${origin}/auth/auth-code-error`);
    }
  }

  // FLUX 2 : OAuth/PKCE Flow (Google, Magic Link avec Code)
  if (!code) {
    console.error("‚ùå No authorization code or token_hash received");
    return NextResponse.redirect(`${origin}/auth/auth-code-error`);
  }

  // √âchanger le code pour une session
  try {
    const { data, error: exchangeError } = await supabase.auth.exchangeCodeForSession(code);

    if (exchangeError) {
      console.error("‚ùå Error exchanging code for session:", exchangeError);
      // Rediriger vers la page d'erreur avec le d√©tail de l'erreur
      const errorUrl = new URL(`${origin}/auth/auth-code-error`);
      errorUrl.searchParams.set("reason", exchangeError.message);
      return NextResponse.redirect(errorUrl.toString());
    }

    if (data.session) {
      console.log("‚úÖ Session cr√©√©e avec succ√®s");
      console.log("üë§ Utilisateur connect√©:", data.user?.email);

      // Si c'est une v√©rification d'email (type=signup), rediriger vers la home avec un message de succ√®s
      const isEmailVerification = searchParams.get("type") === "signup" ||
        searchParams.get("type") === "email";

      if (isEmailVerification) {
        console.log("‚úÖ Email v√©rifi√© - redirection intelligente");
        const smartRedirect = await getSmartRedirectPath();
        return NextResponse.redirect(`${origin}${smartRedirect}?verified=true`);
      }

      // Sinon, utiliser la redirection intelligente
      // Si un next est sp√©cifi√© explicitement, l'utiliser
      if (next) {
        const redirectUrl = `${origin}${next}`;
        console.log("üîÄ Redirecting to explicit next:", redirectUrl);
        return NextResponse.redirect(redirectUrl);
      }

      // Sinon, d√©terminer la bonne route selon le type d'utilisateur
      const smartRedirect = await getSmartRedirectPath();
      console.log("üîÄ Smart redirect to:", smartRedirect);
      return NextResponse.redirect(`${origin}${smartRedirect}`);
    }

    // Si pas de session apr√®s √©change r√©ussi, erreur
    console.error("‚ùå No session after successful exchange");
    return NextResponse.redirect(`${origin}/auth/auth-code-error`);
  } catch (err) {
    console.error("‚ùå Unexpected error in callback:", err);
    return NextResponse.redirect(`${origin}/auth/auth-code-error`);
  }
}

'use client'

import { useSearchParams } from 'next/navigation'
import Link from 'next/link'
import { Suspense } from 'react'
import { Mail, ArrowRight, RefreshCw } from 'lucide-react'
import { Button } from '@/components/ui/button'
import { useState, useEffect } from 'react'
import { toast } from 'sonner'
import { createClient } from '@/utils/supabase/client'
import { useRouter } from 'next/navigation'



function CheckEmailContent() {
  const searchParams = useSearchParams()
  const _router = useRouter()
  const email = searchParams.get('email') || ''
  const [isResending, setIsResending] = useState(false)
  const [isVerifying, setIsVerifying] = useState(false)

  // Fonction pour g√©rer la soumission du code
  const handleVerify = async (token: string) => {
    // Supabase envoie parfois 6 ou 8 chiffres selon la config
    if (token.length < 6) return

    setIsVerifying(true)
    try {
      const supabase = createClient()
      const { data, error } = await supabase.auth.verifyOtp({
        email,
        token,
        type: 'signup'
      })

      console.log('üîê verifyOtp result:', { data, error })

      if (!error && data.session) {
        // Confirmer que la session est bien √©tablie
        const { data: sessionData } = await supabase.auth.getSession()
        console.log('üîë Session confirm√©e:', sessionData?.session?.user?.email)

        if (sessionData?.session) {
          toast.success("Email v√©rifi√© avec succ√®s !", {
            description: "Redirection vers votre compte...",
          })

          // Attendre un peu pour que les cookies soient bien persist√©s
          await new Promise(resolve => setTimeout(resolve, 500))

          // Forcer un rechargement complet
          window.location.href = '/'
        } else {
          console.error('‚ùå Session non trouv√©e apr√®s verifyOtp')
          toast.error("Erreur de session", {
            description: "La session n'a pas pu √™tre cr√©√©e. Veuillez r√©essayer."
          })
        }
      } else {
        toast.error("Code invalide", {
          description: error?.message || "Le code saisi est incorrect ou expir√©"
        })
      }
    } catch (error) {
      console.error('‚ùå Erreur verifyOtp:', error)
      toast.error("Une erreur s'est produite lors de la v√©rification")
    } finally {
      setIsVerifying(false)
    }
  }

  const handleResendEmail = async () => {
    setIsResending(true)
