            </div>
          </div>

          {/* Form */}
          <form
            action={async (formData: FormData) => {
              setError(null);
              setValidationErrors({});

              if (!captchaToken) {
                toast.error("Veuillez compl√©ter la v√©rification anti-robot");
                return;
