// ISR policy
export const revalidate = 3600;

const capitalize = (text: string) =>
    text.charAt(0).toUpperCase() + text.slice(1);

export const metadata: Metadata = {
    title: "Achat Immobilier S√©n√©gal - Toutes les villes",
    description: "D√©couvrez toutes les villes o√π Doussel Immo propose des biens √† vendre. Appartements, villas et terrains au S√©n√©gal.",
};

export default async function SaleRootPage() {
    // R√©cup√©rer toutes les villes AVEC des ventes
    const { data: combinations, error } = await supabase
        .rpc('get_active_cities_and_types', { min_count: 1, target_transaction_type: 'vente' });

    const citiesMap = new Map<string, number>();

    if (combinations) {
        combinations.forEach((item: { city: string; count: number }) => {
            const citySlug = slugify(item.city);
            const currentCount = citiesMap.get(citySlug) || 0;
            citiesMap.set(citySlug, currentCount + Number(item.count));
        });
    }

    const cities = Array.from(citiesMap.entries())
        .map(([slug, count]) => ({
            slug,
            name: capitalize(unslugify(slug)),
            count
        }))
        .sort((a, b) => b.count - a.count);

    return (
        <div className="min-h-screen bg-background pb-20">
            <div className="bg-muted/30 border-b">
                <div className="container mx-auto px-4 py-10">

                    <h1 className="text-3xl md:text-5xl font-display font-bold mb-4">
                        Acheter un bien immobilier
                    </h1>
                    <p className="text-muted-foreground text-lg max-w-2xl">
                        S√©lectionnez une ville pour voir nos offres de vente.
                    </p>
                </div>
            </div>

            <div className="container mx-auto px-4 py-12">
                {cities.length > 0 ? (
                    /* GRILLE DE CARTES POSTALES (Flex centered) pour Vente */
                    <div className="flex flex-wrap justify-center gap-6">
                        {cities.map((city) => {
                            const imageUrl = getCityImage(city.name);

                            return (
                                <Link
                                    key={city.slug}
                                    href={`/vente/${city.slug}`}
                                    className="group relative h-72 w-full sm:w-80 rounded-2xl overflow-hidden shadow-md hover:shadow-xl transition-all duration-300 hover:-translate-y-1 block"
                                >
                                    {/* 1. L'Image de fond */}
                                    <Image
                                        src={imageUrl}
                                        alt={`Vente √† ${city.name}`}
                                        fill
                                        className="object-cover group-hover:scale-110 transition-transform duration-700"
                                        sizes="(max-width: 768px) 100vw, (max-width: 1200px) 50vw, 25vw"
                                    />

                                    {/* 2. L'Overlay sombre */}
                                    <div className="absolute inset-0 bg-gradient-to-t from-black/80 via-black/30 to-transparent opacity-80 group-hover:opacity-90 transition-opacity" />

                                    {/* 3. Le Contenu Texte */}
                                    <div className="absolute bottom-0 left-0 p-6 w-full">
                                        <h3 className="text-2xl font-bold text-white capitalize mb-1">
                                            {city.name}
                                        </h3>
                                        <div className="flex items-center text-white/90 text-sm font-medium gap-2 group/btn">
                                            <span>{city.count} annonce{city.count > 1 ? 's' : ''}</span>
                                            <ArrowRight className="w-4 h-4 group-hover/btn:translate-x-1 transition-transform" />
                                        </div>
                                    </div>
                                </Link>
                            );
                        })}
