                                Pr√©visualiser la Quittance
                            </Button>
                        </div>
                    </div>
                </DialogContent>
            </Dialog>

            {receiptData && (
                <ReceiptModal
                    isOpen={showPreview}
                    onClose={() => setShowPreview(false)}
                    data={receiptData}
                />
            )}
        </>
    );
