export function ModerationNotification() {
  const router = useRouter();
  const [hasShownNotification, setHasShownNotification] = useState(false);

  const pathname = usePathname();

  useEffect(() => {
    // Ne pas afficher la notification si on est d√©j√† sur la page de mod√©ration
    if (pathname?.startsWith("/admin/moderation")) {
      return;
    }

    const checkNewProperties = async () => {
      const supabase = createClient();


      // V√©rifier s'il y a de nouvelles annonces en attente
      const { count, error } = await supabase
        .from("properties")
        .select("*", { count: "exact", head: true })
        .eq("is_agency_listing", false)
        .in("validation_status", ["pending", "payment_pending"]);

      if (!error && count && count > 0 && !hasShownNotification) {
        // Afficher une notification une seule fois
        toast.info(`${count} annonce${count > 1 ? "s" : ""} en attente de mod√©ration`, {
          description: "Cliquez sur Mod√©ration pour les examiner",
          duration: 6000,
          action: {
            label: "Voir",
            onClick: () => {
              router.push("/admin/moderation");
            },
          },
        });
        setHasShownNotification(true);
      }
    };

    // V√©rifier au chargement de la page (avec un petit d√©lai)
    const timeout = setTimeout(checkNewProperties, 1000);

    // V√©rifier toutes les 60 secondes
    const interval = setInterval(checkNewProperties, 60000);

    return () => {
      clearTimeout(timeout);
      clearInterval(interval);
    };
  }, [router, hasShownNotification]);

  return null;
}

"use server";

import { createClient } from "@/utils/supabase/server";
import { requireAdmin } from "@/lib/admin-auth";
import { revalidatePath } from "next/cache";

export type UserRole = "admin" | "moderateur" | "agent";

/**
 * Accorde un r√¥le via une fonction SQL (bypass RLS)
 */
export async function grantRoleViaFunction(
  userId: string,
  role: UserRole
): Promise<{ success: boolean; error?: string }> {
  await requireAdmin();
  const supabase = await createClient();

  try {
    // Utiliser une fonction SQL pour bypass RLS
    const { data, error } = await supabase.rpc("grant_user_role", {
      target_user_id: userId,
      role_to_grant: role,
    });

    if (error) {
      console.error("Error granting role via function:", error);
      return {
        success: false,
        error: `Erreur: ${error.message || "Impossible d'accorder le r√¥le"}`,
      };
    }

    revalidatePath("/admin/roles");
    revalidatePath("/admin/users");
    return { success: true };
  } catch (error) {
    console.error("Error in grantRoleViaFunction:", error);
    return {
      success: false,
      error: `Erreur: ${error instanceof Error ? error.message : "Erreur inconnue"}`,
    };
  }
}

