  const loadAlerts = async () => {
    if (!user) return;

    try {
      const supabase = createClient();
      const { data, error } = await supabase
        .from("search_alerts")
        .select("*")
        .eq("user_id", user.id)
        .order("created_at", { ascending: false });

      // Si la table n'existe pas, utiliser un tableau vide (silencieux)
      if (error) {
        if (error.code === "PGRST116" || error.message?.includes("does not exist")) {
          setAlerts([]);
          return;
        }
        // Erreur de permission ou autre - utiliser un tableau vide (silencieux)
        setAlerts([]);
        return;
      }

      setAlerts(data || []);
    } catch (_error) {
      // Erreur silencieuse - utiliser un tableau vide
      // Ne pas logger car c'est normal si la table n'existe pas
      setAlerts([]);
    } finally {
      setLoading(false);
    }
  };

  const loadPreferences = async () => {
    if (!user) return;

    try {
      const { getNotificationPreferences } = await import("./actions");
      const result = await getNotificationPreferences();

      if (result.error) {
        // Erreur silencieuse - utiliser les valeurs par d√©faut
        return;
      }

      if (result.data) {
        setPreferences(result.data);
      }
      // Sinon, les valeurs par d√©faut sont d√©j√† d√©finies
    } catch (_error) {
      // Erreur silencieuse - utiliser les valeurs par d√©faut
      // Ne pas logger car c'est normal si la table n'existe pas
    }
  };

  const savePreferences = async (newPreferences: NotificationPreferences) => {
    if (!user) return;

    try {
      const { saveNotificationPreferences } = await import("./actions");
      const result = await saveNotificationPreferences(newPreferences);

      if (result.error) {
        toast.error("Erreur", {
          description: result.error,
        });
        return;
      }

      setPreferences(newPreferences);
      toast.success("Pr√©f√©rences mises √† jour");
    } catch (error) {
      console.error("Error saving preferences:", error);
      toast.error("Erreur lors de la sauvegarde des pr√©f√©rences");
    }
  };

  const togglePreference = (key: keyof NotificationPreferences) => {
    const newPreferences = { ...preferences, [key]: !preferences[key] };
    savePreferences(newPreferences);
  };

  const deleteAlert = async (alertId: string) => {
    try {
      const result = await deleteSearchAlert(alertId);

      if (result.error) {
        toast.error("Erreur", {
          description: result.error,
        });
        return;
      }

      setAlerts((prev) => prev.filter((a) => a.id !== alertId));
      toast.success("Alerte supprim√©e");
    } catch (error) {
      console.error("Error deleting alert:", error);
      toast.error("Erreur lors de la suppression de l'alerte");
    }
  };

  const toggleAlert = async (alert: SearchAlert) => {
    try {
      const result = await updateSearchAlert(alert.id, {
        is_active: !alert.is_active,
      });

      if (result.error) {
        toast.error("Erreur", {
          description: result.error,
        });
        return;
      }

      setAlerts((prev) =>
        prev.map((a) => (a.id === alert.id ? { ...a, is_active: !a.is_active } : a))
      );
      toast.success(`Alerte ${!alert.is_active ? "activ√©e" : "d√©sactiv√©e"}`);
