// ACCESS REQUESTS
// =====================================================

/**
 * Cr√©er une demande d'acc√®s temporaire
 */
// ... existing imports

/**
 * Cr√©er une demande d'acc√®s temporaire
 */
export const submitAccessRequest = safeAction(
    "submitAccessRequest",
    requestAccessSchema,
    async (data, { userId }) => {
        const { teamId, permission, reason } = data;
        const supabase = await createClient();

        const { data: { user } } = await supabase.auth.getUser();
        if (!user || user.id !== userId) throw new Error("Non connect√©");

        // V√©rifier que l'utilisateur est membre de l'√©quipe
        const { data: membership } = await supabase
            .from('team_members')
            .select('id, role')
            .eq('team_id', teamId)
            .eq('user_id', userId)
            .eq('status', 'active')
            .single();

        if (!membership) {
            throw new Error("Vous n'√™tes pas membre de cette √©quipe");
        }

        // V√©rifier qu'il n'y a pas d√©j√† une demande pending pour cette permission
        const { data: existingRequest } = await supabase
            .from('access_requests')
            .select('id')
            .eq('team_id', teamId)
            .eq('requester_id', userId)
            .eq('requested_permission', permission)
            .eq('status', 'pending')
            .maybeSingle();

        if (existingRequest) {
            throw new Error("Vous avez d√©j√† une demande en attente pour cette permission");
        }

        // Cr√©er la demande
        const { error: insertError } = await supabase
            .from('access_requests')
            .insert({
                team_id: teamId,
                requester_id: userId,
                requested_permission: permission,
                reason: reason || null,
                status: 'pending',
            });

        if (insertError) {
            console.error('[Access Request] Insert error:', insertError);
            throw new Error("Erreur lors de la cr√©ation de la demande");
        }

        // Envoyer notification aux owners/managers
        await notifyAccessRequest({
            teamId,
            requesterId: userId,
            permission,
            reason,
        });

        return {
            success: true,
            message: "Demande d'acc√®s envoy√©e avec succ√®s"
        };
    }
);

/**
 * R√©cup√©rer les demandes d'acc√®s pour une √©quipe
 */
export async function getAccessRequestsAction(teamId: string, status?: AccessRequestStatus) {
    try {
        const _context = await requireTeamPermission("team.members.view");
        const adminSupabase = createAdminClient();

        // R√©cup√©rer les demandes
        let query = adminSupabase
            .from('access_requests')
            .select('*')
            .eq('team_id', teamId)
            .order('requested_at', { ascending: false });

        if (status) {
            query = query.eq('status', status);
        }

        const { data: requests, error } = await query;

        if (error) {
            console.error('[Access Requests] Fetch error:', error);
            return { success: false, error: "Erreur lors de la r√©cup√©ration des demandes" };
        }

        if (!requests || requests.length === 0) {
            return { success: true, data: [] };
        }

        // R√©cup√©rer les profils des demandeurs et reviewers
        const userIds = new Set<string>();
        requests.forEach(r => {
            if (r.requester_id) userIds.add(r.requester_id);
            if (r.reviewed_by) userIds.add(r.reviewed_by);
        });

        const { data: profiles } = await adminSupabase
            .from('profiles')
            .select('id, email, full_name')
            .in('id', Array.from(userIds));

        const profileMap = new Map(profiles?.map(p => [p.id, p]) || []);

        // Enrichir les demandes avec les profils
        const enrichedData = requests.map(r => ({
            ...r,
            requester: profileMap.get(r.requester_id) || null,
            reviewer: r.reviewed_by ? profileMap.get(r.reviewed_by) || null : null,
        }));

        return { success: true, data: enrichedData };
    } catch (error: any) {
        console.error('[Access Requests] Unexpected error:', error);
        return { success: false, error: error.message || "Erreur inattendue" };
    }
}

/**
 * Approuver ou rejeter une demande d'acc√®s
 */
export async function reviewAccessRequestAction(formData: FormData | {
    requestId: string;
    action: 'approve' | 'reject';
    reviewNotes?: string;
    durationHours?: number;
}) {
    try {
        const context = await requireTeamPermission("team.members.edit_role"); // Owners/Managers
        const supabase = await createClient();
        const adminSupabase = createAdminClient();

        // Parse & validate
        const data = formData instanceof FormData
            ? {
                requestId: formData.get('requestId') as string,
                action: formData.get('action') as 'approve' | 'reject',
                reviewNotes: formData.get('reviewNotes') as string | undefined,
                durationHours: formData.get('durationHours') ? Number(formData.get('durationHours')) : undefined,
            }
            : formData;

        const validation = reviewAccessRequestSchema.safeParse(data);
        if (!validation.success) {
            return {
                success: false,
                error: validation.error.issues[0]?.message || "Donn√©es invalides"
            };
        }

        const { requestId, action, reviewNotes, durationHours } = validation.data;

        // R√©cup√©rer la demande
        const { data: request, error: fetchError } = await supabase
            .from('access_requests')
            .select('*')
            .eq('id', requestId)
            .eq('team_id', context.teamId)
            .single();

        if (fetchError || !request) {
            return { success: false, error: "Demande introuvable" };
        }

        if (request.status !== 'pending') {
            return { success: false, error: "Cette demande a d√©j√† √©t√© trait√©e" };
        }

        // Mettre √† jour la demande
        const updateData: any = {
            status: action === 'approve' ? 'approved' : 'rejected',
            reviewed_by: context.user.id,
            reviewed_at: new Date().toISOString(),
            review_notes: reviewNotes || null,
        };

        if (action === 'approve') {
            const hours = durationHours || 24; // Par d√©faut 24h
            const expiresAt = new Date();
            expiresAt.setHours(expiresAt.getHours() + hours);
            updateData.expires_at = expiresAt.toISOString();

            // Cr√©er la permission temporaire
            const { error: tempPermError } = await adminSupabase
                .from('temporary_permissions')
                .insert({
                    team_id: request.team_id,
                    user_id: request.requester_id,
                    permission: request.requested_permission,
                    granted_by: context.user.id,
                    expires_at: expiresAt.toISOString(),
                    access_request_id: requestId,
                    reason: request.reason,
                });

            if (tempPermError) {
                console.error('[Temporary Permission] Insert error:', tempPermError);
                return {
                    success: false,
                    error: "Erreur lors de la cr√©ation de la permission temporaire"
                };
            }
        }

        const { error: updateError } = await adminSupabase
            .from('access_requests')
            .update(updateData)
            .eq('id', requestId);

        if (updateError) {
            console.error('[Access Request] Update error:', updateError);
            return {
                success: false,
                error: "Erreur lors de la mise √† jour de la demande"
            };
        }

        // Envoyer notification au demandeur
        if (action === 'approve') {
            const hours = durationHours || 24;
            const expiresAt = new Date();
            expiresAt.setHours(expiresAt.getHours() + hours);

            await notifyAccessApproved({
                requesterId: request.requester_id,
                teamId: request.team_id,
                permission: request.requested_permission,
                expiresAt: expiresAt.toISOString(),
                durationHours: hours,
                reviewerId: context.user.id,
                reviewNotes,
            });
        } else {
            await notifyAccessRejected({
                requesterId: request.requester_id,
                teamId: request.team_id,
                permission: request.requested_permission,
                reviewerId: context.user.id,
                reviewNotes,
            });
        }

        return {
            success: true,
            message: action === 'approve'
                ? "Demande approuv√©e et permission accord√©e"
                : "Demande rejet√©e"
        };
    } catch (error: any) {
        return { success: false, error: error.message || "Erreur inattendue" };
    }
}

// =====================================================
// TEMPORARY PERMISSIONS
// =====================================================

/**
 * Accorder directement une permission temporaire (sans demande)
 */
export async function grantTemporaryPermissionAction(formData: FormData | {
    teamId: string;
    userId: string;
    permission: string;
    durationHours: number;
    reason?: string;
}) {
    try {
        const context = await requireTeamPermission("team.members.edit_role");
        const adminSupabase = createAdminClient();

        // Parse & validate
        const data = formData instanceof FormData
            ? {
                teamId: formData.get('teamId') as string,
                userId: formData.get('userId') as string,
                permission: formData.get('permission') as string,
                durationHours: Number(formData.get('durationHours')),
                reason: formData.get('reason') as string | undefined,
            }
            : formData;

        const validation = grantTemporaryPermissionSchema.safeParse(data);
        if (!validation.success) {
            return {
                success: false,
                error: validation.error.issues[0]?.message || "Donn√©es invalides"
            };
        }

        const { teamId, userId, permission, durationHours, reason } = validation.data;

        if (teamId !== context.teamId) {
            return { success: false, error: "Acc√®s non autoris√©" };
        }

        // Calculer la date d'expiration
        const expiresAt = new Date();
        expiresAt.setHours(expiresAt.getHours() + durationHours);

        // Cr√©er la permission temporaire (upsert pour √©viter les doublons)
        const { error: insertError } = await adminSupabase
            .from('temporary_permissions')
            .upsert({
                team_id: teamId,
                user_id: userId,
                permission,
                granted_by: context.user.id,
                expires_at: expiresAt.toISOString(),
                reason: reason || null,
            }, {
                onConflict: 'team_id,user_id,permission'
            });

        if (insertError) {
            console.error('[Temporary Permission] Upsert error:', insertError);
            return {
                success: false,
                error: "Erreur lors de l'octroi de la permission temporaire"
            };
        }

        // Envoyer notification √† l'utilisateur
        await notifyAccessApproved({
            requesterId: userId,
            teamId,
            permission,
            expiresAt: expiresAt.toISOString(),
            durationHours,
            reviewerId: context.user.id,
            reviewNotes: reason,
        });

        return {
            success: true,
            message: `Permission accord√©e pour ${durationHours} heures`
        };
    } catch (error: any) {
        return { success: false, error: error.message || "Erreur inattendue" };
    }
}

/**
 * R√©voquer une permission temporaire
 */
export async function revokeTemporaryPermissionAction(permissionId: string) {
    try {
        const context = await requireTeamPermission("team.members.edit_role");
        const adminSupabase = createAdminClient();

        // V√©rifier que la permission appartient √† l'√©quipe
        const { data: permission, error: fetchError } = await adminSupabase
            .from('temporary_permissions')
            .select('*')
            .eq('id', permissionId)
            .eq('team_id', context.teamId)
            .single();

        if (fetchError || !permission) {
            return { success: false, error: "Permission introuvable" };
        }

        // Supprimer la permission
        const { error: deleteError } = await adminSupabase
            .from('temporary_permissions')
            .delete()
            .eq('id', permissionId);

        if (deleteError) {
            console.error('[Temporary Permission] Delete error:', deleteError);
            return {
                success: false,
                error: "Erreur lors de la r√©vocation de la permission"
            };
        }

        return {
            success: true,
            message: "Permission r√©voqu√©e avec succ√®s"
        };
    } catch (error: any) {
        return { success: false, error: error.message || "Erreur inattendue" };
    }
}

/**
 * R√©cup√©rer les permissions temporaires actives d'une √©quipe
 */
export async function getTemporaryPermissionsAction(teamId: string) {
    try {
        const _context = await requireTeamPermission("team.members.view");
        const adminSupabase = createAdminClient();

        const { data: permissions, error } = await adminSupabase
            .from('temporary_permissions')
            .select('*')
            .eq('team_id', teamId)
            .gte('expires_at', new Date().toISOString())
            .order('expires_at', { ascending: true });

        if (error) {
            console.error('[Temporary Permissions] Fetch error:', error);
            return {
                success: false,
                error: "Erreur lors de la r√©cup√©ration des permissions"
            };
        }

        if (!permissions || permissions.length === 0) {
            return { success: true, data: [] };
        }

        // R√©cup√©rer les profils des users et granters
        const userIds = new Set<string>();
        permissions.forEach(p => {
            if (p.user_id) userIds.add(p.user_id);
            if (p.granted_by) userIds.add(p.granted_by);
        });

        const { data: profiles } = await adminSupabase
            .from('profiles')
            .select('id, email, full_name')
            .in('id', Array.from(userIds));

        const profileMap = new Map(profiles?.map(p => [p.id, p]) || []);

        // Enrichir les permissions avec les profils
        const enrichedData = permissions.map(p => ({
            ...p,
            user: profileMap.get(p.user_id) || null,
            granter: profileMap.get(p.granted_by) || null,
        }));

        return { success: true, data: enrichedData };
    } catch (error: any) {
        console.error('[Temporary Permissions] Unexpected error:', error);
        return { success: false, error: error.message || "Erreur inattendue" };
    }
}

/**
 * Nettoyer les permissions expir√©es
 */
export async function cleanupExpiredPermissionsAction() {
    try {
        const adminSupabase = createAdminClient();

        const { data, error } = await adminSupabase.rpc('cleanup_expired_permissions');

        if (error) {
            console.error('[Cleanup] Error:', error);
            return {
                success: false,
                error: "Erreur lors du nettoyage des permissions expir√©es"
            };
        }

        return {
            success: true,
            deletedCount: data as number,
            message: `${data} permission(s) expir√©e(s) supprim√©e(s)`
        };
    } catch (error: any) {
        return { success: false, error: error.message || "Erreur inattendue" };
    }
}

"use client";

/**
 * AccessControlDashboard - Dashboard de gestion des acc√®s temporaires
 *
 * Affiche:
 * - Demandes d'acc√®s en attente
 * - Permissions temporaires actives
 * - Historique des demandes
 */

import { useEffect, useState, useCallback } from "react";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import {
  ClockCounterClockwise,
  CheckCircle,
  XCircle,
  Warning,
  LockKey,
  Calendar,
  User,
} from "@phosphor-icons/react";
import { formatDistance } from "date-fns";
import { fr } from "date-fns/locale";
import {
  getAccessRequestsAction,
  getTemporaryPermissionsAction,
  reviewAccessRequestAction,
  revokeTemporaryPermissionAction,
} from "../actions";
import type { AccessRequestStatus } from "../actions";

interface AccessControlDashboardProps {
  teamId: string;
}

export function AccessControlDashboard({ teamId }: AccessControlDashboardProps) {
  const [pendingRequests, setPendingRequests] = useState<any[]>([]);
  const [activePermissions, setActivePermissions] = useState<any[]>([]);
  const [historyRequests, setHistoryRequests] = useState<any[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [activeTab, setActiveTab] = useState("pending");

  const loadData = useCallback(async () => {
    setIsLoading(true);

    try {
      // Charger les demandes en attente
      const pendingResult = await getAccessRequestsAction(teamId, "pending");
      if (pendingResult.success) {
        setPendingRequests(pendingResult.data || []);
      }

      // Charger les permissions actives
      const activeResult = await getTemporaryPermissionsAction(teamId);
      if (activeResult.success) {
        setActivePermissions(activeResult.data || []);
      }

      // Charger l'historique (approved + rejected)
      const historyResult = await getAccessRequestsAction(teamId);
      if (historyResult.success) {
        setHistoryRequests(
          (historyResult.data || []).filter(
            (req: any) => req.status !== "pending"
          )
        );
      }
    } catch (error) {
      console.error("[AccessControlDashboard] Error loading data:", error);
    } finally {
      setIsLoading(false);
    }
  }, [teamId]);

  useEffect(() => {
    loadData();
  }, [teamId]);

  return (
    <Tabs value={activeTab} onValueChange={setActiveTab} className="w-full">
      <TabsList className="grid w-full grid-cols-3 bg-zinc-900">
        <TabsTrigger value="pending" className="data-[state=active]:bg-zinc-800">
          <ClockCounterClockwise size={16} className="mr-2" />
          En attente
          {pendingRequests.length > 0 && (
            <Badge variant="secondary" className="ml-2">
              {pendingRequests.length}
            </Badge>
          )}
        </TabsTrigger>
        <TabsTrigger value="active" className="data-[state=active]:bg-zinc-800">
          <LockKey size={16} className="mr-2" />
          Permissions actives
          {activePermissions.length > 0 && (
            <Badge variant="secondary" className="ml-2">
              {activePermissions.length}
            </Badge>
          )}
        </TabsTrigger>
        <TabsTrigger value="history" className="data-[state=active]:bg-zinc-800">
          <Calendar size={16} className="mr-2" />
          Historique
        </TabsTrigger>
      </TabsList>

      {/* Demandes en attente */}
      <TabsContent value="pending" className="mt-6">
        {isLoading ? (
          <div className="text-center py-12 text-zinc-400">Chargement...</div>
        ) : pendingRequests.length === 0 ? (
          <Card className="bg-zinc-900 border-zinc-800">
            <CardContent className="py-12 text-center">
              <CheckCircle size={48} className="mx-auto mb-4 text-green-500" />
              <p className="text-zinc-400">Aucune demande en attente</p>
            </CardContent>
          </Card>
        ) : (
          <div className="space-y-4">
            {pendingRequests.map((request) => (
              <PendingRequestCard
                key={request.id}
                request={request}
                onReview={loadData}
              />
            ))}
          </div>
        )}
