  onRevoke: () => void;
}) {
  const [isRevoking, setIsRevoking] = useState(false);
  const expiresAt = new Date(permission.expires_at);
  const now = new Date();
  const hoursRemaining = Math.ceil((expiresAt.getTime() - now.getTime()) / (1000 * 60 * 60));

