    if (!confirm("√ätes-vous s√ªr de vouloir r√©voquer cette permission temporaire?")) {
      return;
    }

    setIsRevoking(true);

    try {
      const result = await revokeTemporaryPermissionAction(permission.id);

      if (result.success) {
        onRevoke();
      } else {
        alert(result.error);
      }
    } catch (error: any) {
      alert(error.message || "Erreur lors de la r√©vocation");
    } finally {
      setIsRevoking(false);
    }
  };

  return (
    <Card className="bg-zinc-900 border-zinc-800">
      <CardHeader>
        <div className="flex items-start justify-between">
