    // 2. R√©cup√©rer le profil du propri√©taire
    const { data: profile } = await supabase
        .from('profiles')
        .select('company_name, full_name, company_address')
        .eq('id', user.id)
        .maybeSingle();

    const ownerName = profile?.company_name || profile?.full_name || "Votre Gestionnaire";

