type Property = {
  id: string;
  title: string;
  price: number;
  category: "vente" | "location";
  status: string;
  validation_status: string;
  verification_status: string | null;
  scheduled_publish_at?: string;
  images: string[];
  location: {
    city: string;
    district?: string;
    address?: string;
  };
  specs: {
    surface: number;
    bedrooms: number;
    bathrooms: number;
  };
  details: {
