  };
};

type BiensClientProps = {
  teamId: string;
  teamName: string;
  userRole: TeamRole;
  initialProperties: Property[];
  error?: string;
};

export function BiensClient({
  teamId,
  teamName,
  userRole,
  initialProperties,
  error,
}: BiensClientProps) {
  const _router = useRouter();
  const [properties, setProperties] = useState(initialProperties);
  const [searchQuery, setSearchQuery] = useState("");
  const [categoryFilter, setCategoryFilter] = useState<"all" | "vente" | "location">("all");
  const [statusFilter, setStatusFilter] = useState<"all" | "published" | "draft" | "scheduled">("all");
  const [viewMode, setViewMode] = useState<"grid" | "list">("grid");
  const [isLoading, setIsLoading] = useState(false);

  // State pour la modale d'association
  const [propertyToAssociate, setPropertyToAssociate] = useState<{
    id: string;
    title: string;
    address?: string;
    price?: number;
    ownerId: string;
  } | null>(null);

  const canCreate = userRole === "owner" || userRole === "manager";
  const canDelete = userRole === "owner";

  // Filtrer les propri√©t√©s
  const filteredProperties = properties.filter((property) => {
    // Recherche textuelle
    if (searchQuery) {
      const query = searchQuery.toLowerCase();
      const matchesTitle = property.title.toLowerCase().includes(query);
      const matchesCity = property.location.city.toLowerCase().includes(query);
      const matchesDistrict = property.location.district?.toLowerCase().includes(query);
      const matchesTenant = property.tenant?.full_name.toLowerCase().includes(query);

      if (!matchesTitle && !matchesCity && !matchesDistrict && !matchesTenant) return false;
    }

    // Filtre cat√©gorie
    if (categoryFilter !== "all" && property.category !== categoryFilter) return false;

    // Filtre statut publication
    if (statusFilter === "published") {
      // Un bien n'est r√©ellement "en ligne" que s'il est approuv√© ET non lou√©
      if (property.validation_status !== "approved" || property.status === "lou√©") return false;
    }
    if (statusFilter === "draft" && property.validation_status !== "pending") return false;
    if (statusFilter === "scheduled" && property.validation_status !== "scheduled") return false;

    return true;
  });

  // Stats
  const stats = {
    total: properties.length,
    published: properties.filter((p) => p.validation_status === "approved" && p.status !== "lou√©").length,
    draft: properties.filter((p) => p.validation_status === "pending").length,
    scheduled: properties.filter((p) => p.validation_status === "scheduled").length,
    vente: properties.filter((p) => p.category === "vente").length,
    location: properties.filter((p) => p.category === "location").length,
  };

  // Actions
  const handleTogglePublication = async (propertyId: string) => {
    setIsLoading(true);
    const result = await togglePropertyPublication(teamId, propertyId);
    setIsLoading(false);

    if (result.success) {
      setProperties((prev) =>
        prev.map((p) =>
          p.id === propertyId
            ? { ...p, validation_status: result.isPublished ? "approved" : "pending" }
            : p
        )
      );
    }
  };

  const handleDelete = async (propertyId: string) => {
    if (!canDelete) return;

    setIsLoading(true);
    const result = await deleteTeamProperty(teamId, propertyId);
    setIsLoading(false);

    if (result.success) {
      setProperties((prev) => prev.filter((p) => p.id !== propertyId));
    }
  };

  return (
    <div className="min-h-screen bg-background text-foreground">
      <div className="max-w-7xl mx-auto px-4 py-8">
        <BiensTour canCreate={canCreate} />
        {/* Header */}
        <div id="tour-biens-header" className="flex flex-col md:flex-row md:items-center md:justify-between gap-6 mb-8 text-left">
          <div className="flex flex-col items-start">
            <h1 className="text-2xl md:text-3xl font-bold flex items-center justify-start gap-3">
              <Building2 className="w-7 h-7 md:w-8 md:h-8 text-primary" />
              Biens de {teamName}
            </h1>
            <p className="text-muted-foreground mt-1 text-sm md:text-base">
              {stats.total} bien{stats.total > 1 ? "s" : ""} ‚Ä¢ {stats.published} en ligne
            </p>
          </div>

          {canCreate && (
            <div className="flex justify-start md:justify-end w-full md:w-auto">
              <Link
                href="/gestion/biens/nouveau"
                id="tour-biens-add-button"
                className="inline-flex items-center justify-center gap-2 px-6 py-3 bg-primary text-primary-foreground rounded-xl font-semibold hover:bg-primary/90 transition-all shadow-lg w-full md:w-auto text-base"
              >
                <Plus className="w-5 h-5" />
                Ajouter un bien
              </Link>
            </div>
          )}
        </div>

        {/* Stats Cards */}
        <div id="tour-biens-stats" className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-8">
          <div className="bg-card border border-border rounded-xl p-4 shadow-sm">
            <div className="text-3xl font-bold">{stats.total}</div>
            <div className="text-sm text-muted-foreground">Total biens</div>
          </div>
          <div className="bg-card border border-border rounded-xl p-4 shadow-sm">
            <div className="flex items-center gap-2">
              <Eye className="w-5 h-5 text-green-500" />
              <span className="text-3xl font-bold">{stats.published}</span>
            </div>
            <div className="text-sm text-muted-foreground">En ligne</div>
          </div>
          <div className="bg-card border border-border rounded-xl p-4 shadow-sm">
            <div className="text-3xl font-bold">{stats.vente}</div>
            <div className="text-sm text-muted-foreground">Ventes</div>
          </div>
          <div className="bg-card border border-border rounded-xl p-4 shadow-sm">
            <div className="text-3xl font-bold">{stats.location}</div>
            <div className="text-sm text-muted-foreground">Locations</div>
          </div>
        </div>

        {/* Filters */}
        <div id="tour-biens-search-filters" className="flex flex-col md:flex-row gap-4 mb-6">
          <form
            onSubmit={(e) => {
              e.preventDefault();
              (document.activeElement as HTMLElement)?.blur();
            }}
            className="relative flex-1"
          >
            <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-5 h-5 text-muted-foreground" />
            <input
              type="search"
              enterKeyHint="search"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              placeholder="Rechercher un bien..."
              className="w-full bg-card border border-border rounded-lg pl-10 pr-4 py-2.5 placeholder:text-muted-foreground focus:outline-none focus:ring-1 focus:ring-primary focus:border-primary transition-colors"
            />
          </form>

          {/* GROUPE 1 : Segmented Control pour le Type */}
          <div className="bg-muted p-1 rounded-lg inline-flex">
            <button
              onClick={() => setCategoryFilter("all")}
              className={`px-4 py-1.5 rounded-md text-sm font-medium transition-all ${categoryFilter === "all"
                ? "bg-background text-foreground shadow-sm"
                : "text-muted-foreground hover:text-foreground"
                }`}
            >
              Tous
            </button>
            <button
              onClick={() => setCategoryFilter("vente")}
              className={`px-4 py-1.5 rounded-md text-sm font-medium transition-all ${categoryFilter === "vente"
                ? "bg-background text-foreground shadow-sm"
                : "text-muted-foreground hover:text-foreground"
                }`}
            >
              Vente
            </button>
            <button
              onClick={() => setCategoryFilter("location")}
              className={`px-4 py-1.5 rounded-md text-sm font-medium transition-all ${categoryFilter === "location"
                ? "bg-background text-foreground shadow-sm"
                : "text-muted-foreground hover:text-foreground"
                }`}
            >
              Location
            </button>
          </div>

          {/* S√âPARATEUR */}
          <div className="hidden md:block h-6 w-px bg-border mx-2"></div>

          {/* GROUPE 2 : Statuts (Style Badges cliquables) */}
          <div className="flex gap-2">
            <button
              onClick={() => setStatusFilter("published")}
              className={`flex items-center gap-2 px-3 py-1.5 rounded-full text-xs font-medium transition-all ${statusFilter === "published"
                ? "bg-green-500/20 text-green-600 dark:text-green-400 border border-green-500/50"
                : "border border-border hover:border-muted-foreground text-muted-foreground"
                }`}
            >
              <Eye className="w-3.5 h-3.5" /> En ligne
            </button>
            <button
              onClick={() => setStatusFilter("draft")}
              className={`flex items-center gap-2 px-3 py-1.5 rounded-full text-xs font-medium transition-all ${statusFilter === "draft"
                ? "bg-muted text-foreground border border-muted-foreground/50"
                : "border border-border hover:border-muted-foreground text-muted-foreground"
                }`}
            >
              <EyeOff className="w-3.5 h-3.5" /> Brouillon
            </button>
            <button
              onClick={() => setStatusFilter("scheduled")}
              className={`flex items-center gap-2 px-3 py-1.5 rounded-full text-xs font-medium transition-all ${statusFilter === "scheduled"
                ? "bg-blue-500/20 text-blue-600 dark:text-blue-400 border border-blue-500/50"
                : "border border-border hover:border-muted-foreground text-muted-foreground"
                }`}
            >
              <Clock className="w-3.5 h-3.5" /> Programm√©
            </button>
          </div>

          {/* View Toggle */}
          <div className="flex gap-1 bg-muted border border-border rounded-lg p-1">
            <button
              onClick={() => setViewMode("grid")}
              className={`p-2 rounded-md transition-colors ${viewMode === "grid" ? "bg-background text-foreground shadow-sm" : "text-muted-foreground hover:text-foreground"
                }`}
            >
              <Grid className="w-5 h-5" />
            </button>
            <button
              onClick={() => setViewMode("list")}
              className={`p-2 rounded-md transition-colors ${viewMode === "list" ? "bg-background text-foreground shadow-sm" : "text-muted-foreground hover:text-foreground"
                }`}
            >
              <List className="w-5 h-5" />
            </button>
          </div>
        </div>

        {/* Error */}
        {error && (
          <div className="bg-red-500/10 border border-red-500/30 rounded-lg p-4 mb-6 text-red-600 dark:text-red-400">
            {error}
          </div>
        )}

        {/* Properties Grid */}
        {filteredProperties.length === 0 ? (
          <div className="text-center py-16">
            <Building2 className="w-16 h-16 text-muted-foreground mx-auto mb-4" />
            <h3 className="text-xl font-semibold mb-2">
              {properties.length === 0 ? "Aucun bien" : "Aucun r√©sultat"}
            </h3>
            <p className="text-muted-foreground mb-6">
              {properties.length === 0
                ? "Commencez par ajouter votre premier bien immobilier"
                : "Aucun bien ne correspond √† vos crit√®res de recherche"}
            </p>
            {canCreate && properties.length === 0 && (
              <Link
                href="/gestion/biens/nouveau"
                className="inline-flex items-center gap-2 px-5 py-2.5 bg-primary text-primary-foreground rounded-lg font-medium hover:bg-primary/90 transition-all shadow-md"
              >
                <Plus className="w-5 h-5" />
                Ajouter un bien
              </Link>
            )}
          </div>
        ) : (
          <div
            className={
              viewMode === "grid"
                ? "grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6"
                : "space-y-4"
            }
          >
            {filteredProperties.map((property) => (
              <TeamPropertyCard
                key={property.id}
                property={property}
                onTogglePublication={handleTogglePublication}
                onDelete={handleDelete}
                onAssociate={(id) => {
                  const p = properties.find(prop => prop.id === id);
                  if (p) setPropertyToAssociate({
                    id: p.id,
                    title: p.title,
                    address: p.location.address || `${p.location.district ? p.location.district + ', ' : ''}${p.location.city}`,
                    price: p.price,
                    ownerId: p.owner?.id || ""
                  });
                }}
                isLoading={isLoading}
              />
            ))}
          </div>
        )}
      </div>

      {/* Modale d'association (Locataire existant) */}
      {propertyToAssociate && (
        <AssociateTenantDialog
          isOpen={!!propertyToAssociate}
          onClose={() => setPropertyToAssociate(null)}
          propertyId={propertyToAssociate.id}
          propertyTitle={propertyToAssociate.title}
          propertyAddress={propertyToAssociate.address}
          propertyPrice={propertyToAssociate.price}
          ownerId={propertyToAssociate.ownerId}
          teamId={teamId}
        />
      )}
    </div>
  );
}

"use client";

import { AddressAutocomplete } from "@/components/forms/address-autocomplete";
import { toast } from "sonner";
import { useState, useCallback } from "react";
import { useRouter } from "next/navigation";
import Link from "next/link";
import Image from "next/image";
import {
  ArrowLeft,
  ArrowRight,
  Building2,
  Home,
  MapPin,
  Ruler,
  ImageIcon,
  FileText,
  Loader2,
  Plus,
  X,
  Sparkles,
  Calendar,
  Save,
  Send,
  Check,
  Upload,
  User,
  Building,
  Warehouse,
  TreePine,
  Briefcase,
  Hotel,

  ChevronDown,
  Clock,
} from "lucide-react";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { Label } from "@/components/ui/label";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { format } from "date-fns";
import { fr } from "date-fns/locale";
import { createClient } from "@/utils/supabase/client";
import { OwnerSelector } from "@/components/gestion/OwnerSelector";
import { createTeamProperty, generateSEODescription, type TeamPropertyData } from "../actions";
import { UpgradeModal } from "@/components/gestion/UpgradeModal";
import { scrollToTop } from "@/lib/scroll-utils";

type PublishMode = "publish" | "draft" | "schedule";

type NouveauBienClientProps = {
  teamId: string;
  teamName: string;
};

const PROPERTY_TYPES = [
  { value: "villa", label: "Villa", icon: Home },
  { value: "appartement", label: "Appartement", icon: Building },
  { value: "studio", label: "Studio", icon: Hotel },
  { value: "terrain", label: "Terrain", icon: TreePine },
  { value: "immeuble", label: "Immeuble", icon: Building2 },
  { value: "bureau", label: "Bureau", icon: Briefcase },
];

const STEPS = [
  { id: 1, title: "L'essentiel", icon: Building2 },
  { id: 2, title: "Localisation", icon: MapPin },
  { id: 3, title: "D√©tails", icon: Ruler },
  { id: 4, title: "M√©dia", icon: ImageIcon },
  { id: 5, title: "Publication", icon: Send },
];

export function NouveauBienClient({ teamId, teamName }: NouveauBienClientProps) {
  const router = useRouter();
  const [currentStep, setCurrentStep] = useState(1);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState<string | null>(null);

  // Form state
  const [formData, setFormData] = useState({
    type: "appartement",
    category: "location" as "vente" | "location",
    title: "",
    price: "",
    address: "",
    surface: "",
    rooms: "",
    bedrooms: "",
    bathrooms: "",
    description: "",
    virtual_tour_url: "",
    images: [] as string[],
    owner_id: undefined as string | undefined,
    lat: null as number | null,
    lon: null as number | null,
    city: "",
    district: "",
    region: "",
  });

  // Image upload state
  const [uploadingImages, setUploadingImages] = useState(false);
  const [dragActive, setDragActive] = useState(false);

  // AI generation state
  const [isGeneratingAI, setIsGeneratingAI] = useState(false);

  // Publication options
  const [publishMode, _setPublishMode] = useState<PublishMode>("publish");
  const [scheduledDate, setScheduledDate] = useState("");
  const [isScheduleDialogOpen, setIsScheduleDialogOpen] = useState(false);
  const [showUpgradeModal, setShowUpgradeModal] = useState(false);
  const [_statsSummary, _setStatsSummary] = useState({ properties: 0, leases: 0 });

  const updateField = (field: string, value: string | number | string[]) => {
    setFormData((prev) => ({ ...prev, [field]: value }));
  };

  // Drag & Drop handlers
  const handleDrag = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  }, []);

  const handleDrop = useCallback(async (e: React.DragEvent) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);

    const files = e.dataTransfer.files;
    if (files && files.length > 0) {
      await processImageFiles(Array.from(files));
    }
  }, [formData.images.length, teamId]);

  const processImageFiles = async (fileArray: File[]) => {
    if (formData.images.length + fileArray.length > 10) {
      toast.error("Maximum 10 photos autoris√©es");
      return;
    }

    setUploadingImages(true);
    setError(null);

    try {
      const supabase = createClient();
      const uploadedUrls: string[] = [];
      let hasError = false;

      for (const file of fileArray) {
        if (file.size > 5 * 1024 * 1024) {
          toast.error(`Le fichier ${file.name} d√©passe 5MB`);
          hasError = true;
          continue;
        }

        const fileExt = file.name.split(".").pop()?.toLowerCase();
        if (!["jpg", "jpeg", "png", "webp"].includes(fileExt || "")) {
          toast.error(`Format non support√©: ${fileExt}`);
          hasError = true;
          continue;
        }

        try {
          const fileName = `${Date.now()}-${Math.random().toString(36).substring(7)}.${fileExt}`;
          const filePath = `team-properties/${teamId}/${fileName}`;

          const { error: uploadError } = await supabase.storage
            .from("properties")
            .upload(filePath, file, { cacheControl: "3600", upsert: false });

          if (uploadError) throw new Error(`Erreur upload: ${uploadError.message}`);

          const { data: { publicUrl } } = supabase.storage.from("properties").getPublicUrl(filePath);
          uploadedUrls.push(publicUrl);
        } catch (e) {
          console.error(e);
          toast.error(`Erreur lors de l'upload de ${file.name}`);
          hasError = true;
        }
      }

      if (uploadedUrls.length > 0) {
        setFormData((prev) => ({ ...prev, images: [...prev.images, ...uploadedUrls] }));
        toast.success(`${uploadedUrls.length} photo(s) ajout√©e(s)`);
      }

      if (hasError) {
        setError("Certains fichiers n'ont pas pu √™tre upload√©s");
      }

    } catch (err) {
      console.error(err);
      toast.error("Erreur critique lors de l'upload");
      setError("Erreur lors de l'upload");
    } finally {
      setUploadingImages(false);
    }
  };

  const handleImageUpload = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const files = e.target.files;
    if (!files || files.length === 0) return;
    await processImageFiles(Array.from(files));
  };

  const removeImage = (index: number) => {
    setFormData((prev) => ({ ...prev, images: prev.images.filter((_, i) => i !== index) }));
  };

  // AI Description
  const handleGenerateAI = async () => {
    if (!formData.address || !formData.price) {
      setError("Renseignez l'adresse et le prix pour g√©n√©rer une description.");
      return;
    }

    setIsGeneratingAI(true);
    setError(null);

    const result = await generateSEODescription({
      type: formData.type,
      category: formData.category,
      city: formData.address,
      price: parseInt(formData.price),
      surface: formData.surface ? parseInt(formData.surface) : undefined,
      rooms: formData.rooms ? parseInt(formData.rooms) : undefined,
      bedrooms: formData.bedrooms ? parseInt(formData.bedrooms) : undefined,
      bathrooms: formData.bathrooms ? parseInt(formData.bathrooms) : undefined,
      title: formData.title || undefined,
    });

    setIsGeneratingAI(false);

    if (result.success) {
      if (result.description) updateField("description", result.description);
      if (result.title && !formData.title) updateField("title", result.title);
    } else {
      setError(result.error || "Erreur lors de la g√©n√©ration");
    }
  };

  // Step validation
  const validateStep = (step: number): boolean => {
    setError(null);
    switch (step) {
      case 1:
        if (!formData.title || formData.title.length < 3) {
          setError("Le titre doit contenir au moins 3 caract√®res");
          return false;
        }
        if (!formData.price || parseInt(formData.price) <= 0) {
          setError("Le prix est requis");
          return false;
        }
        return true;
      case 2:
        if (!formData.address) {
          setError("L'adresse est requise");
          return false;
        }
        if (!formData.address) {
          setError("L'adresse est requise");
          return false;
        }
        return true;
      case 3:
        return true;
      case 4:
        return true;
      case 5:
        if (publishMode === "publish" && formData.images.length === 0) {
          setError("Au moins une image est requise pour publier");
          return false;
        }
        return true;
      default:
        return true;
    }
  };

  const nextStep = () => {
    if (validateStep(currentStep)) {
      setCurrentStep((prev) => Math.min(prev + 1, 5));
      scrollToTop();
    }
  };

  const prevStep = () => {
    setCurrentStep((prev) => Math.max(prev - 1, 1));
    scrollToTop();
  };

  // Submit
  const handleSubmit = async (mode: PublishMode = "publish", date?: string) => {
    if (!validateStep(5)) return;

    // Specific validation for scheduling
    if (mode === "schedule" && !date) {
      setError("S√©lectionnez une date pour la programmation");
      return;
    }

    setIsSubmitting(true);
    setError(null);

    const data: TeamPropertyData = {
      type: formData.type,
      category: formData.category,
      title: formData.title,
      price: parseInt(formData.price),
      address: formData.address,
      surface: formData.surface ? parseInt(formData.surface) : undefined,
      rooms: formData.rooms ? parseInt(formData.rooms) : undefined,
      bedrooms: formData.bedrooms ? parseInt(formData.bedrooms) : undefined,
      bathrooms: formData.bathrooms ? parseInt(formData.bathrooms) : undefined,
      description: formData.description,
      virtual_tour_url: formData.virtual_tour_url,
      images: formData.images,
      owner_id: formData.owner_id,
      location: formData.lat && formData.lon ? {
        lat: formData.lat,
        lon: formData.lon,
        city: formData.city,
        district: formData.district,
        region: formData.region
      } : undefined,
    };

    const result = await createTeamProperty(
      teamId,
      data,
      mode,
      mode === "schedule" ? date : undefined
    );

    setIsSubmitting(false);

    if (result.success) {
      const successMsg = mode === "draft" ? "saved_draft" : mode === "schedule" ? "scheduled" : "created";
      router.push(`/gestion/biens?success=${successMsg}`);
    } else {
      if (result.upgradeRequired) {
        setShowUpgradeModal(true);
      }
      setError(result.error || "Erreur lors de la cr√©ation");
    }
  };

  const handleScheduleSubmit = () => {
    if (!scheduledDate) return;
    handleSubmit("schedule", scheduledDate);
    setIsScheduleDialogOpen(false);
  };

  const isTerrain = formData.type === "terrain";

  return (
    <div className="min-h-screen bg-background text-foreground">
      {/* Sticky Header */}
      <div className="sticky top-0 z-50 bg-background/95 backdrop-blur-sm border-b border-border">
        <div className="max-w-4xl mx-auto px-4 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-4">
              <Link href="/gestion/biens" className="p-2 hover:bg-muted rounded-lg transition-colors">
                <ArrowLeft className="w-5 h-5 text-muted-foreground" />
              </Link>
              <div>
                <h1 className="text-lg font-semibold">Nouveau bien</h1>
                <p className="text-sm text-muted-foreground">{teamName}</p>
              </div>
            </div>
            <div className="flex items-center gap-2">
              <button
                onClick={() => handleSubmit()}
                disabled={isSubmitting}
                className="px-4 py-2 text-sm text-muted-foreground hover:text-foreground transition-colors disabled:opacity-50"
              >
                {isSubmitting ? <Loader2 className="w-4 h-4 animate-spin" /> : <Save className="w-4 h-4" />}
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Progress Steps */}
      <div className="max-w-4xl mx-auto px-4 py-6">
        <div className="flex items-center justify-between mb-8 overflow-x-auto pb-4 scrollbar-hide -mx-1 px-1">
          {STEPS.map((step, index) => {
            const Icon = step.icon;
            const isActive = currentStep === step.id;
            const isCompleted = currentStep > step.id;

            return (
              <div key={step.id} className="flex items-center shrink-0">
                <button
                  onClick={() => step.id < currentStep && setCurrentStep(step.id)}
                  className={`flex flex-col items-center gap-2 transition-all ${step.id <= currentStep ? "cursor-pointer" : "cursor-default"
                    }`}
                >
                  <div className={`w-9 h-9 sm:w-10 sm:h-10 rounded-full flex items-center justify-center transition-all ${isActive
                    ? "bg-primary text-primary-foreground shadow-lg"
                    : isCompleted
                      ? "bg-primary/20 text-primary"
                      : "bg-muted text-muted-foreground"
                    }`}>
                    {isCompleted ? <Check className="w-5 h-5" /> : <Icon className="w-4 h-4 sm:w-5 sm:h-5" />}
                  </div>
                  <span className={`text-[10px] sm:text-xs font-medium hidden sm:block ${isActive ? "text-foreground" : isCompleted ? "text-primary" : "text-muted-foreground"
                    }`}>
                    {step.title}
                  </span>
