                        bg-primary text-primary-foreground hover:bg-primary/90
                        rounded-r-lg rounded-l-none 
                        border-l border-white/10 
                        transition-all duration-200 ease-in-out
                        focus:z-10 focus:ring-2 focus:ring-primary/50
