                    </div>
                )}

                {/* Errors display (for Step 1) */}
                {step === 1 && errors.length > 0 && (
                    <ul className="text-sm text-destructive p-3 rounded-lg bg-destructive/10 border border-destructive/20">
                        {errors.map((e, i) => <li key={i}>‚Ä¢ {e}</li>)}
                    </ul>
                )}
            </DialogContent>
        </Dialog>
    );
}

'use client';

import { Wallet, Clock, AlertCircle, Users, TrendingUp, TrendingDown } from 'lucide-react';
import { useTheme } from "@/components/theme-provider";

interface DashboardStatsProps {
    stats: {
        totalExpected: number;
        totalCollected: number;
        totalPending: number;
        paidCount: number;
        pendingCount: number;
        overdueCount: number;
        collectedPercent: number;
        totalTenants: number;
    };
    onFilterChange?: (status: 'all' | 'paid' | 'pending' | 'overdue') => void;
    activeFilter?: 'all' | 'paid' | 'pending' | 'overdue';
}

export function DashboardStats({ stats, onFilterChange, activeFilter = 'all' }: DashboardStatsProps) {
    const { isDark } = useTheme();

    const formatCompact = (num: number) => {
        return new Intl.NumberFormat('fr-FR', {
            notation: "compact",
            maximumFractionDigits: 1
        }).format(num);
    };

    const cards = [
        {
            title: "Total Attendu",
            value: formatCompact(stats.totalExpected),
            suffix: "FCFA",
            icon: Wallet,
            colorClass: isDark ? "bg-blue-900/20 text-blue-400" : "bg-blue-100 text-blue-600",
            description: `${stats.paidCount + stats.pendingCount + stats.overdueCount} locataires`,
            filter: 'all' as const,
        },
        {
            title: "Encaiss√©",
            value: formatCompact(stats.totalCollected),
            suffix: "FCFA",
            icon: TrendingUp,
            colorClass: isDark ? "bg-green-900/20 text-green-400" : "bg-green-100 text-green-600",
            description: `${stats.collectedPercent}% du total`,
            trend: stats.collectedPercent >= 50 ? "up" : "down",
            filter: 'paid' as const,
        },
        {
            title: "En Attente",
            value: formatCompact(stats.totalPending),
            suffix: "FCFA",
            icon: Clock,
            colorClass: isDark ? "bg-amber-900/20 text-amber-400" : "bg-amber-100 text-amber-600",
            description: `${stats.pendingCount} paiement${stats.pendingCount > 1 ? 's' : ''} en cours`,
            filter: 'pending' as const,
        },
        {
            title: "Retards",
            value: stats.overdueCount.toString(),
            suffix: stats.overdueCount > 0 ? "impay√©" + (stats.overdueCount > 1 ? "s" : "") : "",
            icon: AlertCircle,
            colorClass: isDark ? "bg-red-900/20 text-red-400" : "bg-red-100 text-red-600",
            description: stats.overdueCount > 0 ? "Action requise" : "Tout est √† jour",
            alert: stats.overdueCount > 0,
            filter: 'overdue' as const,
        },
    ];

    return (
        <div className="grid grid-cols-2 lg:grid-cols-4 gap-4 mb-6">
            {cards.map((card, index) => {
                const isActive = activeFilter === card.filter;
                const Icon = card.icon;

                return (
                    <div
                        key={index}
                        onClick={() => onFilterChange?.(card.filter)}
                        className={`
                            p-5 rounded-lg border shadow-sm cursor-pointer transition-all duration-200
                            ${isActive
                                ? isDark
                                    ? 'bg-gray-900 border-gray-700 ring-1 ring-gray-700'
                                    : 'bg-white border-gray-300 ring-1 ring-gray-300'
                                : isDark
                                    ? 'bg-black border-gray-800 hover:border-gray-700'
                                    : 'bg-white border-gray-200 hover:border-gray-300'
                            }
                        `}
                    >
                        {/* Header : Icone color√©e + Titre */}
                        <div className="flex items-center gap-3 mb-3">
                            <div className={`p-2 rounded-md ${card.colorClass}`}>
                                <Icon className="w-4 h-4" />
                            </div>
                            <h3 className={`text-xs font-medium uppercase tracking-wider ${isDark ? 'text-gray-500' : 'text-gray-500'
                                }`}>
                                {card.title}
                            </h3>
                        </div>

                        <div className="flex items-baseline gap-1">
                            {/* Chiffre : Toujours Neutre (Slate-900 / White) */}
                            <span className={`text-3xl font-semibold tracking-tighter ${isDark ? 'text-white' : 'text-gray-900'
                                }`}>
                                {card.value}
                            </span>
                            {/* Unit√© : Discr√®te */}
                            {card.suffix && (
                                <span className={`text-sm font-normal ${isDark ? 'text-gray-500' : 'text-gray-500'
                                    }`}>
                                    {card.suffix}
                                </span>
                            )}
                        </div>

                        {/* Description subtile en bas */}
                        {card.description && (
                            <div className={`mt-2 text-xs flex items-center gap-1 ${isDark ? 'text-gray-500' : 'text-gray-500'
                                }`}>
                                {card.trend === "up" && (
                                    <TrendingUp className="w-3 h-3 text-green-500" />
                                )}
                                {card.trend === "down" && (
                                    <TrendingDown className="w-3 h-3 text-red-500" />
                                )}
                                {card.alert && (
                                    <AlertCircle className="w-3 h-3 text-red-500 mr-1" />
                                )}
                                {card.description}
                            </div>
                        )}
                    </div>
                );
            })}
        </div>
    );
}

'use client';

import { ReactNode, useState, useEffect } from 'react';
import { useTheme } from "@/components/theme-provider";
import { Users, TrendingUp } from 'lucide-react';

type TabId = 'overview' | 'performance';

interface Tab {
    id: TabId;
    label: string;
    icon: typeof Users;
}

const tabs: Tab[] = [
    { id: 'overview', label: 'Vue d\'ensemble', icon: Users },
    { id: 'performance', label: 'Performance', icon: TrendingUp },
];

interface DashboardTabsProps {
    overviewContent: ReactNode;
    performanceContent: ReactNode;
}

export function DashboardTabs({
    overviewContent,
    performanceContent,
}: DashboardTabsProps) {
    const [activeTab, setActiveTab] = useState<TabId>('overview');
    const { isDark } = useTheme();

    const content: Record<TabId, ReactNode> = {
        overview: overviewContent,
        performance: performanceContent,
    };

    // Reset to overview tab whenever component mounts/remounts
    useEffect(() => {
        setActiveTab('overview');
    }, []);

    // Support for programmatic tab switching (e.g., from the tour)
    useEffect(() => {
        const handleSwitchTab = (event: any) => {
            const detail = event.detail;
            if (detail && (detail === 'overview' || detail === 'performance')) {
                setActiveTab(detail as TabId);
            }
        };

        window.addEventListener('dousell-tour-switch-tab', handleSwitchTab);
        return () => window.removeEventListener('dousell-tour-switch-tab', handleSwitchTab);
    }, []);

    return (
        <div>
            {/* Tab Navigation */}
            <div className={`flex items-center gap-1 p-1 rounded-lg mb-6 w-fit ${isDark ? 'bg-slate-900/80' : 'bg-gray-100'
                }`}>
                {tabs.map((tab) => {
                    const Icon = tab.icon;
                    const isActive = activeTab === tab.id;

                    return (
                        <button
                            key={tab.id}
                            onClick={() => setActiveTab(tab.id)}
                            className={`
                                flex items-center gap-1.5 px-3 py-1.5 rounded-md text-xs font-medium
                                transition-all duration-200
                                ${isActive
                                    ? isDark
                                        ? 'bg-slate-800 text-white shadow-sm'
                                        : 'bg-white text-gray-900 shadow-sm'
                                    : isDark
                                        ? 'text-slate-400 hover:text-slate-200'
                                        : 'text-gray-500 hover:text-gray-700'
                                }
                            `}
                        >
                            <Icon className="w-3.5 h-3.5" />
                            <span className="hidden sm:inline">{tab.label}</span>
                        </button>
                    );
                })}
            </div>

            {/* Tab Content */}
            <div>{content[activeTab]}</div>
        </div>
    );
}

