    tenant_name: string;
    tenant_phone?: string;
    tenant_email?: string;
    property_address?: string;
    monthly_amount: number;
    billing_day?: number;
    start_date?: string;
    end_date?: string;
    status?: 'active' | 'terminated' | 'pending';
    created_at?: string;
    property_id?: string;
    properties?: {
        id: string;
        title: string;
        images?: string[];
    } | null;
}

interface Profile {
