        tenant_name?: string;
        name?: string;
        email?: string;
        phone?: string;
        address?: string;
    };
    property_address?: string;
    amount?: number;
    period?: string;
    month?: string | number;
    year?: number;
    periodStart?: string;
    periodEnd?: string;
    receiptNumber?: string;
    userEmail?: string;
    profile?: {
        company_name?: string | null;
        full_name?: string | null;
        company_address?: string | null;
        company_email?: string | null;
        email?: string;
        logo_url?: string | null;
        signature_url?: string | null;
        ninea?: string | null;
        company_ninea?: string | null;
    };
    receiptImage?: string | null;
}

interface GestionLocativeClientProps {
    leases: Lease[];
    transactions: Transaction[];
    expenses: Expense[];
    profile: Profile | null;
    userEmail?: string;
    ownerId: string;
    isViewingTerminated?: boolean;
    minDate?: string;
}

export function GestionLocativeClient({
    leases,
    transactions,
    expenses,
    profile,
    userEmail,
    ownerId,
    isViewingTerminated = false,
    minDate
}: GestionLocativeClientProps) {
    const router = useRouter();
    // √âtat pour le mois/ann√©e s√©lectionn√© - Initialis√© √† aujourd'hui
    const today = useMemo(() => new Date(), []);
    const [selectedMonth, setSelectedMonth] = useState(today.getMonth() + 1); // 1-12
    const [selectedYear, setSelectedYear] = useState(today.getFullYear());
    // Local state for optimistic updates
    const [localTransactions, setLocalTransactions] = useState<Transaction[]>(transactions);

    // Sync props to local state when they change (re-fetch)
    useEffect(() => {
        setLocalTransactions(transactions);
    }, [transactions]);

    const searchParams = useSearchParams();
    const pathname = usePathname(); // Add this
    const urlQuery = searchParams?.get('q') || '';
    const [searchQuery, setSearchQuery] = useState(urlQuery);

    // Sync state with URL when it changes
    useEffect(() => {
        setSearchQuery(urlQuery);
    }, [urlQuery]);

    // DEBUG: Mobile Crash Investigation
    useEffect(() => {

        console.log("[GestionLocativeClient] Component Mounted", {
            leasesCount: leases?.length,
            transactionsCount: transactions?.length,
            expensesCount: expenses?.length,
            hasProfile: !!profile,
            ownerId,
            timestamp: new Date().toISOString()
        });
    }, []);

    // Update URL when local search changes (Debounced)
    useEffect(() => {
        const timer = setTimeout(() => {
            if (searchQuery !== urlQuery) {
                const params = new URLSearchParams(searchParams?.toString());
                if (searchQuery) {
                    params.set('q', searchQuery);
                } else {
                    params.delete('q');
                }
                router.replace(`${pathname}?${params.toString()}`, { scroll: false });
            }
        }, 300); // 300ms debounce

        return () => clearTimeout(timer);
    }, [searchQuery, urlQuery, pathname, router, searchParams]);
    const [editingTenant, setEditingTenant] = useState<Tenant | null>(null);
    const [viewMode, setViewMode] = useState<'table' | 'cards'>('cards'); // Default to cards (Noflaye style)
    const [isReceiptOpen, setIsReceiptOpen] = useState(false);
    const [currentReceipt, setCurrentReceipt] = useState<ReceiptData | null>(null);
    const [_saving, setSaving] = useState(false);
    const [filterStatus, setFilterStatus] = useState<'all' | 'paid' | 'pending' | 'overdue'>('all');

    const handleMonthChange = (month: number, year: number) => {
        setSelectedMonth(month);
        setSelectedYear(year);
    };

    // ========================================
    // AUTO-UPDATE MONTH DETECTION (Passage automatique au nouveau mois)
    // ========================================


    // ========================================
    // FILTRAGE STRICT PAR P√âRIODE
    // ========================================
    const currentTransactions = useMemo(() => {
        return localTransactions.filter(t =>
            t.period_month === selectedMonth &&
            t.period_year === selectedYear
        );
    }, [localTransactions, selectedMonth, selectedYear]);

    // ========================================
    // KPI CALCULATIONS (Source Unique de V√©rit√© : lib/finance.ts)
    // ========================================
    const kpiStats = useMemo(() => {
        // Pr√©parer les donn√©es pour le Finance Guard
        const targetDate = new Date(selectedYear, selectedMonth - 1, 1);

        const safeLeases: LeaseInput[] = leases.map(l => ({
            id: l.id,
            monthly_amount: l.monthly_amount,
            status: l.status || 'active',
            start_date: l.start_date || null,
            billing_day: l.billing_day || 5
        }));

        const safeTransactions: TransactionInput[] = currentTransactions.map(t => ({
            id: t.id,
            lease_id: t.lease_id,
            amount_due: t.amount_due || 0,
            amount_paid: t.amount_paid,
            status: t.status
        }));

        // Filtrer les d√©penses pour le mois/ann√©e s√©lectionn√©
        const monthlyExpenses = (expenses || []).filter(e => {
            const d = new Date(e.expense_date);
            return d.getMonth() + 1 === selectedMonth && d.getFullYear() === selectedYear;
        });

        const kpis = calculateFinancials(safeLeases, safeTransactions, monthlyExpenses, targetDate);

        return {
            totalExpected: kpis.totalExpected,
            totalCollected: kpis.totalCollected,
            totalExpenses: kpis.totalExpenses,
            actualNetProfit: kpis.actualNetProfit,
            projectedNetProfit: kpis.projectedNetProfit,
            hasTemporaryDebt: kpis.hasTemporaryDebt,
            totalPending: kpis.pendingAmount, // En attente stricte (hors retards)
            paidCount: kpis.paidCount,
            pendingCount: kpis.pendingCount,
            overdueCount: kpis.overdueCount,
            collectedPercent: kpis.collectionRate,
            transactionCount: currentTransactions.length
        };
    }, [leases, currentTransactions, selectedMonth, selectedYear]);

    // ========================================
    // FORMATER LES LOCATAIRES POUR LE TABLEAU
    // ========================================
    // ========================================
    // FORMATER LES LOCATAIRES POUR LE TABLEAU
    // ========================================
    const formattedTenants: Tenant[] = useMemo(() => {
        const result: Tenant[] = [];

        (leases || []).forEach(lease => {
            // Trouver TOUTES les transactions pour CE mois s√©lectionn√©
            const leaseTransactions = currentTransactions.filter(t => t.lease_id === lease.id);

            // Consolider le statut pour √©viter les doublons
            const paidTx = leaseTransactions.find(t => t.status === 'paid' || t.status?.toLowerCase() === 'paid');
            const hasPayment = !!paidTx;

            // Calcul du jour actuel (pour d√©terminer si overdue)
            const currentDay = today.getDate();
            const currentMonthIndex = today.getMonth() + 1;
            const currentYearValue = today.getFullYear();

            const isCurrentMonth = selectedMonth === currentMonthIndex && selectedYear === currentYearValue;
            const isPastMonth = selectedYear < currentYearValue || (selectedYear === currentYearValue && selectedMonth < currentMonthIndex);

            let displayStatus: 'paid' | 'pending' | 'overdue' = 'pending';

            if (hasPayment) {
                displayStatus = 'paid';
            } else {
                const billingDay = lease.billing_day || 5;
                if (isPastMonth) {
                    displayStatus = 'overdue';
                } else if (isCurrentMonth && currentDay > billingDay) {
                    displayStatus = 'overdue';
                }
                // sinon pending (futur ou courant avant √©ch√©ance)
            }

            result.push({
                id: lease.id,
                name: lease.tenant_name,
                property: lease.properties?.title || lease.property_address || 'Bien non renseign√©',
                phone: lease.tenant_phone,
                email: lease.tenant_email,
                rentAmount: lease.monthly_amount,
                status: displayStatus,
                dueDate: lease.billing_day,
                startDate: lease.start_date,
                endDate: lease.end_date,
                last_transaction_id: paidTx?.id, // ID unique de la transaction pay√©e si elle existe
                period_month: selectedMonth,
                period_year: selectedYear,
                period_start: paidTx?.period_start || null,
                period_end: paidTx?.period_end || null
            });
        });

        return result;
    }, [leases, currentTransactions, selectedMonth, selectedYear, today]);

    // ========================================
    // FILTERED TENANTS (Search + Status Filter)
    // ========================================
    const filteredTenants = useMemo(() => {
        let result = formattedTenants;

        // 1. Status Filter (KPI Cards)
        if (filterStatus !== 'all') {
            result = result.filter(t => t.status === filterStatus);
        }

        return result;
    }, [formattedTenants, filterStatus]);

    // Helper function to format CSV data
    const _formatCSVValue = (value: string | number | null | undefined): string => {
        if (value === null || value === undefined) return '';
        const stringValue = String(value);
        if (stringValue.includes(',') || stringValue.includes('\n') || stringValue.includes('"')) {
            return `"${stringValue.replace(/"/g, '""')}"`;
        }
        return stringValue;
    };

    // Export to Excel function using xlsx
    const handleExportExcel = () => {
        // Prepare data for xlsx
        const data = formattedTenants.map(tenant => {
            const periodStr = selectedMonth && selectedYear
                ? `${selectedMonth.toString().padStart(2, '0')}/${selectedYear}`
                : '';

            const statusLabels: Record<string, string> = {
                paid: 'Pay√©',
                pending: 'En attente',
                overdue: 'Retard'
            };

            return {
                'Locataire': tenant.name || '',
                'Email': tenant.email || '',
                'T√©l√©phone': tenant.phone || '',
                'Bien': tenant.property || '',
                'P√©riode': periodStr,
                'Statut': statusLabels[tenant.status] || '',
                'Montant (FCFA)': tenant.rentAmount || 0
            };
        });

        // Import xlsx dynamically to use writeFile
        import('xlsx').then((XLSX) => {
            const worksheet = XLSX.utils.json_to_sheet(data);
            const workbook = XLSX.utils.book_new();
            XLSX.utils.book_append_sheet(workbook, worksheet, 'Locataires');

            // Generate filename
            const monthNames = ['janvier', 'fevrier', 'mars', 'avril', 'mai', 'juin',
                'juillet', 'aout', 'septembre', 'octobre', 'novembre', 'decembre'];
            const filename = `locataires-${monthNames[selectedMonth - 1]}-${selectedYear}.xlsx`;

            // Write file - this properly handles the filename
            XLSX.writeFile(workbook, filename);

            toast.success(`Export Excel t√©l√©charg√© : ${filename}`);
        });
    };

    // Format montant avec espaces
    const _formatAmount = (amount: number) => {
        return amount.toLocaleString('fr-FR');
    };

    // ========================================
    // ACTION HANDLERS (LIFTED FROM TENANT TABLE)
    // ========================================
    const handleConfirmPayment = async (leaseId: string, transactionId?: string) => {
        const tenant = formattedTenants.find(t => t.id === leaseId);
        if (!tenant) {
            toast.error('Locataire introuvable');
            return;
        }

        const result = await confirmPayment(
            leaseId,
            transactionId,
            selectedMonth,
            selectedYear
        );

        if (!result.success) {
            toast.error(result.error || 'Erreur inconnue');
            return;
        }

        // --- OPTIMISTIC UPDATE ---
        const txIndex = localTransactions.findIndex(t =>
            (transactionId && t.id === transactionId) ||
            (!transactionId && t.lease_id === leaseId && t.period_month === selectedMonth && t.period_year === selectedYear)
        );

        const updatedTx: Transaction = txIndex >= 0
            ? { ...localTransactions[txIndex], status: 'paid', amount_paid: tenant.rentAmount, amount_due: tenant.rentAmount }
            : {
                id: transactionId || `temp-${Date.now()}`,
                lease_id: leaseId,
                period_month: selectedMonth,
                period_year: selectedYear,
                status: 'paid',
                amount_due: tenant.rentAmount,
                amount_paid: tenant.rentAmount,
                paid_at: new Date().toISOString()
            };

        if (txIndex >= 0) {
            const newTxs = [...localTransactions];
            newTxs[txIndex] = updatedTx;
            setLocalTransactions(newTxs);
        } else {
            setLocalTransactions([...localTransactions, updatedTx]);
        }
        // ------------------------

        const shouldSendReceipt = window.confirm(
            `Le loyer est marqu√© comme pay√©. Envoyer la quittance par email √† ${tenant.name} ?`
        );

        if (shouldSendReceipt) {
            if (!tenant.email) {
                toast.error('Email manquant pour ce locataire');
                return;
            }

            const periodMonth = tenant.period_month?.toString().padStart(2, '0') || '01';
            const periodYear = tenant.period_year || new Date().getFullYear();
            const periodStartDate = tenant.period_start
                ? new Date(tenant.period_start)
                : new Date(periodYear, parseInt(periodMonth) - 1, 1);
            const periodEndDate = tenant.period_end
                ? new Date(tenant.period_end)
                : new Date(periodYear, parseInt(periodMonth), 0);

            const receiptData = {
                tenantName: tenant.name,
                tenantEmail: tenant.email,
                tenantPhone: tenant.phone || '',
                tenantAddress: tenant.property,
                amount: Number(tenant.rentAmount) || 0,
                periodMonth: `${periodMonth}/${periodYear}`,
                periodStart: periodStartDate.toLocaleDateString('fr-FR'),
                periodEnd: periodEndDate.toLocaleDateString('fr-FR'),
                receiptNumber: `QUITT-${Date.now().toString().slice(-6)}`,
                ownerName: profile?.company_name || profile?.full_name || "Propri√©taire",
                ownerAddress: profile?.company_address || "Adresse non renseign√©e",
                ownerNinea: profile?.company_ninea || undefined,
                ownerLogo: profile?.logo_url || undefined,
                ownerSignature: profile?.signature_url || undefined,
                ownerEmail: profile?.company_email || undefined,
                ownerAccountEmail: userEmail || undefined,
                propertyAddress: tenant.property,
            };

            toast.promise(
                fetch('/api/send-receipt', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify(receiptData),
                }).then(async (res) => {
                    const data = await res.json();
                    if (!res.ok) throw new Error(data.error || 'Erreur lors de l\'envoi');
                    return data;
                }),
                {
                    loading: 'Envoi de la quittance...',
                    success: 'Quittance envoy√©e !',
                    error: (err) => `Erreur: ${err.message}`,
                }
            );
        } else {
            toast.success(result.message || "Paiement enregistr√© !");
        }

        // Background refresh to true data
        router.refresh();
    };

    const handleViewReceipt = (tenant: Tenant) => {
        const periodMonth = tenant.period_month?.toString().padStart(2, '0') || '01';
        const periodYear = tenant.period_year || new Date().getFullYear();

        setCurrentReceipt({
            tenant: {
                tenant_name: tenant.name,
                email: tenant.email,
                phone: tenant.phone,
                address: tenant.property
            },
            profile: {
                company_name: profile?.company_name || profile?.full_name || "Propri√©taire",
                company_address: profile?.company_address || "Adresse non renseign√©e",
                company_email: profile?.company_email || undefined,
                company_ninea: profile?.company_ninea || undefined,
                logo_url: profile?.logo_url || undefined,
                signature_url: profile?.signature_url || undefined
            },
            userEmail: userEmail,
            amount: tenant.rentAmount,
            month: periodMonth,
            year: periodYear,
            property_address: tenant.property
        });
        setIsReceiptOpen(true);
    };

    const handleTerminateLease = async (leaseId: string, tenantName: string) => {
        const confirmed = window.confirm(
            `‚ö†Ô∏è Voulez-vous vraiment r√©silier le bail de ${tenantName} ?`
        );
        if (!confirmed) return;

        setSaving(true);
        const result = await terminateLease(leaseId);
        setSaving(false);

        if (result.success) {
            toast.success(result.message || 'Bail r√©sili√©');
            window.location.reload();
        } else {
            toast.error(result.error || 'Erreur');
        }
    };

    const handleReactivateLease = async (leaseId: string, tenantName: string) => {
        const confirmed = window.confirm(
            `‚úÖ R√©activer le bail de ${tenantName} ?`
        );
        if (!confirmed) return;

        setSaving(true);
        const result = await reactivateLease(leaseId);
        setSaving(false);

        if (result.success) {
            toast.success(result.message || 'Bail r√©activ√©');
            window.location.reload();
        } else {
            toast.error(result.error || 'Erreur');
        }
    };

    const handleInvite = async (leaseId: string) => {
        toast.promise(sendTenantInvitation(leaseId), {
            loading: 'Envoi de l\'invitation...',
            success: (data) => {
                if (!data.success) throw new Error(data.error);
                return data.message || 'Invitation envoy√©e avec succ√®s';
            },
            error: (err) => `Erreur: ${err.message}`
        });
    };

    const handleSendReminders = async () => {
        // Optimistic UI interaction
        const promise = processLateReminders();

        toast.promise(promise, {
            loading: 'Envoi des relances...',
            success: (result) => {
                if (result.count > 0) {
                    return `‚úÖ ${result.count} relance(s) envoy√©e(s)`;
                } else {
                    return '‚ÑπÔ∏è Aucune relance √† envoyer';
                }
            },
            error: '‚ùå Erreur lors de l\'envoi'
        });

        try {
            await promise;
            router.refresh(); // Refresh to update reminder status/counts
        } catch (error) {
            console.error(error);
        }
    };

    return (
        <div className="space-y-0 w-full">
            <ReceiptModal
                isOpen={isReceiptOpen}
                onClose={() => setIsReceiptOpen(false)}
                data={currentReceipt}
            />

            {/* ========================================
                DASHBOARD STATS - Style Noflaye
                ======================================== */}
            <div id="tour-stats">
                <DashboardStats
                    stats={{
                        totalExpected: kpiStats.totalExpected,
                        totalCollected: kpiStats.totalCollected,
                        totalPending: kpiStats.totalPending,
                        paidCount: kpiStats.paidCount,
                        pendingCount: kpiStats.pendingCount,
                        overdueCount: kpiStats.overdueCount,
                        collectedPercent: kpiStats.collectedPercent,
                        totalTenants: leases.length,
                    }}
                    onFilterChange={setFilterStatus}
                    activeFilter={filterStatus}
                />
            </div>

            {/* ========================================
                QUICK ACTIONS - Style Noflaye
                ======================================== */}
            <QuickActions
                onExportExcel={handleExportExcel}
                pendingCount={kpiStats.pendingCount}
                overdueCount={kpiStats.overdueCount}
                onSendReminders={handleSendReminders}
                ownerId={ownerId}
                profile={profile}
            />

            {/* Ligne 1: Controls (Month Selector aligned left) */}
            <div className="flex flex-col sm:flex-row gap-2 sm:items-center justify-between">
                {/* S√©lecteur de mois - Align√© √† gauche */}
                <MonthSelector
                    selectedMonth={selectedMonth}
                    selectedYear={selectedYear}
                    onMonthChange={handleMonthChange}
                    minDate={minDate}
                />

                {/* View Toggle + Export CSV */}
                <div className="flex items-center gap-2">
                    {/* View Mode Toggle */}
                    <div className="flex items-center rounded-lg p-0.5 border border-border bg-muted">
                        <button
                            onClick={() => setViewMode('cards')}
                            className={`p-1.5 rounded-md transition-colors ${viewMode === 'cards'
                                ? 'bg-background text-primary shadow-sm'
                                : 'text-muted-foreground hover:text-foreground'
                                }`}
                            title="Vue cartes"
                        >
                            <LayoutGrid className="w-4 h-4" />
                        </button>
                        <button
                            onClick={() => setViewMode('table')}
                            className={`p-1.5 rounded-md transition-colors ${viewMode === 'table'
                                ? 'bg-background text-primary shadow-sm'
                                : 'text-muted-foreground hover:text-foreground'
                                }`}
                            title="Vue tableau"
                        >
                            <List className="w-4 h-4" />
                        </button>
                    </div>

                    {/* Bouton Export Excel */}
                    <Button
                        id="tour-export-excel"
                        onClick={() => {
                            handleExportExcel();
                        }}
                        variant="outline"
                        size="sm"
                        className="h-9 px-3 shrink-0 bg-background border-border text-foreground hover:bg-muted"
                        disabled={formattedTenants.length === 0}
                    >
                        <Download className="w-4 h-4 mr-1.5" />
                        Excel
                    </Button>
                </div>
            </div>

            {/* ========================================
                TENANT VIEW - Cards or Table
                ======================================== */}
            <div id="tour-tenants-list" className="mt-6">
                <div id="tour-add-tenant">
                    {viewMode === 'cards' ? (
                        <TenantCardGrid
                            tenants={filteredTenants}
                            onEdit={(tenant) => setEditingTenant(tenant)}
                            ownerId={ownerId}
                            isViewingTerminated={isViewingTerminated}
                            searchQuery={searchQuery}
                            onConfirmPayment={handleConfirmPayment}
                            onViewReceipt={handleViewReceipt}
                            onTerminate={handleTerminateLease}
                            onReactivate={handleReactivateLease}
                            onInvite={handleInvite}
                        />
                    ) : (
                        <TenantTable
                            tenants={filteredTenants}
                            profile={profile}
                            userEmail={userEmail}
                            ownerId={ownerId}
                            isViewingTerminated={isViewingTerminated}
                            searchQuery={searchQuery}
                            onEdit={(tenant) => setEditingTenant(tenant)}
                            onDelete={deleteTransaction}
                            onDeleteLease={deleteLease}
                            onConfirmPayment={handleConfirmPayment}
                            onViewReceipt={handleViewReceipt}
                            onTerminate={handleTerminateLease}
                            onReactivate={handleReactivateLease}
                            onInvite={handleInvite}
                        />
                    )}
                </div>
            </div>

            {/* Modale d'√©dition */}
            {
                editingTenant && (
                    <EditTenantDialog
                        isOpen={!!editingTenant}
                        onClose={() => setEditingTenant(null)}
                        tenant={editingTenant}
                    />
                )
            }
        </div >
    );
}

'use client';

import { useState, useEffect } from 'react';
import { Wrench, Clock, CheckCircle2, X, Plus, ChevronDown, ChevronUp, CircleDollarSign, Calendar, Loader2, Send, Star, Phone, MapPin, AlertTriangle, Image as ImageIcon, User, Home, FileText, Upload } from 'lucide-react';
import { createClient } from '@/utils/supabase/client';
import { format } from 'date-fns';
import { fr } from 'date-fns/locale';
import { Button } from "@/components/ui/button";
import { toast } from 'sonner';
import { EmptyState } from '@/components/ui/empty-state';
import {
    Dialog,
    DialogContent,
    DialogDescription,
    DialogHeader,
    DialogTitle,
    DialogFooter,
} from "@/components/ui/dialog"
import { createMaintenanceRequest, getActiveLeases, submitQuote, approveQuoteByOwner, completeIntervention, validateMaintenanceRequest, rejectMaintenanceRequest, handleOwnerRescheduleResponse } from '../actions';

interface MaintenanceRequest {
    id: string;
    description: string;
    category?: string;
    status: 'submitted' | 'open' | 'artisan_found' | 'awaiting_approval' | 'approved' | 'in_progress' | 'completed' | 'rejected' | 'cancelled';
    quoted_price?: number;
    intervention_date?: string;
    created_at?: string;
    tenant_name?: string;
    // Colonnes artisan
    artisan_name?: string;
    artisan_phone?: string;
    artisan_rating?: number;
    artisan_address?: string;
    tenant_response?: 'confirmed' | 'reschedule_requested';
    tenant_suggested_date?: string;
    rejection_reason?: string;
    owner_approved?: boolean;
    photo_urls?: string[];
    quote_url?: string;
    property_title?: string;
    property_images?: string[];
    tenant_email?: string;
    is_new?: boolean;
}

interface Lease {
    id: string;
    tenant_name: string;
    property_address?: string;
}

interface MaintenanceHubProps {
    requests?: MaintenanceRequest[];
}

const CATEGORIES = [
    { value: 'Plomberie', emoji: 'üîß' },
    { value: '√âlectricit√©', emoji: '‚ö°' },
    { value: 'Ma√ßonnerie', emoji: 'üß±' },
    { value: 'Climatisation', emoji: '‚ùÑÔ∏è' },
    { value: 'Serrurerie', emoji: 'üîë' },
    { value: 'Peinture', emoji: 'üé®' },
    { value: 'Autre', emoji: 'üìã' }
];

export function MaintenanceHub({ requests = [] }: MaintenanceHubProps) {
    const [showForm, setShowForm] = useState(false);
    const [description, setDescription] = useState('');
    const [category, setCategory] = useState('Plomberie');
    const [selectedLease, setSelectedLease] = useState('');
    const [leases, setLeases] = useState<Lease[]>([]);
    const [submitting, setSubmitting] = useState(false);
    const [processingId, setProcessingId] = useState<string | null>(null);

    // Modal saisie devis
    const [showQuoteModal, setShowQuoteModal] = useState(false);
    const [quoteRequestId, setQuoteRequestId] = useState<string | null>(null);
    const [quotePrice, setQuotePrice] = useState('');
    const [quoteDate, setQuoteDate] = useState('');
    const [quoteFile, setQuoteFile] = useState<File | null>(null);
    const [isUploading, setIsUploading] = useState(false);
    const [expandedId, setExpandedId] = useState<string | null>(null);

    useEffect(() => {
        if (showForm) {
            getActiveLeases().then(result => {
                if (result.success && result.data) {
                    setLeases(result.data);
                    if (result.data.length > 0) {
                        setSelectedLease(result.data[0].id);
                    }
                }
            });
        }
    }, [showForm]);

    const getStatusStyle = (status: string) => {
        switch (status) {
            case 'submitted': return "bg-zinc-500/10 text-zinc-400";
            case 'open': return "bg-blue-500/10 text-blue-400";
            case 'artisan_found': return "bg-emerald-500/10 text-emerald-400";
            case 'awaiting_approval': return "bg-primary/10 text-primary";
            case 'approved': return "bg-purple-500/10 text-purple-400";
            case 'in_progress': return "bg-yellow-500/10 text-yellow-400";
            case 'completed': return "bg-green-500/10 text-green-400";
            case 'rejected': return "bg-red-500/10 text-red-400";
            case 'cancelled': return "bg-zinc-500/10 text-zinc-500";
            default: return "bg-muted text-muted-foreground";
        }
    };

    const getStatusLabel = (status: string) => {
        switch (status) {
            case 'submitted': return "√Ä valider";
            case 'open': return "Recherche...";
            case 'artisan_found': return "Artisan trouv√©";
            case 'awaiting_approval': return "Validation requise";
            case 'approved': return "Approuv√©";
            case 'in_progress': return "En cours";
            case 'completed': return "Termin√©";
            case 'rejected': return "Rejet√©";
            case 'cancelled': return "Annul√©";
            default: return status;
        }
    };

    const handleSubmit = async () => {
        if (!description.trim()) {
            toast.error('Veuillez d√©crire le probl√®me');
            return;
        }

        setSubmitting(true);
        const result = await createMaintenanceRequest({
            leaseId: selectedLease || undefined,
            description: description.trim(),
            category: category
        });
        setSubmitting(false);

        if (result.success) {
            toast.success(result.message || 'Intervention signal√©e!');
            setDescription('');
            setCategory('Plomberie');
            setShowForm(false);
        } else {
            toast.error(result.error || 'Erreur lors du signalement');
        }
    };

    const openQuoteModal = (requestId: string, currentPrice?: number, currentDate?: string) => {
        setQuoteRequestId(requestId);
        setQuotePrice(currentPrice ? currentPrice.toString() : '');
        // Convert ISO date to YYYY-MM-DDTHH:mm for datetime-local
        if (currentDate) {
            const date = new Date(currentDate);
            const formatted = date.toISOString().slice(0, 16);
            setQuoteDate(formatted);
        } else {
            setQuoteDate('');
        }
        setQuoteFile(null);
        setShowQuoteModal(true);
    };

    const handleSubmitQuote = async () => {
        if (!quoteRequestId || !quotePrice || !quoteDate) {
            toast.error('Veuillez remplir tous les champs');
            return;
        }

        setProcessingId(quoteRequestId);
        let finalQuoteUrl = '';

        if (quoteFile) {
            setIsUploading(true);
            try {
                const supabase = createClient();
                const fileExt = quoteFile.name.split('.').pop();
                const fileName = `maintenance/quote_${Date.now()}.${fileExt}`;

                const { data: _uploadData, error: uploadError } = await supabase.storage
                    .from('properties')
                    .upload(fileName, quoteFile);

                if (uploadError) throw uploadError;

                const { data: { publicUrl } } = supabase.storage
                    .from('properties')
                    .getPublicUrl(fileName);

                finalQuoteUrl = publicUrl;
            } catch (error: any) {
                toast.error("Erreur upload: " + error.message);
                setProcessingId(null);
                setIsUploading(false);
                return;
            } finally {
                setIsUploading(false);
            }
        }

        const result = await submitQuote(quoteRequestId, {
            quoted_price: parseInt(quotePrice),
            intervention_date: quoteDate,
            quote_url: finalQuoteUrl || undefined
        });
        setProcessingId(null);

        if (result.success) {
            toast.success(result.message || 'Devis enregistr√© !');
            setShowQuoteModal(false);
            setQuoteFile(null);
        } else {
            toast.error(result.error || 'Erreur');
        }
    };

    const handleApproveQuote = async (requestId: string) => {
        setProcessingId(requestId);
        const result = await approveQuoteByOwner(requestId);
        setProcessingId(null);
        if (result.success) {
            toast.success(result.message || 'Devis approuv√© !');
        } else {
            toast.error(result.error || 'Erreur');
        }
    };

    const handleAcceptReschedule = async (requestId: string) => {
        setProcessingId(requestId);
        try {
            const result = await handleOwnerRescheduleResponse(requestId, 'accept');
            if (result.success) {
                toast.success(result.message);
            } else {
                toast.error(result.error || "Erreur lors de l'acceptation du report");
            }
        } catch (_error) {
            toast.error("Erreur de connexion");
        } finally {
            setProcessingId(null);
        }
    };

    const handleValidateRequest = async (requestId: string) => {
        setProcessingId(requestId);
        const result = await validateMaintenanceRequest(requestId);
        setProcessingId(null);
        if (result.success) {
            toast.success(result.message || 'Intervention valid√©e !');
        } else {
            toast.error(result.error || 'Erreur lors de la validation');
        }
    };

    const handleRejectRequest = async (requestId: string) => {
        const reason = window.prompt("Motif du rejet (ex: d√©j√† r√©par√©, demande invalide...) :");
        if (reason === null) return; // Annul√© par l'utilisateur

        setProcessingId(requestId);
        const result = await rejectMaintenanceRequest(requestId, reason);
        setProcessingId(null);
        if (result.success) {
            toast.success(result.message || 'Intervention rejet√©e');
        } else {
            toast.error(result.error || 'Erreur');
        }
    };

    const handleComplete = async (requestId: string) => {
        setProcessingId(requestId);
        const result = await completeIntervention(requestId);
        setProcessingId(null);
        if (result.success) {
            toast.success(result.message || 'Intervention termin√©e !');
        } else {
            toast.error(result.error || 'Erreur');
        }
    };

    return (
        <div className="relative overflow-hidden rounded-xl p-5 border border-border bg-card">
            <div className="relative space-y-4">
                {/* Header */}
                <div className="flex items-center justify-between">
                    <div className="flex items-center gap-3">
                        <div className="p-2.5 rounded-xl border border-border bg-muted">
                            <Wrench className="w-5 h-5 text-foreground" />
                        </div>
                        <div>
                            <h3 className="font-semibold text-sm text-foreground">Interventions</h3>
                            <p className="text-xs text-muted-foreground">Maintenance & travaux</p>
                        </div>
                    </div>
                    <div id="tour-intervention-signaler">
                        <Button
                            size="sm"
                            variant={showForm ? "ghost" : "outline"}
                            className="text-xs h-8"
                            onClick={() => setShowForm(!showForm)}
                        >
                            {showForm ? <><X className="w-3 h-3 mr-1" /> Annuler</> : '+ Signaler'}
                        </Button>
                    </div>
                </div>

                {/* Formulaire */}
                {showForm && (
                    <div className="p-4 rounded-xl space-y-3 border border-border bg-muted/30">
                        <p className="text-xs font-medium text-primary">Nouvelle intervention</p>

                        {leases.length > 0 && (
                            <div className="relative">
                                <select
                                    value={selectedLease}
                                    onChange={(e) => setSelectedLease(e.target.value)}
                                    className="w-full rounded-lg px-3 py-2 pr-8 text-sm appearance-none cursor-pointer border border-border bg-background text-foreground"
                                >
                                    {leases.map(lease => (
                                        <option key={lease.id} value={lease.id}>
                                            {lease.tenant_name}
                                        </option>
                                    ))}
                                </select>
                                <ChevronDown className="absolute right-2 top-1/2 -translate-y-1/2 w-4 h-4 pointer-events-none text-muted-foreground" />
                            </div>
                        )}

                        <textarea
                            placeholder="D√©crivez le probl√®me..."
                            value={description}
                            onChange={(e) => setDescription(e.target.value)}
                            className="w-full rounded-lg px-3 py-2 text-sm resize-none h-16 border border-border bg-background text-foreground placeholder:text-muted-foreground focus:ring-1 focus:ring-primary focus:outline-none"
                        />

                        <div className="flex gap-2">
                            <div className="relative flex-1">
                                <select
                                    value={category}
                                    onChange={(e) => setCategory(e.target.value)}
