            {/* Actions Grid */}
            <div className="flex flex-wrap gap-2">
                {actions.map((action) => {
                    const baseClasses = `
                        relative flex items-center justify-center sm:justify-start
                        gap-0 sm:gap-2 
                        p-1 sm:px-3 sm:py-2
                        rounded-xl border border-border bg-card
                        transition-all duration-200
                        cursor-pointer
                        group
                        hover:bg-muted
                        min-w-[44px] sm:min-w-0
                        h-[44px] sm:h-auto
                    `;

                    if (action.type === 'link') {
                        return (
                            <Link
                                key={action.id}
