'use client';

import { useState } from 'react';
import { Trash2, Building2, Calendar, Tag, Loader2, AlertCircle } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Expense, deleteExpense } from '../expenses-actions';
import { EXPENSE_CATEGORY_LABELS, ExpenseCategory } from '../expense-types';
import { toast } from 'sonner';
import { useTheme } from "@/components/theme-provider";

interface ExpenseListProps {
    expenses: Expense[];
    onExpenseDeleted?: () => void;
}

export function ExpenseList({ expenses, onExpenseDeleted }: ExpenseListProps) {
    const { isDark } = useTheme();
    const [deletingId, setDeletingId] = useState<string | null>(null);

    const handleDelete = async (id: string) => {
        if (!window.confirm('Supprimer cette d√©pense ?')) return;

        setDeletingId(id);
        const result = await deleteExpense(id);
        setDeletingId(null);

        if (result.success) {
            toast.success('D√©pense supprim√©e');
            onExpenseDeleted?.();
        } else {
            toast.error(result.error || 'Erreur');
        }
    };

    const formatAmount = (amount: number) => amount.toLocaleString('fr-FR');
    const formatDate = (dateStr: string) => {
        const d = new Date(dateStr);
        return d.toLocaleDateString('fr-FR', { day: '2-digit', month: 'short', year: 'numeric' });
    };

    const getCategoryColor = (cat: ExpenseCategory) => {
        const colors: Record<ExpenseCategory, string> = {
            maintenance: 'bg-brand/10 text-brand border-brand/20',
            tax: 'bg-red-500/10 text-red-400 border-red-500/20',
            utility: 'bg-blue-500/10 text-blue-400 border-blue-500/20',
            insurance: 'bg-purple-500/10 text-purple-400 border-purple-500/20',
            management_fee: 'bg-slate-500/10 text-slate-400 border-slate-500/20',
            other: 'bg-gray-500/10 text-gray-400 border-gray-500/20',
        };
        return colors[cat] || colors.other;
    };

    if (expenses.length === 0) {
        return (
            <div className={`flex flex-col items-center justify-center py-12 ${isDark ? 'text-slate-500' : 'text-gray-500'}`}>
                <AlertCircle className="w-12 h-12 mb-4 opacity-50" />
                <p className="text-lg font-medium">Aucune d√©pense ce mois</p>
                <p className="text-sm">Cliquez sur &quot;D√©pense&quot; pour en ajouter une.</p>
            </div>
        );
    }

    // Calculate total
    const totalExpenses = expenses.reduce((sum, e) => sum + e.amount, 0);

    return (
        <div className="space-y-4">
            {/* Summary */}
            <div className="flex justify-between items-center p-4 bg-red-500/10 border border-red-500/20 rounded-xl">
                <span className={`font-medium ${isDark ? 'text-slate-300' : 'text-gray-700'}`}>Total D√©penses</span>
                <span className="text-red-400 font-bold text-xl">{formatAmount(totalExpenses)} FCFA</span>
            </div>

            {/* Mobile View: Cards */}
            <div className="md:hidden space-y-3">
                {expenses.map((expense) => (
                    <div key={expense.id} className={`p-4 rounded-xl border ${isDark ? 'bg-slate-900/50 border-slate-800' : 'bg-white border-gray-100'} shadow-sm`}>
                        <div className="flex justify-between items-start mb-3">
                            <span className={`text-xs px-2 py-1 rounded-full border whitespace-nowrap ${getCategoryColor(expense.category)}`}>
                                {EXPENSE_CATEGORY_LABELS[expense.category]}
                            </span>
                            <span className="text-red-400 font-bold">{formatAmount(expense.amount)} FCFA</span>
                        </div>

                        <div className="space-y-2">
                            <p className={`text-sm font-medium ${isDark ? 'text-slate-200' : 'text-gray-800'}`}>
                                {expense.description || 'Sans description'}
                            </p>

                            <div className="flex flex-wrap gap-x-4 gap-y-1 text-xs text-slate-500">
                                <div className="flex items-center gap-1">
                                    <Calendar className="w-3 h-3" />
                                    {formatDate(expense.expense_date)}
                                </div>
                                {expense.property_address && (
                                    <div className="flex items-center gap-1">
                                        <Building2 className="w-3 h-3" />
                                        <span className="truncate max-w-[150px]">{expense.property_address}</span>
                                    </div>
                                )}
                            </div>
                        </div>

                        <div className="mt-3 pt-3 border-t border-slate-800/50 flex justify-end">
                            <Button
                                variant="ghost"
                                size="sm"
                                onClick={() => handleDelete(expense.id)}
                                disabled={deletingId === expense.id}
                                className="text-slate-500 hover:text-red-400 hover:bg-red-500/10 h-8 px-2"
                            >
                                {deletingId === expense.id ? (
                                    <Loader2 className="w-4 h-4 animate-spin mr-2" />
                                ) : (
                                    <Trash2 className="w-4 h-4 mr-2" />
                                )}
                                Supprimer
                            </Button>
                        </div>
                    </div>
                ))}
            </div>

            {/* Desktop View: Table */}
            <div className="hidden md:block overflow-x-auto">
                <table className="w-full">
                    <thead>
                        <tr className={`text-left text-xs border-b ${isDark ? 'text-slate-500 border-slate-800' : 'text-gray-500 border-gray-200'}`}>
                            <th className="pb-3 font-medium">Date</th>
                            <th className="pb-3 font-medium">Cat√©gorie</th>
                            <th className="pb-3 font-medium">Description</th>
                            <th className="pb-3 font-medium">Bien</th>
                            <th className="pb-3 font-medium text-right">Montant</th>
                            <th className="pb-3 font-medium w-10"></th>
                        </tr>
                    </thead>
                    <tbody className={`divide-y ${isDark ? 'divide-slate-800' : 'divide-gray-200'}`}>
                        {expenses.map((expense) => (
                            <tr key={expense.id} className={`group transition-colors ${isDark ? 'hover:bg-slate-800/30' : 'hover:bg-gray-50'}`}>
                                <td className={`py-4 text-sm whitespace-nowrap ${isDark ? 'text-slate-400' : 'text-gray-600'}`}>
                                    <div className="flex items-center gap-2">
                                        <Calendar className={`w-4 h-4 ${isDark ? 'text-slate-600' : 'text-gray-400'}`} />
                                        {formatDate(expense.expense_date)}
                                    </div>
                                </td>
                                <td className="py-4">
                                    <span className={`text-xs px-2 py-1 rounded-full border whitespace-nowrap ${getCategoryColor(expense.category)}`}>
                                        {EXPENSE_CATEGORY_LABELS[expense.category]}
                                    </span>
                                </td>
                                <td className={`py-4 text-sm max-w-[250px] truncate ${isDark ? 'text-slate-300' : 'text-gray-700'}`}>
                                    {expense.description || '-'}
                                </td>
                                <td className={`py-4 text-sm ${isDark ? 'text-slate-400' : 'text-gray-600'}`}>
                                    {expense.property_address ? (
                                        <div className="flex items-center gap-1">
                                            <Building2 className="w-3 h-3" />
                                            <span className="truncate max-w-[150px]">{expense.property_address}</span>
                                        </div>
                                    ) : (
                                        <span className={isDark ? 'text-slate-600' : 'text-gray-400'}>G√©n√©ral</span>
                                    )}
                                </td>
                                <td className="py-4 text-right whitespace-nowrap">
                                    <span className="text-red-400 font-medium">{formatAmount(expense.amount)} FCFA</span>
                                </td>
                                <td className="py-4 text-right">
                                    <Button
                                        variant="ghost"
                                        size="sm"
                                        onClick={() => handleDelete(expense.id)}
                                        disabled={deletingId === expense.id}
                                        className="opacity-0 group-hover:opacity-100 text-slate-500 hover:text-red-400 hover:bg-red-500/10 h-8 w-8 p-0"
                                    >
                                        {deletingId === expense.id ? (
                                            <Loader2 className="w-4 h-4 animate-spin" />
                                        ) : (
                                            <Trash2 className="w-4 h-4" />
                                        )}
                                    </Button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

