export const inviteTeamMember = safeAction(
  "inviteTeamMember",
  inviteMemberSchema,
  async (data, { userId, teamId: _ctxTeamId }) => {
    // V√©rifier permission
    const { user, teamId } = await requireTeamPermission("team.members.invite");

    if (teamId !== data.teamId) {
      throw new Error("Acc√®s non autoris√©");
    }

    const supabase = await createClient();

    // ‚úÖ QUOTA: V√©rifier les limites du plan (Starter/Pro/Enterprise)
    const access = await checkFeatureAccess(data.teamId, 'invite_member');

    if (!access.allowed) {
      throw new Error(access.message || "Impossible d'inviter plus de membres avec votre plan actuel.");
    }

    // R√©cup√©rer information √©quipe pour l'email (optimisation: d√©j√† fait dans checkFeatureAccess mais on a besoin du nom ici)
    const supabaseAdmin = createAdminClient();

    // V√©rifier si l'email est d√©j√† membre
    const { data: profiles } = await supabase
      .from("profiles")
      .select("id")
      .eq("email", data.email.toLowerCase())
      .maybeSingle();

    if (profiles) {
      const { data: existingMember } = await supabase
        .from("team_members")
        .select("id")
        .eq("team_id", data.teamId)
        .eq("user_id", profiles.id)
        .eq("status", "active")
        .maybeSingle();

      if (existingMember) {
        throw new Error("Cet utilisateur est d√©j√† membre de l'√©quipe");
      }
    }

    // V√©rifier si invitation d√©j√† en cours
    const { data: existingInvite } = await supabaseAdmin
      .from("team_invitations")
      .select("id")
      .eq("team_id", data.teamId)
      .eq("email", data.email.toLowerCase())
      .eq("status", "pending")
      .maybeSingle();

    if (existingInvite) {
