                <StatBadge
                  icon={<Users className="h-4 w-4" />}
                  value={stats.total_members}
                  label="Membres"
                  isDark={isDark}
                  onClick={() => setActiveTab("members")}
                />
                {stats.pending_invitations > 0 && (
                  <StatBadge
