        .select("team_id")
        .eq("user_id", user.id);

    const userTeamIds = teamMemberships?.map(tm => tm.team_id) || [];

    // Construire la requ√™te avec OR: owner_id OU team_id
    let leaseQuery = supabase
        .from("leases")
        .select(`
            *,
            properties:property_id(id, title, images),
            rental_transactions (
                id,
                period_month,
                period_year,
                amount_due,
                status,
                paid_at,
                created_at
            ),
            maintenance_requests (
                id,
                description,
                status,
                created_at
            )
        `)
        .eq("id", id);

    // Filtre: owner_id = user.id OU team_id dans les √©quipes de l'utilisateur
    if (userTeamIds.length > 0) {
        leaseQuery = leaseQuery.or(`owner_id.eq.${user.id},team_id.in.(${userTeamIds.join(',')})`);
    } else {
        leaseQuery = leaseQuery.eq("owner_id", user.id);
    }

    const { data: lease } = await leaseQuery.single();

    const { data: profile } = await supabase
        .from('profiles')
        .select('*')
        .eq('id', user.id)
        .single();

    if (!lease) notFound();

    // Calculate generic stats
    const transactions = lease.rental_transactions || [];
    const totalPaid = transactions
        .filter((t: any) => t.status === "paid")
        .reduce((sum: number, t: any) => sum + (t.amount_due || 0), 0);

    const pendingAmount = transactions
        .filter((t: any) => t.status === "pending")
        .reduce((sum: number, t: any) => sum + (t.amount_due || 0), 0);

    const overdueCount = transactions.filter((t: any) => t.status === "overdue").length;

    return (
        <TenantProfileClient
            lease={lease}
            transactions={transactions}
            totalPaid={totalPaid}
            pendingAmount={pendingAmount}
            overdueCount={overdueCount}

            user={user}
            profile={profile}
        />
    );
}

'use server';

import { createClient } from '@/utils/supabase/server';
import { revalidatePath } from 'next/cache';
import { sendEmail } from '@/lib/mail';
import { getUserTeamContext } from "@/lib/team-context";
import { requireTeamPermission } from "@/lib/permissions";
import { notifyTenant } from "@/lib/notifications";

export async function getOwnerMessages(leaseId: string) {
    const { teamId, user } = await getUserTeamContext();
    const supabase = await createClient();

    if (!user) return { error: "Non authentifi√©" };

    // V√©rifier que le bail appartient bien √† l'√©quipe
    const { data: lease } = await supabase
        .from('leases')
        .select('id, tenant_name, team_id')
        .eq('id', leaseId)
        .eq('team_id', teamId)
        .single();

    if (!lease) return { error: "Bail introuvable ou acc√®s refus√©" };

    const { data: messages } = await supabase
        .from('messages')
        .select('*')
        .eq('lease_id', leaseId)
        .order('created_at', { ascending: true });

    return { messages: messages || [], tenantName: lease.tenant_name };
}

/**
 * Mark all tenant messages in a conversation as read by the owner.
 */
export async function markConversationAsRead(leaseId: string) {
    const { teamId, user } = await getUserTeamContext();
    const supabase = await createClient();

    if (!user) return;

    // Only mark tenant messages as read (owner's own messages don't need read_at)
    await supabase
        .from('messages')
        .update({ read_at: new Date().toISOString() })
        .eq('lease_id', leaseId)
        .eq('sender_type', 'tenant')
        .is('read_at', null);

    revalidatePath('/gestion/messages');
}

export async function sendOwnerMessage(leaseId: string, content: string) {
    const { teamId, user } = await getUserTeamContext();
    await requireTeamPermission('leases.view'); // Base permission for messaging
    const supabase = await createClient();

    if (!user) return { error: "Non authentifi√©" };

    const { error } = await supabase
        .from('messages')
        .insert({
            lease_id: leaseId,
            sender_id: user.id,
            sender_type: 'owner',
            team_id: teamId,
            content: content
        });

    if (error) {
        console.error("Erreur envoi message owner:", error);
        return { error: "Impossible d'envoyer le message" };
    }

    // Notification Email au Locataire
    const { data: lease } = await supabase
        .from('leases')
        .select('tenant_email, tenant_name, owner:profiles(full_name)') // On r√©cup√®re l'email locataire
        .eq('id', leaseId)
        .single();

    if (lease?.tenant_email) {
        // @ts-ignore
        const ownerName = lease.owner?.full_name || "Votre Propri√©taire";

        try {
            await sendEmail({
                to: lease.tenant_email,
                subject: `Nouveau message de ${ownerName}`,
                html: `
                    <p>Bonjour ${lease.tenant_name || ''},</p>
                    <p>Vous avez re√ßu un nouveau message de <strong>${ownerName}</strong>.</p>
                    <p><em>"${content}"</em></p>
                    <a href="${process.env.NEXT_PUBLIC_APP_URL}/locataire/messages">Voir la conversation</a>
                `
            });
        } catch (mailError) {
            console.error("Erreur notif mail owner:", mailError);
        }

        // Notification Push
        await notifyTenant({
            leaseId,
            title: `Message de ${ownerName}`,
            message: content,
            url: "/locataire/messages"
        });
    }

    revalidatePath(`/gestion/messages/${leaseId}`);
    return { success: true };
}

import { createClient } from "@/utils/supabase/server";
import { MessagesPageClient } from "./MessagesPageClient";

export default async function OwnerMessagesPage() {
    const supabase = await createClient();
    const { data: { user } } = await supabase.auth.getUser();

    // 1. R√©cup√©rer TOUS les baux actifs (pour le dropdown "Nouveau message")
    const { data: allLeases } = await supabase
        .from('leases')
        .select('id, tenant_name, property:properties(title)')
        .eq('owner_id', user?.id)
        .eq('status', 'active');

    // 2. R√©cup√©rer les messages avec info de lecture
    const { data: messagesData } = await supabase
        .from('messages')
        .select('lease_id, sender_type, read_at');

    const leaseIdsWithMessages = new Set(messagesData?.map(m => m.lease_id) || []);

    // 3. Compter les messages non lus par lease (messages du locataire non lus)
    const unreadByLease: Record<string, number> = {};
    for (const msg of messagesData || []) {
        if (msg.sender_type === 'tenant' && !msg.read_at) {
            unreadByLease[msg.lease_id] = (unreadByLease[msg.lease_id] || 0) + 1;
        }
    }

    // 4. Filtrer pour n'afficher que les conversations actives (avec messages)
    const activeConversations = allLeases?.filter(lease => leaseIdsWithMessages.has(lease.id)) || [];

    // 5. Locataires sans conversation (pour le dropdown)
    const tenantsWithoutConversation = allLeases?.filter(lease => !leaseIdsWithMessages.has(lease.id)) || [];

    return (
        <MessagesPageClient
            activeConversations={activeConversations as any}
            tenantsWithoutConversation={tenantsWithoutConversation as any}
            unreadByLease={unreadByLease}
        />
    );
}

