import { createClient } from "@/utils/supabase/server";
import { NextResponse } from 'next/server';

export async function GET(request: Request) {
    const { searchParams } = new URL(request.url);
    const query = searchParams.get('q');

    if (!query || query.length < 2) {
        return NextResponse.json([]);
    }

    const supabase = await createClient();
    const { data: { user } } = await supabase.auth.getUser();

    const results = [];

    // 1. Search Tenants (Authenticated only)
    if (user) {
        // Get team memberships to allow searching across team leases
        const { data: teamMemberships } = await supabase
            .from("team_members")
            .select("team_id")
            .eq("user_id", user.id);

        const userTeamIds = teamMemberships?.map(tm => tm.team_id) || [];

        let leasesQuery = supabase
            .from('leases')
            .select('id, tenant_name, property_address, status')
            .ilike('tenant_name', `%${query}%`)
            .limit(5);

        // Apply ownership/team filter logic similar to getLeasesByOwner
        if (userTeamIds.length > 0) {
            leasesQuery = leasesQuery.or(`owner_id.eq.${user.id},team_id.in.(${userTeamIds.join(',')})`);
        } else {
            leasesQuery = leasesQuery.eq("owner_id", user.id);
        }

        // We might want to include terminated leases? 
        // Usually search should find everything.

        const { data: tenants } = await leasesQuery;

        if (tenants) {
            results.push(...tenants.map(t => ({
                id: t.id,
                label: t.tenant_name,
                subLabel: t.property_address,
                type: 'tenant',
                url: `/gestion?q=${encodeURIComponent(t.tenant_name)}&tab=tenants`
            })));
        }
    }

    // 2. Search Properties (Public)
    const { data: properties } = await supabase
        .from('properties')
        .select('id, title, location')
        .eq('validation_status', 'approved')
        .eq('status', 'disponible')
        .or(`title.ilike.%${query}%,location->>city.ilike.%${query}%`)
        .limit(5);

    if (properties) {
        results.push(...properties.map(p => ({
            id: p.id,
            label: p.title || (p.location as any)?.city || "Bien",
            subLabel: (p.location as any)?.city,
            type: 'property' as const,
            url: `/recherche?q=${encodeURIComponent(p.title || "")}`
        })));
    }

    // 3. Search External Listings (Public) - Critical for Dakar results
    const { data: externalProperties } = await supabase
        .from('external_listings')
        .select('id, title, city')
        .or(`title.ilike.%${query}%,city.ilike.%${query}%`)
        .limit(5);

    if (externalProperties) {
        results.push(...externalProperties.map(p => ({
            id: p.id,
            label: p.title,
            subLabel: p.city,
            type: 'property' as const,
            url: `/recherche?q=${encodeURIComponent(p.title || p.city || "")}`
        })));
    }

    return NextResponse.json(results);
}

import { NextResponse } from 'next/server';
import { stripe } from '@/lib/subscription/stripe';
import { getUserTeamContext } from '@/lib/team-context';
import { createClient } from '@/utils/supabase/server';
import { CONNECT_CONFIG } from '@/lib/config/stripe-config';

export async function POST(req: Request) {
    try {
        const { teamId, team } = await getUserTeamContext();

        // 1. Strict Security Checks (Audit Requirement)
        if (!team.stripe_account_id) {
            return NextResponse.json({ error: "Agence non connect√©e Stripe Connect." }, { status: 400 });
        }
        if (!team.stripe_charges_enabled) {
            return NextResponse.json({ error: "Compte agence invalide (KYC incomplet)." }, { status: 400 });
        }
        if (!team.stripe_payouts_enabled) {
            return NextResponse.json({ error: "Compte bancaire agence manquant." }, { status: 400 });
        }

        const body = await req.json();
        const { amount, currency = CONNECT_CONFIG.DEFAULT_CURRENCY, rentId, customerId } = body;

        // 2. Amount Validation
        if (!amount || amount <= 0) {
            return NextResponse.json({ error: "Montant invalide." }, { status: 400 });
        }

        // 3. Commission Calculation (Diaspora Model)
        // Formula: Amount * 3.5% + 0.50‚Ç¨
        // Note: Amount is in cents. Fixed fee must be converted to cents (0.50 * 100 = 50)
        const fixedFeeCents = Math.round(CONNECT_CONFIG.DIASPORA_FIXED_FEE_EUR * 100);
        const commissionAmount = Math.round((amount * CONNECT_CONFIG.DIASPORA_COMMISSION_PERCENT) + fixedFeeCents);

        // Safety check: Commission cannot exceed total amount
        if (commissionAmount >= amount) {
            console.error(`Commission Error: Amount ${amount}, Comm ${commissionAmount}`);
            return NextResponse.json({ error: "Configuration erreur: Commission sup√©rieure au montant." }, { status: 500 });
        }

        // 4. Create PaymentIntent (Diaspora Flow: User pays Platform, Platform splits to Agency)
        const paymentIntent = await stripe.paymentIntents.create({
            amount: amount,
            currency: currency,
            customer: customerId, // Optional: if logged in user has a stripe customer ID
            automatic_payment_methods: { enabled: true },

            application_fee_amount: commissionAmount, // Platform Commission (Covers Stripe Fees + Profit)

            transfer_data: {
                destination: team.stripe_account_id, // Agency receives the rest
            },

            metadata: {
                team_id: teamId,
                rent_id: rentId,
                integration_type: 'connect_hybrid_v1'
            }
        });

        return NextResponse.json({
            clientSecret: paymentIntent.client_secret,
            paymentIntentId: paymentIntent.id
        });

    } catch (error) {
        console.error('PaymentIntent Error:', error);
        return NextResponse.json({ error: error instanceof Error ? error.message : 'Unknown payment error' }, { status: 500 });
    }
}

import { headers } from 'next/headers';
import { NextResponse } from 'next/server';
import { stripe } from '@/lib/subscription/stripe';
import Stripe from 'stripe';

/**
 * Helper: Resolve team_id from subscription metadata or fallback to DB lookup
 */
async function resolveTeamId(
    adminClient: any,
    metadata: Record<string, string> | null | undefined,
    subscriptionId: string | null
): Promise<string | null> {
    const teamId = metadata?.team_id;
    if (teamId) return teamId;

    // Fallback: lookup by stripe_subscription_id
    if (subscriptionId) {
        const { data: team } = await adminClient
            .from('teams')
            .select('id')
            .eq('stripe_subscription_id', subscriptionId)
            .single();
        return team?.id ?? null;
    }

    return null;
}

export async function POST(req: Request) {
    const body = await req.text();
    const signature = (await headers()).get('Stripe-Signature') as string;

    let event: Stripe.Event;

    try {
        if (!process.env.STRIPE_WEBHOOK_SECRET) {
            throw new Error('STRIPE_WEBHOOK_SECRET is not defined');
        }
        event = stripe.webhooks.constructEvent(
            body,
            signature,
            process.env.STRIPE_WEBHOOK_SECRET
        );
    } catch (err: any) {
        console.error(`Webhook Signature Error: ${err.message}`);
        return NextResponse.json({ error: `Webhook Error: ${err.message}` }, { status: 400 });
    }

    // Use admin client to bypass RLS for background updates
    const { createAdminClient } = await import('@/utils/supabase/admin');
    const adminClient = createAdminClient();

    try {
        // ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
        // IDEMPOTENCY CHECK - skip already-processed events
        // ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
        const { data: existingEvent } = await adminClient
            .from('stripe_events_log')
            .select('id')
            .eq('stripe_event_id', event.id)
            .single();

        if (existingEvent) {
            console.log(`‚è≠Ô∏è Event already processed: ${event.id}`);
            return NextResponse.json({ received: true });
        }

        // Log event for idempotency (UNIQUE constraint on stripe_event_id)
        await adminClient
            .from('stripe_events_log')
            .insert({
                stripe_event_id: event.id,
                type: event.type,
            });

        switch (event.type) {
            case 'checkout.session.completed': {
                const session = event.data.object as Stripe.Checkout.Session;

                // ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
                // RENT PAYMENT HANDLING
                // ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
                if (session.metadata?.type === 'rent_payment') {
                    const leaseId = session.metadata.lease_id;
                    const periodMonth = parseInt(session.metadata.period_month);
                    const periodYear = parseInt(session.metadata.period_year);
                    const amountFcfa = parseInt(session.metadata.amount_fcfa);

                    // Check if transaction already exists for this period
                    const { data: existingTx } = await adminClient
                        .from('rental_transactions')
                        .select('id')
                        .eq('lease_id', leaseId)
                        .eq('period_month', periodMonth)
                        .eq('period_year', periodYear)
                        .single();

                    // Build rich traceability metadata
                    const amountEurCents = session.amount_total || 0;
                    const traceMeta = {
                        provider: 'stripe',
                        stripe_session_id: session.id,
                        stripe_payment_intent_id: typeof session.payment_intent === 'string'
                            ? session.payment_intent : null,
                        amount_eur_cents: amountEurCents,
                        amount_eur: amountEurCents / 100,
                        amount_fcfa: amountFcfa,
                        exchange_rate: amountFcfa > 0 && amountEurCents > 0
                            ? Math.round(amountFcfa / (amountEurCents / 100))
                            : 656,
                        currency: 'eur',
                        paid_at: new Date().toISOString(),
                    };

                    if (existingTx) {
                        // Update existing transaction
                        await adminClient
                            .from('rental_transactions')
                            .update({
                                status: 'paid',
                                paid_at: new Date().toISOString(),
                                amount_paid: amountFcfa,
                                payment_method: 'stripe',
                                payment_ref: session.id,
                                meta: traceMeta,
                            })
                            .eq('id', existingTx.id);

                        console.log(`‚úÖ Rent payment updated: ${leaseId} - ${periodMonth}/${periodYear}`);
                    } else {
                        // Create new transaction record
                        await adminClient
                            .from('rental_transactions')
                            .insert({
                                lease_id: leaseId,
                                period_month: periodMonth,
                                period_year: periodYear,
                                amount_due: amountFcfa,
                                amount_paid: amountFcfa,
                                status: 'paid',
                                paid_at: new Date().toISOString(),
                                payment_method: 'stripe',
                                payment_ref: session.id,
                                meta: traceMeta,
                            });

                        console.log(`‚úÖ Rent payment created: ${leaseId} - ${periodMonth}/${periodYear}`);
                    }

                    // Log for audit trail
                    await adminClient
                        .from('tenant_access_logs')
                        .insert({
                            lease_id: leaseId,
                            action: 'payment_completed',
                            details: {
                                amount: amountFcfa,
                                period: `${periodMonth}/${periodYear}`,
                                method: 'stripe',
                                stripe_session_id: session.id,
                            },
                        });

                    break;
                }

                // ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
                // TEAM SUBSCRIPTION HANDLING
                // ‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê
                const teamId = session.metadata?.team_id;
                const planId = session.metadata?.plan_id;
                const subscriptionId = session.subscription as string;

                if (teamId && planId) {
                    await adminClient
                        .from('teams')
                        .update({
                            subscription_tier: planId,
                            subscription_status: 'active',
                            stripe_subscription_id: subscriptionId,
                            subscription_trial_ends_at: null,
                        })
                        .eq('id', teamId);

                    console.log(`‚úÖ Subscription activated for team ${teamId}`);
                }
                break;
            }

            case 'customer.subscription.deleted': {
                const subscription = event.data.object as any;
                const teamId = await resolveTeamId(adminClient, subscription.metadata, subscription.id);

                if (teamId) {
                    await adminClient
                        .from('teams')
                        .update({
                            subscription_status: 'canceled',
                            stripe_subscription_id: null,
                        })
                        .eq('id', teamId);

                    console.log(`‚ùå Subscription canceled for team ${teamId}`);
                } else {
                    console.warn(`‚ö†Ô∏è customer.subscription.deleted: no team found for subscription ${subscription.id}`);
                }
                break;
            }

            case 'invoice.payment_failed': {
                const invoice = event.data.object as any;
                const subscriptionId = invoice.subscription as string;

                if (subscriptionId) {
                    await adminClient
                        .from('teams')
                        .update({
                            subscription_status: 'past_due',
                        })
                        .eq('stripe_subscription_id', subscriptionId);

                    console.log(`‚ö†Ô∏è Payment failed for subscription ${subscriptionId}`);
                }
                break;
            }

            case 'customer.subscription.updated': {
                const subscription = event.data.object as any;
                const teamId = await resolveTeamId(adminClient, subscription.metadata, subscription.id);
                const status = subscription.status;

                if (teamId) {
                    await adminClient
                        .from('teams')
                        .update({
                            subscription_status: status,
                            subscription_tier: subscription.metadata?.plan_id,
                            subscription_current_period_end: new Date(subscription.current_period_end * 1000).toISOString(),
                            subscription_currency: subscription.currency ? subscription.currency.toLowerCase() : null,
                        })
                        .eq('id', teamId);

                    console.log(`üîÑ Subscription updated for team ${teamId}: ${status}`);
                } else {
                    console.warn(`‚ö†Ô∏è customer.subscription.updated: no team found for subscription ${subscription.id}`);
                }
                break;
            }

            case 'invoice.payment_succeeded':
            case 'invoice.paid': {
                const invoice = event.data.object as any;
                const subscriptionId = invoice.subscription as string;

                if (subscriptionId) {
                    await adminClient
                        .from('teams')
                        .update({
                            subscription_status: 'active',
                        })
                        .eq('stripe_subscription_id', subscriptionId);

                    console.log(`‚úÖ Payment succeeded for subscription ${subscriptionId}`);
                }
                break;
            }
        }

        return NextResponse.json({ received: true });
    } catch (error: any) {
        console.error('Webhook processing failed:', error);
        return NextResponse.json({ error: 'Internal Server Error' }, { status: 500 });
    }
}

