import { NextRequest, NextResponse } from 'next/server';
import ReactPDF from '@react-pdf/renderer';
import { createQuittanceDocument } from '@/components/pdf/QuittancePDF_v2';
import { createAdminClient } from '@/utils/supabase/admin';
import { getTenantSessionFromCookie } from '@/lib/tenant-magic-link';

export async function GET(
    request: NextRequest,
    { params }: { params: Promise<{ id: string }> }
) {
    try {
        // 1. V√©rifier la session tenant via cookie
        const session = await getTenantSessionFromCookie();
        if (!session) {
            return NextResponse.json({ error: 'Session expir√©e' }, { status: 401 });
        }

        const { id: transactionId } = await params;
        const adminClient = createAdminClient();

        // 2. R√©cup√©rer la transaction (v√©rifier qu'elle appartient au bail du tenant)
        const { data: transaction, error } = await adminClient
            .from('rental_transactions')
            .select(`
                *,
                leases (
                    id,
                    tenant_name,
                    tenant_email,
                    tenant_phone,
                    property_address,
                    owner_id,
                    profiles:owner_id (
                        full_name,
                        company_name,
                        company_address,
                        company_ninea,
                        logo_url,
                        signature_url
                    )
                )
            `)
            .eq('id', transactionId)
            .single();

        if (error || !transaction) {
            return NextResponse.json({ error: 'Quittance introuvable' }, { status: 404 });
        }

        // 3. S√©curit√© : v√©rifier que la transaction appartient au bail du tenant
        if (transaction.lease_id !== session.lease_id) {
            return NextResponse.json({ error: 'Acc√®s interdit' }, { status: 403 });
        }

        const lease = transaction.leases as any;
        const ownerProfile = lease.profiles;

        // 4. Pr√©parer les donn√©es pour le PDF
        const isGuarantee = transaction.period_month === 0;
        const periodDate = isGuarantee
            ? new Date()
            : new Date(transaction.period_year, transaction.period_month - 1);
        const periodMonthStr = isGuarantee
            ? 'Garantie'
            : periodDate.toLocaleString('fr-FR', { month: 'long', year: 'numeric' });

        const firstDay = isGuarantee
            ? new Date()
            : new Date(transaction.period_year, transaction.period_month - 1, 1);
        const lastDay = isGuarantee
            ? new Date()
            : new Date(transaction.period_year, transaction.period_month, 0);

        const pdfData = {
            tenantName: lease.tenant_name,
            tenantEmail: lease.tenant_email || '',
            tenantPhone: lease.tenant_phone || '',
            tenantAddress: lease.property_address,
            amount: transaction.amount_paid || transaction.amount_due,
            periodMonth: periodMonthStr,
            periodStart: firstDay.toLocaleDateString('fr-FR'),
            periodEnd: lastDay.toLocaleDateString('fr-FR'),
            receiptNumber: `QUITT-${transaction.id.substring(0, 8).toUpperCase()}`,
            ownerName: ownerProfile?.company_name || ownerProfile?.full_name || 'Propri√©taire',
            ownerAddress: ownerProfile?.company_address || '',
            ownerNinea: ownerProfile?.company_ninea || '',
            ownerLogo: ownerProfile?.logo_url || undefined,
            ownerSignature: ownerProfile?.signature_url || undefined,
            propertyAddress: lease.property_address,
            isGuarantee,
        };

        // 5. G√©n√©rer le PDF
        const pdfDocument = createQuittanceDocument(pdfData);
        const stream = await ReactPDF.renderToStream(pdfDocument);

        const chunks: Uint8Array[] = [];
        for await (const chunk of stream) {
            chunks.push(chunk as Uint8Array);
        }
        const pdfBuffer = Buffer.concat(chunks);

        // 6. Retourner le PDF
        return new NextResponse(pdfBuffer, {
            headers: {
                'Content-Type': 'application/pdf',
                'Content-Disposition': `inline; filename="Quittance_${pdfData.receiptNumber}.pdf"`,
            },
        });

    } catch (error) {
        console.error('Erreur g√©n√©ration PDF quittance tenant:', error);
        return NextResponse.json(
            { error: 'Erreur lors de la g√©n√©ration du PDF' },
            { status: 500 }
        );
    }
}

import { headers } from 'next/headers';
import { NextResponse } from 'next/server';
import { stripe } from '@/lib/subscription/stripe';
import { createClient } from '@/utils/supabase/server';
import Stripe from 'stripe';

// Using the generic Stripe Webhook Secret for Connect events
// Ensure this is configured in .env.local
const webhookSecret = process.env.STRIPE_CONNECT_WEBHOOK_SECRET;

export async function POST(req: Request) {
    if (!webhookSecret) {
        console.error('Missing STRIPE_CONNECT_WEBHOOK_SECRET');
        return NextResponse.json({ error: 'Config error' }, { status: 500 });
    }

    const body = await req.text();
    const headersList = await headers();
    const signature = headersList.get('stripe-signature');

    let event: Stripe.Event;

    try {
        if (!signature) throw new Error('No signature');
        event = stripe.webhooks.constructEvent(body, signature, webhookSecret);
    } catch (_err) {
        console.error(`Webhook signature verification failed`);
        return NextResponse.json({ error: 'Webhook Error' }, { status: 400 });
    }

    const supabase = await createClient();

    // 1. LOGGING (Idempotent)
    try {
        const { error: logError } = await supabase
            .from('stripe_events_log')
            .insert({
                type: event.type,
                stripe_event_id: event.id,
                payload: event as any // Cast to JSON
            })
            // Ignore if duplicate (handled by UNIQUE constraint)
            .select()
            .single();

        if (logError && logError.code !== '23505') { // 23505 = unique_violation
            console.error('Failed to log webhook:', logError);
        } else if (logError?.code === '23505') {
            console.log(`Event ${event.id} already processed.`);
            return NextResponse.json({ received: true });
        }
    } catch (logEx) {
        console.error('Logging exception:', logEx);
    }

    // 2. EVENT PROCESSING
    try {
        switch (event.type) {
            case 'account.updated': {
                const account = event.data.object as Stripe.Account;
                const teamId = account.metadata?.team_id;

                if (teamId) {
                    await supabase.from('teams').update({
                        stripe_charges_enabled: account.charges_enabled,
                        stripe_payouts_enabled: account.payouts_enabled,
                        stripe_details_submitted: account.details_submitted,
                        stripe_account_status: (account.requirements?.disabled_reason) ? 'restricted' : (account.charges_enabled ? 'active' : 'pending'),
                        // Capture simplified status
                    }).eq('id', teamId);
                }
                break;
            }
            case 'account.application.deauthorized': {
                const accountId = (event.data.object as any).id;
                // Agency disconnected
                await supabase.from('teams').update({
                    stripe_account_id: null,
                    stripe_charges_enabled: false,
                    stripe_payouts_enabled: false,
                    stripe_account_status: 'disconnected'
                }).eq('stripe_account_id', accountId);
                break;
            }
            // Add other handlers (payment_intent.succeeded) here as needed for transaction tracking
            // Use 'transactions' table for that.
            default:
                // Just log (already done above)
                break;
        }
    } catch (error) {
        console.error('Webhook processing error:', error);
        return NextResponse.json({ error: 'Processing error' }, { status: 500 });
    }

    return NextResponse.json({ received: true });
}

