import { MetadataRoute } from 'next';
import { supabase } from '@/lib/supabase';
import { slugify } from '@/lib/slugs';

const BASE_URL = process.env.NEXT_PUBLIC_APP_URL || 'https://www.dousell-immo.com';

export const revalidate = 3600; // Update sitemap every hour

export default async function sitemap(): Promise<MetadataRoute.Sitemap> {

    // 1. R√©cup√©rer les donn√©es LOCATION
    const { data: rentals } = await supabase
        .rpc('get_active_cities_and_types', { min_count: 1, target_transaction_type: 'location' });

    // 2. R√©cup√©rer les donn√©es VENTE
    const { data: sales } = await supabase
        .rpc('get_active_cities_and_types', { min_count: 1, target_transaction_type: 'vente' });

    const routes: MetadataRoute.Sitemap = [];

    // Helper pour ajouter des routes
    const addRoutes = (items: any[], mode: 'location' | 'vente') => {
        const processedCities = new Set<string>();

        // Root de la section
        routes.push({
            url: `${BASE_URL}/${mode}`,
            lastModified: new Date(),
            changeFrequency: 'daily',
            priority: 1.0,
        });

        (items || []).forEach((page: { city: string; type: string }) => {
            const citySlug = slugify(page.city);
            const typeSlug = slugify(page.type);

            // Page Ville/Type (Feuille)
            routes.push({
                url: `${BASE_URL}/${mode}/${citySlug}/${typeSlug}`,
                lastModified: new Date(),
                changeFrequency: 'daily',
                priority: 0.8,
            });

            // Collecter les villes uniques
            processedCities.add(citySlug);
        });

        // Pages Ville (Parents)
        processedCities.forEach(citySlug => {
            routes.push({
                url: `${BASE_URL}/${mode}/${citySlug}`,
                lastModified: new Date(),
                changeFrequency: 'daily',
                priority: 0.9,
            });
        });
    }

    addRoutes(rentals, 'location');
    addRoutes(sales, 'vente');

    // 3. Les pages statiques de base
    const staticRoutes = [
        '',
        '/recherche',
        '/a-propos',
        '/contact',
        '/planifier-visite',
    ].map((route) => ({
        url: `${BASE_URL}${route}`,
        lastModified: new Date(),
        changeFrequency: 'monthly' as const,
        priority: 1.0,
    }));

    return [...staticRoutes, ...routes];
}

import React from 'react';

interface KPICardProps {
    label: string;
    value: number;
    currency?: string;
    unit?: string;
    trend?: 'up' | 'down' | 'neutral';
    color?: 'gold' | 'green' | 'red' | 'blue';
    small?: boolean;
    alert?: {
        type: 'warning' | 'danger' | 'info';
        icon?: string;
        message?: string;
    };
}

export function KPICard({
    label,
    value,
    currency = '',
    unit = '',
    trend,
    color = 'gold',
    small = false,
    alert,
}: KPICardProps) {
    const bgColor = {
        gold: 'from-amber-500/20 to-amber-500/5', // Adaptation gold -> amber pour Tailwind standard si gold pas d√©fini
        green: 'from-green-500/20 to-green-500/5',
        red: 'from-red-500/20 to-red-500/5',
        blue: 'from-blue-500/20 to-blue-500/5',
    }[color];

    const textColor = {
        gold: 'text-amber-400',
        green: 'text-green-400',
        red: 'text-red-400',
        blue: 'text-blue-400',
    }[color];

    const borderColor = {
        gold: 'border-amber-500/30',
        green: 'border-green-500/30',
        red: 'border-red-500/30',
        blue: 'border-blue-500/30',
    }[color];

    const formattedValue = new Intl.NumberFormat('fr-FR', {
        style: currency ? 'currency' : 'decimal',
        currency: currency || undefined,
        maximumFractionDigits: 0,
    }).format(value);

    return (
        <div
            className={`bg-gradient-to-br ${bgColor} border ${borderColor} rounded-xl p-4 transition-all hover:shadow-lg hover:shadow-amber-500/10`}
        >
            <div className="flex items-start justify-between">
                <div className="space-y-1">
                    <p className="text-gray-400 text-xs font-medium uppercase tracking-wider">{label}</p>
                    <p className={`text-2xl font-bold ${textColor} tracking-tight`}>
                        {formattedValue}
                        {unit && <span className="text-sm ml-1 font-normal opacity-70">{unit}</span>}
                    </p>
                </div>
                {trend && (
                    <div
                        className={`text-lg p-2 rounded-lg bg-gray-900/50 ${trend === 'up' ? 'text-green-400' : trend === 'down' ? 'text-red-400' : 'text-gray-400'
                            }`}
                    >
                        {trend === 'up' ? 'üìà' : trend === 'down' ? 'üìâ' : '‚û°Ô∏è'}
                    </div>
                )}
            </div>

            {alert && (
                <div
                    className={`mt-4 p-2.5 rounded-lg text-[11px] leading-relaxed ${alert.type === 'warning'
                            ? 'bg-yellow-500/10 text-yellow-300 border border-yellow-500/20'
                            : alert.type === 'danger'
                                ? 'bg-red-500/10 text-red-300 border border-red-500/20'
                                : 'bg-blue-500/10 text-blue-300 border border-blue-500/20'
                        }`}
                >
                    <div className="flex gap-2">
                        {alert.icon && <span className="shrink-0">{alert.icon}</span>}
                        <span>{alert.message}</span>
                    </div>
                </div>
            )}
        </div>
    );
}

