  disponible: "bg-emerald-500/20 text-emerald-300 border-emerald-500/30",
  "sous-offre": "bg-amber-500/20 text-amber-300 border-amber-500/30",
  vendu: "bg-red-500/20 text-red-300 border-red-500/30",
};

