"use client";

import { useEffect, useState, useTransition } from "react";
import { CheckCircle2, XCircle, FileText, ExternalLink, Loader2 } from "lucide-react";
import { toast } from "sonner";
import Image from "next/image";
import _Link from "next/link";

import { Button } from "@/components/ui/button";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Badge } from "@/components/ui/badge";
import { VerificationStatusBadge } from "@/components/dashboard/verification-status-badge";
import { getPendingVerifications, reviewVerification } from "@/app/_actions/admin-verification";
import type { VerificationRequest } from "@/app/_actions/admin-verification";

export function VerificationQueue() {
  const [verifications, setVerifications] = useState<VerificationRequest[]>([]);
  const [loading, setLoading] = useState(true);
  const [selectedVerification, setSelectedVerification] = useState<VerificationRequest | null>(null);
  const [reviewDialogOpen, setReviewDialogOpen] = useState(false);
  const [reviewAction, setReviewAction] = useState<"approve" | "reject" | null>(null);
  const [isPending, startTransition] = useTransition();

  const loadVerifications = async () => {
    console.log("üîç [VerificationQueue] D√©but chargement des v√©rifications...");
    setLoading(true);
    try {
      const result = await getPendingVerifications();
      console.log("üîç [VerificationQueue] R√©sultat re√ßu:", result);
      console.log("üîç [VerificationQueue] Success:", result.success);
      console.log("üîç [VerificationQueue] Data length:", result.data?.length);
      console.log("üîç [VerificationQueue] Error:", result.error);

      if (result.success && result.data) {
        console.log("‚úÖ [VerificationQueue] Donn√©es charg√©es:", result.data.length, "v√©rifications");
        setVerifications(result.data);
      } else {
        console.error("‚ùå [VerificationQueue] Erreur:", result.error);
        toast.error("Erreur", {
          description: result.error || "Impossible de charger les demandes.",
        });
      }
    } catch (error) {
      console.error("‚ùå [VerificationQueue] Exception:", error);
      toast.error("Erreur", {
        description: "Une erreur inattendue s'est produite.",
      });
    } finally {
      setLoading(false);
      console.log("üîç [VerificationQueue] Chargement termin√©");
    }
  };

  useEffect(() => {
    loadVerifications();
  }, []);

  const handleReview = (verification: VerificationRequest, action: "approve" | "reject") => {
    setSelectedVerification(verification);
    setReviewAction(action);
    setReviewDialogOpen(true);
  };

  const confirmReview = () => {
    if (!selectedVerification || !reviewAction) return;

    startTransition(async () => {
      const result = await reviewVerification(
        selectedVerification.propertyId,
        reviewAction === "approve" ? "verified" : "rejected"
      );

      if (result.success) {
        toast.success(
          reviewAction === "approve" ? "V√©rification approuv√©e" : "V√©rification refus√©e"
        );
        setReviewDialogOpen(false);
        setSelectedVerification(null);
        setReviewAction(null);
        await loadVerifications();
      } else {
        toast.error("Erreur", {
          description: result.error || "Une erreur est survenue.",
        });
      }
    });
  };

  const formatPrice = (price: number) => {
    return new Intl.NumberFormat("fr-FR", {
      maximumFractionDigits: 0,
    }).format(price);
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString("fr-FR", {
      day: "numeric",
      month: "short",
      year: "numeric",
      hour: "2-digit",
      minute: "2-digit",
    });
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center py-12">
        <Loader2 className="h-6 w-6 animate-spin text-white/60" />
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-semibold text-foreground">V√©rifications en attente</h2>
          <p className="mt-1 text-sm text-muted-foreground">
            {verifications.length} demande{verifications.length > 1 ? "s" : ""} √† traiter
          </p>
        </div>
        {verifications.length > 0 && (
          <Badge className="bg-amber-500/20 text-amber-600 dark:text-amber-300 border-amber-500/30">
            {verifications.length} en attente
          </Badge>
        )}
      </div>

      {verifications.length === 0 ? (
        <div className="rounded-2xl border border-border bg-card p-12 text-center">
          <CheckCircle2 className="mx-auto h-12 w-12 text-muted-foreground mb-4" />
          <p className="text-lg text-foreground">Aucune v√©rification en attente</p>
          <p className="mt-2 text-sm text-muted-foreground">
            Toutes les demandes ont √©t√© trait√©es.
          </p>
        </div>
      ) : (
        <div className="rounded-2xl border border-border bg-card overflow-hidden">
          <Table>
            <TableHeader>
              <TableRow className="border-border hover:bg-muted/50">
                <TableHead className="text-muted-foreground">Bien</TableHead>
                <TableHead className="text-muted-foreground">Propri√©taire</TableHead>
                <TableHead className="text-muted-foreground">Document</TableHead>
                <TableHead className="text-muted-foreground">Date</TableHead>
                <TableHead className="text-muted-foreground text-right">Actions</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {verifications.map((verification) => (
                <TableRow key={verification.id} className="border-border hover:bg-muted/50">
                  <TableCell>
                    <div className="flex items-center gap-3">
                      {verification.propertyImages && verification.propertyImages[0] && (
                        <div className="relative h-12 w-12 rounded-lg overflow-hidden flex-shrink-0">
                          <Image
                            src={verification.propertyImages[0]}
                            alt={verification.propertyTitle}
                            fill
                            className="object-cover"
                            sizes="48px"
                          />
                        </div>
                      )}
                      <div>
                        <p className="font-medium text-foreground">{verification.propertyTitle}</p>
                        <p className="text-sm text-muted-foreground">
                          {formatPrice(verification.propertyPrice)} FCFA
                        </p>
                      </div>
                    </div>
                  </TableCell>
                  <TableCell>
                    <div>
                      <p className="text-foreground">{verification.ownerName}</p>
                      <p className="text-sm text-muted-foreground">{verification.ownerEmail}</p>
                    </div>
                  </TableCell>
                  <TableCell>
                    {verification.proofDocumentUrl ? (
                      <a
                        href={verification.proofDocumentUrl}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="inline-flex items-center gap-2 text-primary hover:underline text-sm"
                      >
                        <FileText className="h-4 w-4" />
                        Voir le document
                        <ExternalLink className="h-3 w-3" />
                      </a>
                    ) : (
                      <span className="text-muted-foreground text-sm">Aucun document</span>
                    )}
                  </TableCell>
                  <TableCell>
                    <span className="text-muted-foreground text-sm">
                      {formatDate(verification.verificationRequestedAt)}
                    </span>
                  </TableCell>
                  <TableCell className="text-right">
                    <div className="flex items-center justify-end gap-2">
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => handleReview(verification, "approve")}
                        className="text-emerald-600 dark:text-emerald-400 border-emerald-500/30 hover:bg-emerald-500/10"
                      >
                        <CheckCircle2 className="h-4 w-4 mr-1" />
                        Approuver
                      </Button>
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => handleReview(verification, "reject")}
                        className="text-red-600 dark:text-red-400 border-red-500/30 hover:bg-red-500/10"
                      >
                        <XCircle className="h-4 w-4 mr-1" />
                        Refuser
                      </Button>
                    </div>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </div>
      )}

      {/* Dialog de confirmation */}
      <Dialog open={reviewDialogOpen} onOpenChange={setReviewDialogOpen}>
        <DialogContent className="border-border bg-background text-foreground sm:max-w-md">
          <DialogHeader>
            <DialogTitle>
              {reviewAction === "approve" ? "Approuver la v√©rification" : "Refuser la v√©rification"}
            </DialogTitle>
            <DialogDescription className="text-muted-foreground">
              {reviewAction === "approve" ? (
                <>
                  Confirmez-vous l&apos;approbation de la v√©rification pour{" "}
                  <strong>{selectedVerification?.propertyTitle}</strong> ?
                </>
              ) : (
                <>
                  Confirmez-vous le refus de la v√©rification pour{" "}
                  <strong>{selectedVerification?.propertyTitle}</strong> ?
                </>
              )}
            </DialogDescription>
          </DialogHeader>
          <DialogFooter>
            <Button
              variant="outline"
              onClick={() => {
                setReviewDialogOpen(false);
                setSelectedVerification(null);
                setReviewAction(null);
              }}
              disabled={isPending}
            >
              Annuler
            </Button>
            <Button
              variant={reviewAction === "approve" ? "default" : "outline"}
              onClick={confirmReview}
              disabled={isPending}
              className={reviewAction === "reject" ? "border-red-500/30 text-red-500 hover:bg-red-500/10" : ""}
            >
              {isPending ? (
                <>
                  <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                  Traitement...
                </>
              ) : reviewAction === "approve" ? (
                "Approuver"
              ) : (
                "Refuser"
              )}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
}

"use client";

import { useState } from "react";
import { toast } from "sonner";
import { Upload, X, FileText, Loader2, ShieldCheck } from "lucide-react";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { uploadVerificationDoc } from "@/app/(workspace)/compte/mes-biens/actions";

type VerificationUploadFormProps = {
  propertyId: string;
  variant?: "default" | "outline" | "ghost";
  currentStatus?: string;
  proofDocumentUrl?: string | null;
  onSuccess?: () => void;
};

export function VerificationUploadForm({
  propertyId,
  variant = "outline",
  currentStatus,
  proofDocumentUrl,
  onSuccess,
}: VerificationUploadFormProps) {
  const [isOpen, setIsOpen] = useState(false);
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [isUploading, setIsUploading] = useState(false);
  const [isDragOver, setIsDragOver] = useState(false);

  // Si d√©j√† v√©rifi√©, ne rien afficher (apr√®s les hooks pour respecter les r√®gles React)
  if (currentStatus === 'verified') {
    return null;
  }

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(true);
  };

  const handleDragLeave = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(false);
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(false);

    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      validateAndSetFile(e.dataTransfer.files[0]);
    }
  };

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files[0]) {
      validateAndSetFile(e.target.files[0]);
    }
  };

  const validateAndSetFile = (file: File) => {
    const allowedTypes = ["application/pdf", "image/jpeg", "image/png", "image/webp"];

    if (!allowedTypes.includes(file.type)) {
      toast.error("Format non support√©", {
        description: "Veuillez utiliser un fichier PDF, JPG ou PNG."
      });
      return;
    }

    if (file.size > 5 * 1024 * 1024) {
      toast.error("Fichier trop volumineux", {
        description: "La taille maximale est de 5Mo."
      });
      return;
    }

    setSelectedFile(file);
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!selectedFile) return;

    setIsUploading(true);
    const formData = new FormData();
    formData.append("file", selectedFile);
    formData.append("propertyId", propertyId);

    try {
      const result = await uploadVerificationDoc(formData);

      if (result.error) {
        toast.error("Erreur d'envoi", { description: result.error });
      } else {
        toast.success("Document envoy√© !", {
          description: "Votre demande de certification est en cours d'examen."
        });
        setIsOpen(false);
        setSelectedFile(null);
        onSuccess?.();
      }
    } catch (error) {
      console.error(error);
      toast.error("Une erreur inconnue est survenue.");
    } finally {
      setIsUploading(false);
    }
  };

  return (
    <Dialog open={isOpen} onOpenChange={setIsOpen}>
      <DialogTrigger asChild>
        <Button
          variant={variant}
          size="sm"
          className="h-8 border-[#F4C430]/50 text-[#F4C430] hover:bg-[#F4C430]/10 hover:text-[#F4C430]"
        >
          <ShieldCheck className="mr-2 h-4 w-4" />
          Certifier ce bien
        </Button>
      </DialogTrigger>
      <DialogContent className="sm:max-w-md bg-zinc-950 border-white/10 text-white">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2 text-[#F4C430]">
            <ShieldCheck className="h-5 w-5" />
            Certification du bien
          </DialogTitle>
          <DialogDescription className="text-white/60">
            T√©l√©chargez votre titre de propri√©t√© ou tout document officiel prouvant que vous √™tes le propri√©taire.
            Ce document restera confidentiel.
          </DialogDescription>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-6 mt-4">
          <div
            className={`relative flex flex-col items-center justify-center rounded-xl border-2 border-dashed transition-all p-8
              ${isDragOver
                ? "border-[#F4C430] bg-[#F4C430]/5"
                : "border-white/10 bg-white/5 hover:bg-white/10"
              }
              ${selectedFile ? "border-[#F4C430]/50" : ""}
            `}
            onDragOver={handleDragOver}
            onDragLeave={handleDragLeave}
            onDrop={handleDrop}
          >
            <input
              type="file"
              id="verification-file"
              accept=".pdf,.jpg,.jpeg,.png,.webp"
              className="absolute inset-0 cursor-pointer opacity-0"
              onChange={handleFileChange}
              disabled={isUploading}
            />

            {selectedFile ? (
              <div className="flex flex-col items-center gap-2 text-center">
                <div className="rounded-full bg-[#F4C430]/10 p-3">
                  <FileText className="h-8 w-8 text-[#F4C430]" />
                </div>
                <div>
                  <p className="font-medium text-white">{selectedFile.name}</p>
                  <p className="text-xs text-white/50">
                    {(selectedFile.size / 1024 / 1024).toFixed(2)} MB
                  </p>
                </div>
                <Button
                  type="button"
                  size="sm"
                  variant="ghost"
                  className="mt-2 h-8 text-red-400 hover:text-red-300 hover:bg-red-400/10"
                  onClick={(e) => {
                    e.stopPropagation(); // Prevent clicking input again
                    setSelectedFile(null);
                  }}
                >
                  <X className="mr-2 h-3.5 w-3.5" />
                  Changer de fichier
                </Button>
              </div>
            ) : (
              <div className="flex flex-col items-center gap-2 text-center">
                <div className="rounded-full bg-white/5 p-3">
                  <Upload className="h-6 w-6 text-white/40" />
                </div>
                <div>
                  <p className="font-medium text-white">
                    Glissez votre document ici
                  </p>
                  <p className="text-xs text-white/50 mt-1">
                    ou cliquez pour parcourir
                  </p>
                </div>
                <p className="text-[10px] text-white/30 uppercase tracking-widest mt-2">
                  PDF, JPG, PNG (Max 5MB)
                </p>
              </div>
            )}
          </div>

          <div className="flex justify-end gap-3">
            <Button
              type="button"
              variant="ghost"
              onClick={() => setIsOpen(false)}
              disabled={isUploading}
              className="text-white/60 hover:text-white"
            >
              Annuler
            </Button>
            <Button
              type="submit"
              disabled={!selectedFile || isUploading}
              className="bg-[#F4C430] text-black hover:bg-[#F4C430]/90 font-semibold"
            >
              {isUploading ? (
                <>
                  <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                  Envoi...
                </>
              ) : (
                "Envoyer pour certification"
              )}
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}

