    source: "manual" | "verification";
};

interface DocumentSelectorProps {
    onSelect: (document: { id: string; url: string; name: string; file_path?: string } | null) => void;
    selectedDocumentId?: string;
    documentType?: string;
    label?: string;
    description?: string;
