}

const documentTypes = [
    { value: "titre_propriete", label: "Titre de Propri√©t√©" },
    { value: "bail", label: "Bail" },
    { value: "cni", label: "CNI / Pi√®ce d'identit√©" },
    { value: "facture", label: "Facture" },
    { value: "attestation", label: "Attestation" },
    { value: "autre", label: "Autre document" },
];

export function DocumentSelector({
    onSelect,
    selectedDocumentId,
    documentType,
    label = "Document justificatif",
    description = "S√©lectionnez un document depuis votre coffre-fort ou uploadez-en un nouveau",
    className = "",
}: DocumentSelectorProps) {
    const [documents, setDocuments] = useState<Document[]>([]);
    const [loading, setLoading] = useState(true);
    const [selectedId, setSelectedId] = useState<string | undefined>(selectedDocumentId);
    const [isUploadDialogOpen, setIsUploadDialogOpen] = useState(false);
    const [uploading, setUploading] = useState(false);
    const [uploadForm, setUploadForm] = useState({
        type: documentType || "titre_propriete",
        file: null as File | null,
    });
    const [previewDoc, setPreviewDoc] = useState<Document | null>(null);
    const [isPreviewOpen, setIsPreviewOpen] = useState(false);

    useEffect(() => {
        loadDocuments();
    }, []);

    useEffect(() => {
        setSelectedId(selectedDocumentId);
    }, [selectedDocumentId]);

    const loadDocuments = async () => {
        setLoading(true);
        try {
            const result = await getMyDocuments();
            if (result.success && result.data) {
                const allDocs = result.data as Document[];
                // Filtrer seulement les documents manuels, et par type si sp√©cifi√©
                const filteredDocs = documentType
                    ? allDocs.filter((doc) => doc.source === "manual" && doc.type === documentType)
                    : allDocs.filter((doc) => doc.source === "manual");
                setDocuments(filteredDocs);
            } else {
                toast.error("Erreur lors du chargement des documents");
            }
        } catch (error) {
            console.error("Error loading documents:", error);
            toast.error("Impossible de charger les documents");
        } finally {
            setLoading(false);
        }
    };

    const handleSelectDocument = (docId: string) => {
        setSelectedId(docId);
        const doc = documents.find((d) => d.id === docId);
        if (doc) {
            onSelect({
                id: doc.id,
                url: doc.url,
                name: doc.name,
            });
        }
    };

    const handleDeselectDocument = () => {
        setSelectedId(undefined);
        onSelect(null);
    };

    const handleUpload = async () => {
        if (!uploadForm.file) {
            toast.error("Veuillez s√©lectionner un fichier");
            return;
        }

        setUploading(true);
        try {
            const formData = new FormData();
            formData.append("file", uploadForm.file);
            formData.append("type", uploadForm.type);

            const result = await uploadDocument(formData);

            if (result.success && result.data) {
                toast.success("Document upload√© avec succ√®s");
                setIsUploadDialogOpen(false);
                setUploadForm({ type: documentType || "titre_propriete", file: null });
                // Recharger la liste
                await loadDocuments();
                // Auto-s√©lectionner le nouveau document
                const newDoc = result.data as { id?: string };
                if (newDoc.id) {
                    handleSelectDocument(newDoc.id);
                }
            } else {
                toast.error(result.error || "Erreur lors de l'upload");
            }
        } catch (error) {
            console.error("Upload error:", error);
            toast.error("Erreur lors de l'upload du document");
        } finally {
            setUploading(false);
        }
    };

    const getFileIcon = (fileName: string) => {
        const ext = fileName.split(".").pop()?.toLowerCase();
        if (ext === "pdf") return <FileText className="h-5 w-5 text-red-500" />;
        if (["jpg", "jpeg", "png", "webp"].includes(ext || ""))
            return <ImageIcon className="h-5 w-5 text-blue-500" />;
        return <FileIcon className="h-5 w-5 text-gray-500" />;
    };

    const formatFileSize = (bytes: number) => {
        if (bytes < 1024) return bytes + " B";
        if (bytes < 1024 * 1024) return (bytes / 1024).toFixed(1) + " KB";
        return (bytes / (1024 * 1024)).toFixed(1) + " MB";
    };

    return (
        <div className={`space-y-4 ${className}`}>
            <div>
                <Label className="text-white">{label}</Label>
                {description && <p className="text-sm text-white/60 mt-1">{description}</p>}
            </div>

            {loading ? (
                <Card className="p-6 bg-white/5 border-white/10">
                    <div className="flex items-center justify-center gap-2 text-white/60">
                        <Loader2 className="h-4 w-4 animate-spin" />
                        <span>Chargement des documents...</span>
                    </div>
                </Card>
            ) : documents.length === 0 ? (
                <Card className="p-6 bg-white/5 border-white/10 text-center">
                    <FileText className="h-12 w-12 text-white/30 mx-auto mb-3" />
                    <p className="text-white/60 mb-4">Aucun document dans votre coffre-fort</p>
                    <Dialog open={isUploadDialogOpen} onOpenChange={setIsUploadDialogOpen}>
                        <DialogTrigger asChild>
                            <Button variant="outline" className="mx-auto">
                                <Upload className="h-4 w-4 mr-2" />
                                Uploader un document
                            </Button>
                        </DialogTrigger>
                        <DialogContent className="bg-[#0a0118] border-white/10">
                            <DialogHeader>
                                <DialogTitle className="text-white">Uploader un nouveau document</DialogTitle>
                                <DialogDescription className="text-white/60">
                                    Ajoutez un document √† votre coffre-fort (PDF, JPG, PNG, max 5 MB)
                                </DialogDescription>
                            </DialogHeader>
                            <div className="space-y-4 pt-4">
                                <div>
                                    <Label htmlFor="doc-type" className="text-white">
                                        Type de document
                                    </Label>
                                    <Select
                                        value={uploadForm.type}
                                        onValueChange={(value) => setUploadForm({ ...uploadForm, type: value })}
                                        disabled={!!documentType}
                                    >
                                        <SelectTrigger className="bg-white/5 border-white/10 text-white">
                                            <SelectValue />
                                        </SelectTrigger>
                                        <SelectContent>
                                            {documentTypes.map((type) => (
                                                <SelectItem key={type.value} value={type.value}>
