"use client";

import { useState, useEffect, useCallback } from "react";
import { Search, Plus, User, Phone, Mail, Check, X } from "lucide-react";
import { searchOwners, createOwner } from "@/app/(workspace)/gestion/biens/actions";

type Owner = {
  id: string;
  full_name: string | null;
  phone: string | null;
  email?: string | null;
  avatar_url?: string | null;
};

type OwnerSelectorProps = {
  value?: string;
  onChange: (ownerId: string | undefined, ownerData?: Owner) => void;
  className?: string;
};

export function OwnerSelector({ value, onChange, className = "" }: OwnerSelectorProps) {
  const [isOpen, setIsOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [owners, setOwners] = useState<Owner[]>([]);
  const [selectedOwner, setSelectedOwner] = useState<Owner | null>(null);
  const [isSearching, setIsSearching] = useState(false);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [newOwner, setNewOwner] = useState({ full_name: "", phone: "", email: "" });
  const [isCreating, setIsCreating] = useState(false);

  // Recherche de propri√©taires
  const handleSearch = useCallback(async (query: string) => {
    setIsSearching(true);
    const result = await searchOwners(query);
    setOwners(result.owners);
    setIsSearching(false);
  }, []);

  // Charger la liste initiale √† l'ouverture
  useEffect(() => {
    if (isOpen) {
      handleSearch("");
    }
  }, [isOpen, handleSearch]);

  // Debounce de la recherche
  useEffect(() => {
    const timer = setTimeout(() => {
      // Ne pas relancer la recherche vide car d√©j√† faite au chargement
      if (searchQuery.trim().length > 0) {
        handleSearch(searchQuery);
      } else if (searchQuery === "" && isOpen) {
        // Optionnel : recharger la liste vide si on efface tout
        handleSearch("");
      }
    }, 300);
    return () => clearTimeout(timer);
  }, [searchQuery, handleSearch, isOpen]);

  // S√©lection d'un propri√©taire
  const handleSelect = (owner: Owner) => {
    setSelectedOwner(owner);
    onChange(owner.id, owner);
    setIsOpen(false);
    setSearchQuery("");
  };

  // Cr√©er un nouveau propri√©taire
  const handleCreate = async () => {
    if (!newOwner.full_name || !newOwner.phone) return;

    setIsCreating(true);
    const result = await createOwner(newOwner);
    setIsCreating(false);

    if (result.success && result.owner) {
      setSelectedOwner(result.owner);
      onChange(result.owner.id, result.owner);
      setShowCreateForm(false);
      setNewOwner({ full_name: "", phone: "", email: "" });
      setIsOpen(false);
    }
  };

  // Retirer le propri√©taire s√©lectionn√©
  const handleClear = () => {
    setSelectedOwner(null);
    onChange(undefined);
  };

  return (
    <div className={`relative ${className}`}>
      <label className="block text-sm font-medium text-muted-foreground mb-2">
        Propri√©taire du bien
      </label>

      {/* Propri√©taire s√©lectionn√© */}
      {selectedOwner ? (
        <div className="flex items-center justify-between bg-card border border-border rounded-lg p-3 shadow-sm">
          <div className="flex items-center gap-3">
            <div className="w-10 h-10 rounded-full bg-muted flex items-center justify-center text-muted-foreground">
              <User className="w-5 h-5" />
            </div>
            <div>
              <p className="font-medium text-foreground">{selectedOwner.full_name}</p>
              <p className="text-sm text-muted-foreground">{selectedOwner.phone}</p>
            </div>
          </div>
          <button
            type="button"
            onClick={handleClear}
            className="p-2 hover:bg-muted rounded-lg transition-colors text-muted-foreground hover:text-foreground"
          >
            <X className="w-4 h-4" />
          </button>
        </div>
      ) : (
        <>
          {/* Bouton d'ouverture */}
          <button
            type="button"
            onClick={() => setIsOpen(true)}
            className="w-full flex items-center gap-3 bg-background border border-border rounded-lg p-3 hover:bg-muted/50 transition-colors text-left"
          >
            <div className="w-10 h-10 rounded-full bg-muted flex items-center justify-center text-muted-foreground">
              <User className="w-5 h-5" />
            </div>
            <span className="text-muted-foreground">S√©lectionner un propri√©taire...</span>
          </button>
        </>
      )}

      {/* Modal de s√©lection */}
      {isOpen && (
        <div className="fixed inset-0 bg-black/60 z-50 flex items-center justify-center p-4 backdrop-blur-sm">
          <div className="bg-background border border-border rounded-xl w-full max-w-md max-h-[80vh] overflow-hidden shadow-2xl">
            {/* Header */}
            <div className="p-4 border-b border-border">
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-lg font-semibold text-foreground">
                  {showCreateForm ? "Nouveau propri√©taire" : "S√©lectionner un propri√©taire"}
                </h3>
                <button
                  onClick={() => {
                    setIsOpen(false);
                    setShowCreateForm(false);
                  }}
                  className="p-2 hover:bg-muted rounded-lg transition-colors text-muted-foreground hover:text-foreground"
                >
                  <X className="w-5 h-5" />
                </button>
              </div>

              {!showCreateForm && (
                <div className="relative">
                  <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-5 h-5 text-muted-foreground" />
                  <input
                    type="text"
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    placeholder="Rechercher par nom, t√©l√©phone..."
                    className="w-full bg-muted border border-transparent focus:bg-background focus:border-primary rounded-lg pl-10 pr-4 py-2 text-foreground placeholder:text-muted-foreground focus:outline-none transition-colors"
                    autoFocus
                  />
                </div>
              )}
            </div>

            {/* Content */}
            <div className="p-4 overflow-y-auto max-h-[50vh]">
              {showCreateForm ? (
                <div className="space-y-4">
                  <div>
                    <label htmlFor="owner_fullname" className="block text-sm text-muted-foreground mb-1">Nom complet *</label>
                    <input
                      id="owner_fullname"
                      type="text"
                      value={newOwner.full_name}
                      onChange={(e) => setNewOwner({ ...newOwner, full_name: e.target.value })}
                      className="w-full bg-background border border-border rounded-lg px-4 py-2 text-foreground focus:outline-none focus:ring-1 focus:ring-primary"
                      placeholder="Ex: Mamadou Diallo"
                    />
                  </div>
                  <div>
                    <label htmlFor="owner_phone" className="block text-sm text-muted-foreground mb-1">T√©l√©phone *</label>
                    <input
                      id="owner_phone"
                      type="tel"
                      value={newOwner.phone}
                      onChange={(e) => setNewOwner({ ...newOwner, phone: e.target.value })}
                      className="w-full bg-background border border-border rounded-lg px-4 py-2 text-foreground focus:outline-none focus:ring-1 focus:ring-primary"
                      placeholder="Ex: 77 123 45 67"
                    />
                  </div>
                  <div>
                    <label htmlFor="owner_email" className="block text-sm text-muted-foreground mb-1">Email (optionnel)</label>
                    <input
                      id="owner_email"
                      type="email"
                      value={newOwner.email}
                      onChange={(e) => setNewOwner({ ...newOwner, email: e.target.value })}
                      className="w-full bg-background border border-border rounded-lg px-4 py-2 text-foreground focus:outline-none focus:ring-1 focus:ring-primary"
                      placeholder="Ex: mamadou@email.com"
                    />
                  </div>
                </div>
              ) : (
                <>
                  {isSearching ? (
                    <div className="text-center py-8 text-muted-foreground">Recherche...</div>
                  ) : owners.length > 0 ? (
                    <div className="space-y-2">
                      {owners.map((owner) => (
                        <button
                          key={owner.id}
                          type="button"
                          onClick={() => handleSelect(owner)}
                          className="w-full flex items-center gap-3 p-3 rounded-lg hover:bg-muted transition-colors text-left group"
                        >
                          <div className="w-10 h-10 rounded-full bg-muted group-hover:bg-background border border-transparent group-hover:border-border flex items-center justify-center transition-colors">
                            <User className="w-5 h-5 text-muted-foreground" />
                          </div>
                          <div className="flex-1 min-w-0">
                            <p className="font-medium text-foreground truncate">{owner.full_name}</p>
                            <div className="flex items-center gap-3 text-sm text-muted-foreground">
                              {owner.phone && (
                                <span className="flex items-center gap-1">
                                  <Phone className="w-3 h-3" /> {owner.phone}
                                </span>
                              )}
                              {owner.email && (
                                <span className="flex items-center gap-1">
                                  <Mail className="w-3 h-3" /> {owner.email}
                                </span>
                              )}
                            </div>
                          </div>
                          {value === owner.id && (
                            <Check className="w-5 h-5 text-primary" />
                          )}
                        </button>
                      ))}
                    </div>
                  ) : (
                    <div className="text-center py-8 text-muted-foreground">
                      Aucun propri√©taire trouv√©. Cr√©ez-en un nouveau.
                    </div>
                  )}
                </>
              )}
            </div>

            {/* Footer */}
            <div className="p-4 border-t border-border bg-muted/20">
              {showCreateForm ? (
                <div className="flex gap-3">
                  <button
                    type="button"
                    onClick={() => setShowCreateForm(false)}
                    className="flex-1 px-4 py-2 border border-border rounded-lg text-foreground hover:bg-muted transition-colors"
                  >
                    Retour
                  </button>
                  <button
                    type="button"
                    onClick={handleCreate}
                    disabled={!newOwner.full_name || !newOwner.phone || isCreating}
                    className="flex-1 px-4 py-2 bg-primary text-primary-foreground rounded-lg font-medium hover:bg-primary/90 transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    {isCreating ? "Cr√©ation..." : "Cr√©er"}
                  </button>
                </div>
              ) : (
                <button
                  type="button"
                  onClick={() => setShowCreateForm(true)}
                  className="w-full flex items-center justify-center gap-2 px-4 py-2 border border-dashed border-border rounded-lg text-muted-foreground hover:border-primary hover:text-primary hover:bg-primary/5 transition-all"
                >
                  <Plus className="w-5 h-5" />
                  Cr√©er un nouveau propri√©taire
                </button>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

"use client";

import { useEffect, useState } from "react";
import { useRouter } from "next/navigation";
import { createClient } from "@/utils/supabase/client";
import {
    CreditCard,
    Check,
    Warning,
    Buildings,
    Clock,
    Sparkle,
    Crown,
    Rocket,
    Receipt,
} from "@phosphor-icons/react";
import { Button } from "@/components/ui/button";
import { toast } from "sonner";
import { getAllPlans, formatPrice, getAnnualSavings, TRIAL_DURATION_DAYS, type BillingCycle } from "@/lib/subscription/plans-config";
import { reactivateSubscription } from "@/app/(workspace)/gestion/abonnement/actions";

export function SubscriptionManager() {
    const router = useRouter();
    const [isLoading, setIsLoading] = useState(true);
    const [isSubmitting, setIsSubmitting] = useState(false);
    const [proStatus, setProStatus] = useState<string | null>(null);
    const [subscriptionTier, setSubscriptionTier] = useState<string | null>(null);
    const [trialEndsAt, setTrialEndsAt] = useState<Date | null>(null);
    const [stats, setStats] = useState({ properties: 0, leases: 0 });
    const [hasStripeCustomer, setHasStripeCustomer] = useState(false);
    const [billingCycle, setBillingCycle] = useState<BillingCycle>('monthly');
    const [canReactivateTrial, setCanReactivateTrial] = useState(false);

    useEffect(() => {
        const loadData = async () => {
          try {
            const supabase = createClient();
            const { data: { user } } = await supabase.auth.getUser();

            if (!user) return;

            const { data: teamMemberships } = await supabase
                .from("team_members")
                .select("team_id, team:teams(*)")
                .eq("user_id", user.id)
                .eq("status", "active");

            const teamMembership = teamMemberships && teamMemberships.length > 0 ? teamMemberships[0] : null;
            const team = teamMembership?.team as {
                subscription_status?: string;
                subscription_tier?: string;
                subscription_trial_ends_at?: string;
                stripe_customer_id?: string;
                trial_reactivation_count?: number;
            } | null;
            const tId = teamMembership?.team_id;

            if (team) {
                let effectiveStatus = team.subscription_status || null;
                let effectiveTier = team.subscription_tier || 'starter';

                // Normaliser : si trialing mais date expir√©e, traiter comme expir√©
                if (effectiveStatus === 'trialing' && team.subscription_trial_ends_at) {
                    const trialEnd = new Date(team.subscription_trial_ends_at);
                    if (trialEnd < new Date()) {
                        effectiveStatus = 'past_due';
                        effectiveTier = 'starter';
                    }
                }

                setProStatus(effectiveStatus);
                setSubscriptionTier(effectiveTier);
                setHasStripeCustomer(!!team.stripe_customer_id);

                // Peut r√©activer l'essai si expir√© et pas encore utilis√© la r√©activation
                const MAX_REACTIVATIONS = 1;
                const isExpiredStatus = effectiveStatus === 'past_due' || effectiveStatus === 'canceled';
                const reactivationsUsed = team.trial_reactivation_count ?? 0;
                setCanReactivateTrial(isExpiredStatus && reactivationsUsed < MAX_REACTIVATIONS);
                if (team.subscription_trial_ends_at) {
                    setTrialEndsAt(new Date(team.subscription_trial_ends_at));
                }
            }

            if (tId) {
                const { count: propertiesCount } = await supabase
                    .from("properties")
                    .select("*", { count: "exact", head: true })
                    .eq("team_id", tId);

                const { count: leasesCount } = await supabase
                    .from("leases")
                    .select("*", { count: "exact", head: true })
                    .eq("team_id", tId)
                    .eq("status", "active");

                setStats({
                    properties: propertiesCount || 0,
                    leases: leasesCount || 0,
                });
            }
          } catch (err) {
            console.error("[SubscriptionManager] Failed to load data:", err);
          } finally {
            setIsLoading(false);
          }
        };

        loadData();
    }, []);

    const handleUpgrade = async (planId: string) => {
        setIsSubmitting(true);
        try {
            const response = await fetch("/api/subscription/checkout", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({
                    planId,
                    interval: billingCycle,
                    currency: 'xof',
                }),
            });

            const data = await response.json();
            if (data.url) {
                window.location.href = data.url;
            } else {
                toast.error(data.error || "Erreur lors de l'initialisation du paiement");
            }
        } catch {
            toast.error("Une erreur est survenue.");
        } finally {
            setIsSubmitting(false);
        }
    };

    const handleOpenPortal = async () => {
        setIsSubmitting(true);
        try {
            const response = await fetch("/api/subscription/portal", {
                method: "POST",
            });
            const data = await response.json();
            if (data.url) {
                window.location.href = data.url;
            } else {
                toast.error(data.error || "Impossible d'acc√©der au portail de facturation");
            }
        } catch {
            toast.error("Une erreur est survenue.");
        } finally {
            setIsSubmitting(false);
        }
    };

    const handleReactivateTrial = async () => {
        setIsSubmitting(true);
        try {
            const result = await reactivateSubscription();
            if (result?.data?.success) {
                toast.success("Essai gratuit r√©activ√© ! Vous avez 14 jours suppl√©mentaires.");
                window.location.reload();
                return;
            } else {
                toast.error(result?.error || "Erreur lors de la r√©activation.");
            }
        } catch (err: any) {
            toast.error(err.message || "Une erreur est survenue.");
        } finally {
            setIsSubmitting(false);
        }
    };

    if (isLoading) {
        return (
            <div className="p-12 flex flex-col items-center justify-center bg-white dark:bg-gray-900/40 rounded-3xl border border-slate-200 dark:border-gray-800">
                <div className="w-8 h-8 border-2 border-primary border-t-transparent rounded-full animate-spin" />
                <p className="mt-4 text-sm text-gray-500">Chargement de votre abonnement...</p>
            </div>
        );
    }

    const isExpired = proStatus === "canceled" || proStatus === "past_due" || proStatus === "unpaid" || proStatus === "incomplete";
    const isTrial = proStatus === "trialing";
    const isActive = proStatus === "active";
    const daysLeft = trialEndsAt
        ? Math.max(0, Math.ceil((trialEndsAt.getTime() - Date.now()) / (1000 * 60 * 60 * 24)))
        : 0;

    // Nom du plan actuel pour l'affichage
    const currentPlanName = getAllPlans().find(p => p.id === subscriptionTier)?.name || 'Starter';

    return (
        <section className="p-6 md:p-8 bg-white dark:bg-gray-900/40 rounded-3xl border border-slate-200 dark:border-gray-800 space-y-8 shadow-sm dark:shadow-none">
            {/* Header */}
            <div className="flex flex-col md:flex-row md:items-center justify-between gap-4">
                <div>
                    <h2 className="text-xl font-bold flex items-center gap-2 text-slate-900 dark:text-white">
                        <CreditCard className="w-6 h-6 text-primary" />
                        Mon Abonnement
                    </h2>
                    <p className="text-gray-500 text-sm mt-1">G√©rez votre formule et vos factures</p>
                </div>

                <div className="flex items-center gap-3">
                    {/* Stripe Portal Button - for active subscribers */}
                    {isActive && hasStripeCustomer && (
                        <Button
                            variant="outline"
                            size="sm"
                            onClick={handleOpenPortal}
                            disabled={isSubmitting}
                            className="gap-2 text-xs"
                        >
                            <Receipt size={14} />
                            G√©rer la facturation
                        </Button>
                    )}
                </div>
            </div>

            {/* Current Plan Summary - toujours visible */}
            <div className={`rounded-2xl p-5 border ${isExpired
                ? 'bg-red-500/5 border-red-500/20'
                : isTrial
                    ? 'bg-primary/5 border-primary/20'
                    : isActive
                        ? 'bg-emerald-500/5 border-emerald-500/20'
                        : 'bg-slate-50 dark:bg-white/5 border-slate-200 dark:border-white/10'
                }`}>
                <div className="flex flex-col sm:flex-row sm:items-center justify-between gap-3">
                    <div className="flex items-center gap-3">
                        <div className={`w-10 h-10 rounded-xl flex items-center justify-center ${isExpired ? 'bg-red-500/10' : isTrial ? 'bg-primary/10' : isActive ? 'bg-emerald-500/10' : 'bg-slate-100 dark:bg-white/10'
                            }`}>
                            {isExpired ? <Warning size={20} className="text-red-400" /> :
                                isTrial ? <Clock size={20} className="text-primary" /> :
                                    isActive ? <Sparkle size={20} className="text-emerald-400" /> :
                                        <Rocket size={20} className="text-gray-400" />}
                        </div>
                        <div>
                            <p className="text-sm font-semibold text-slate-900 dark:text-white">
                                Plan {currentPlanName}
                                {isTrial && <span className="ml-2 text-xs font-medium text-primary bg-primary/10 px-2 py-0.5 rounded-full">Essai gratuit</span>}
                                {isActive && <span className="ml-2 text-xs font-medium text-emerald-500 bg-emerald-500/10 px-2 py-0.5 rounded-full">Actif</span>}
                                {isExpired && <span className="ml-2 text-xs font-medium text-red-400 bg-red-500/10 px-2 py-0.5 rounded-full">Expir√©</span>}
                            </p>
                            <p className="text-xs text-gray-500 mt-0.5">
                                {isExpired && "Votre abonnement a expir√©. R√©activez pour retrouver l'acc√®s."}
