            </Badge>
          ) : isScheduled ? (
            <Badge className="bg-blue-500/90 text-white hover:bg-blue-600 border-0 backdrop-blur-md flex gap-1">
              <Clock className="w-3 h-3" />
              {property.scheduled_publish_at
                ? formatScheduledDate(property.scheduled_publish_at)
