                "absolute -inset-1 rounded-full border-2 border-[#F4C430]/30 transition-all duration-500",
                "group-hover:border-[#F4C430]/60 group-hover:scale-110",
                isFlipped && "border-[#F4C430]/60 scale-110 animate-ping"
