          </p>
        </motion.div>

        {/* Grille des biens */}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4 md:gap-6 lg:gap-8">
          {properties.map((property, index) => {
            const isExternal = property.isExternal;
            const targetUrl = isExternal
              ? `/biens/ext/${property.id}`
