function StepAgency({ data, updateData }: { data: WizardData; updateData: any }) {
    const [uploadingLogo, setUploadingLogo] = useState(false);
    const [uploadingSignature, setUploadingSignature] = useState(false);
    const [signatureMode, setSignatureMode] = useState<"upload" | "draw">("upload");
    const canvasRef = useRef<HTMLCanvasElement>(null);
    const [isDrawing, setIsDrawing] = useState(false);
    const [hasDrawn, setHasDrawn] = useState(false);

    // Initialize canvas
    useEffect(() => {
        if (signatureMode === "draw" && canvasRef.current) {
            const canvas = canvasRef.current;
            const ctx = canvas.getContext("2d");
            if (!ctx) return;
            const rect = canvas.getBoundingClientRect();
            canvas.width = rect.width * 2;
            canvas.height = rect.height * 2;
            ctx.scale(2, 2);
            ctx.strokeStyle = "#000000";
            ctx.lineWidth = 2;
            ctx.lineCap = "round";
            ctx.lineJoin = "round";
            ctx.fillStyle = "white";
            ctx.fillRect(0, 0, rect.width, rect.height);
        }
    }, [signatureMode]);

    const getCoordinates = (e: React.MouseEvent | React.TouchEvent) => {
        const canvas = canvasRef.current;
        if (!canvas) return { x: 0, y: 0 };
        const rect = canvas.getBoundingClientRect();
        if ("touches" in e) {
            return { x: e.touches[0].clientX - rect.left, y: e.touches[0].clientY - rect.top };
        }
        return { x: e.clientX - rect.left, y: e.clientY - rect.top };
    };

    const startDrawing = (e: React.MouseEvent | React.TouchEvent) => {
        e.preventDefault();
        const ctx = canvasRef.current?.getContext("2d");
        if (!ctx) return;
        const { x, y } = getCoordinates(e);
        ctx.beginPath();
        ctx.moveTo(x, y);
        setIsDrawing(true);
    };

    const draw = (e: React.MouseEvent | React.TouchEvent) => {
        if (!isDrawing) return;
        e.preventDefault();
        const ctx = canvasRef.current?.getContext("2d");
        if (!ctx) return;
        const { x, y } = getCoordinates(e);
        ctx.lineTo(x, y);
        ctx.stroke();
        setHasDrawn(true);
    };

    const stopDrawing = () => setIsDrawing(false);

    const clearCanvas = () => {
        const canvas = canvasRef.current;
        const ctx = canvas?.getContext("2d");
        if (!ctx || !canvas) return;
        const rect = canvas.getBoundingClientRect();
        ctx.fillStyle = "white";
        ctx.fillRect(0, 0, rect.width, rect.height);
        setHasDrawn(false);
        updateData("signatureUrl", "");
    };

    const saveDrawnSignature = async () => {
        const canvas = canvasRef.current;
        if (!canvas) return;
        setUploadingSignature(true);
        canvas.toBlob(async (blob) => {
            if (!blob) {
                toast.error("Erreur lors de la cr√©ation de l'image");
                setUploadingSignature(false);
                return;
            }
            try {
                const file = new File([blob], "signature.png", { type: "image/png" });
                const formData = new FormData();
                formData.append("file", file);
                formData.append("type", "signature");
                const response = await fetch("/api/branding/upload", { method: "POST", body: formData });
                const result = await response.json();
                if (result.success) {
                    updateData("signatureUrl", result.url);
                    toast.success("Signature enregistr√©e!");
                } else {
                    toast.error(result.error || "Erreur lors de l'upload");
                }
            } catch {
                toast.error("Erreur lors de l'upload de la signature");
            } finally {
                setUploadingSignature(false);
            }
        }, "image/png");
    };

    const handleFileUpload = async (file: File, type: "logo" | "signature") => {
        const isLogo = type === "logo";
        isLogo ? setUploadingLogo(true) : setUploadingSignature(true);
        try {
            const formData = new FormData();
            formData.append("file", file);
            formData.append("type", type);
            const response = await fetch("/api/branding/upload", { method: "POST", body: formData });
            const result = await response.json();
            if (result.success) {
                updateData(isLogo ? "logoUrl" : "signatureUrl", result.url);
                toast.success(`${isLogo ? "Logo" : "Signature"} enregistr√©!`);
            } else {
                toast.error(result.error || "Erreur lors de l'upload");
            }
        } catch {
            toast.error("Erreur lors de l'upload du fichier");
        } finally {
            isLogo ? setUploadingLogo(false) : setUploadingSignature(false);
        }
    };

    return (
        <div className="space-y-6">
            <div className="text-center mb-6">
                <h2 className="text-2xl font-bold text-slate-900 dark:text-white">Configurez votre Agence</h2>
