                    </div>
                    <div className="space-y-2">
                        <Label htmlFor="companyAddress">Adresse du si√®ge</Label>
                        <Input id="companyAddress" placeholder="Ex: 123 Rue de la R√©publique" value={data.companyAddress} onChange={(e) => updateData("companyAddress", e.target.value)} className="h-11 bg-slate-50 dark:bg-gray-800" />
                    </div>
                </div>
                <div className="grid md:grid-cols-2 gap-4">
                    <div className="space-y-2">
                        <Label>T√©l√©phone de l&apos;agence</Label>
                        <PhoneInput placeholder="33 800 00 00" value={data.companyPhone} onChange={(val) => updateData("companyPhone", val)} className="bg-slate-50 dark:bg-gray-800" defaultCountry="SN" />
                    </div>
                    <div className="space-y-2">
