"use client";

import Image from "next/image";
import { ExternalLink, MapPin, Navigation } from "lucide-react";
import { motion } from "framer-motion";
import { useState, useEffect, useMemo } from "react";

import { Button } from "@/components/ui/button";

type StaticMapProps = {
  coords: { lat: number; lng: number };
  city: string;
  address?: string;
  landmark?: string;
};

export const StaticMap = ({ coords, city, address, landmark }: StaticMapProps) => {
  const [mapUrl, setMapUrl] = useState<string | null>(null);
  const [mapError, setMapError] = useState(false);

  // V√©rifier si les coordonn√©es sont valides
  const hasValidCoords = useMemo(() => {
    if (!coords) return false;
    return coords.lat !== 0 && coords.lng !== 0 &&
      !isNaN(coords.lat) && !isNaN(coords.lng) &&
      coords.lat >= -90 && coords.lat <= 90 &&
      coords.lng >= -180 && coords.lng <= 180;
  }, [coords]);

  const mapsLink = hasValidCoords && coords
    ? `https://www.google.com/maps?q=${coords.lat},${coords.lng}`
    : `https://www.google.com/maps/search/${encodeURIComponent(city + (address ? `, ${address}` : ""))}`;

  // Construire l'URL de la carte avec CartoDB Dark Matter (comme la carte principale)
  useEffect(() => {
    if (!hasValidCoords) {
      setMapUrl(null);
      return;
    }

    const mapboxToken = process.env.NEXT_PUBLIC_MAPBOX_TOKEN;
    const googleApiKey = process.env.NEXT_PUBLIC_GOOGLE_MAPS_KEY;

    if (mapboxToken && coords) {
      // Utiliser Mapbox avec style dark (comme la carte principale)
      const mapboxMapUrl = `https://api.mapbox.com/styles/v1/mapbox/dark-v11/static/pin-s+3B82F6(${coords.lng},${coords.lat})/${coords.lng},${coords.lat},15/800x400@2x?access_token=${mapboxToken}`;
      setMapUrl(mapboxMapUrl);
    } else if (googleApiKey && coords) {
      // Utiliser Google Maps Static API avec un style sombre
      const googleMapUrl = new URL("https://maps.googleapis.com/maps/api/staticmap");
      googleMapUrl.searchParams.set("center", `${coords.lat},${coords.lng}`);
      googleMapUrl.searchParams.set("zoom", "15");
      googleMapUrl.searchParams.set("size", "800x400");
      googleMapUrl.searchParams.set("scale", "2");
      googleMapUrl.searchParams.set("format", "jpg");
      googleMapUrl.searchParams.set("maptype", "roadmap");

      // Style sombre pour correspondre au th√®me
      googleMapUrl.searchParams.set(
        "style",
        "feature:all|element:labels|visibility:simplified|feature:poi|visibility:off|feature:road|element:labels|visibility:off|feature:water|color:0x1a1a2e|feature:landscape|color:0x16213e"
      );

      // Marqueur personnalis√©
      googleMapUrl.searchParams.set(
        "markers",
        `color:0x3B82F6|size:mid|${coords.lat},${coords.lng}`
      );

      googleMapUrl.searchParams.set("key", googleApiKey);
      setMapUrl(googleMapUrl.toString());
    }
  }, [hasValidCoords, coords]);

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      whileInView={{ opacity: 1, y: 0 }}
      viewport={{ once: true, amount: 0.4 }}
      className="space-y-4 rounded-3xl border border-gray-100 bg-gray-50 p-6 dark:border-white/10 dark:bg-white/5"
    >
      <div className="flex flex-col gap-3 sm:flex-row sm:items-center sm:justify-between">
        <div className="flex-1">
          <p className="text-xs uppercase tracking-[0.3em] text-gray-500 dark:text-white/50">
            LOCALISATION
          </p>
          <h3 className="mt-1 text-2xl font-semibold text-gray-900 dark:text-white">
            {city}
          </h3>
          {address && (
            <p className="mt-1 flex items-center gap-2 text-sm text-gray-600 dark:text-white/70">
              <MapPin className="h-4 w-4 shrink-0" />
              <span>{address}</span>
            </p>
          )}
          {landmark && (
            <p className="mt-1 text-sm text-gray-500 dark:text-white/60">
              {landmark}
            </p>
          )}
        </div>
        <Button
          variant="secondary"
          className="shrink-0 rounded-2xl border border-gray-200 bg-white text-sm font-medium text-gray-900 hover:bg-gray-100 dark:border-white/20 dark:bg-transparent dark:text-white dark:hover:bg-white/10"
          asChild
        >
          <a href={mapsLink} target="_blank" rel="noreferrer" className="flex items-center gap-2">
            <Navigation className="h-4 w-4" />
            Ouvrir Maps
          </a>
        </Button>
      </div>

      <div className="relative h-64 w-full overflow-hidden rounded-2xl border border-gray-200 bg-gray-100 dark:border-white/10 dark:bg-gray-900">
        {hasValidCoords && mapUrl && !mapError ? (
          <>
            <Image
              src={mapUrl}
              alt={`Carte de ${city}`}
              fill
              className="object-cover"
              sizes="(max-width: 768px) 100vw, 800px"
              quality={90}
              onError={() => setMapError(true)}
              priority={false}
            />
            {/* Marqueur personnalis√© au centre */}
            <div className="absolute left-1/2 top-1/2 -translate-x-1/2 -translate-y-1/2">
              <div className="relative">
                <div className="absolute inset-0 animate-ping rounded-full bg-blue-500/30" />
                <div className="relative h-8 w-8 rounded-full bg-blue-500 shadow-lg ring-2 ring-white dark:ring-gray-900" />
                <div className="absolute left-1/2 top-1/2 h-3 w-3 -translate-x-1/2 -translate-y-1/2 rounded-full bg-white" />
              </div>
            </div>
            {/* Coordonn√©es en bas √† droite */}
            <div className="absolute bottom-3 right-3 rounded-lg bg-black/70 px-3 py-1.5 text-xs font-mono text-white/90 backdrop-blur-sm">
              {coords.lat.toFixed(6)}, {coords.lng.toFixed(6)}
            </div>
          </>
        ) : (
          <div className="flex h-full w-full flex-col items-center justify-center bg-gradient-to-br from-gray-100 via-gray-50 to-gray-100 p-6 dark:from-gray-800 dark:via-gray-900 dark:to-gray-800">
            <div className="mb-4 flex h-16 w-16 items-center justify-center rounded-full bg-gray-200 dark:bg-white/10">
              <MapPin className="h-8 w-8 text-gray-400 dark:text-white/30" />
            </div>
            <div className="text-center">
              <p className="text-sm font-medium text-gray-600 dark:text-white/60">
                {hasValidCoords ? "Carte non disponible" : "Coordonn√©es non disponibles"}
              </p>
              <p className="mt-1 text-xs text-gray-500 dark:text-white/40">
                {hasValidCoords
                  ? 'Cliquez sur "Ouvrir Maps" pour voir la localisation'
                  : 'Les coordonn√©es GPS ne sont pas configur√©es pour ce bien'}
              </p>
            </div>
          </div>
        )}
      </div>
    </motion.div>
  );
};

"use client";

import { useEffect, useState } from "react";
import { Download, X, Share2 } from "lucide-react";
import { motion, AnimatePresence } from "framer-motion";
import { toast } from "sonner";

import { Button } from "@/components/ui/button";

type BeforeInstallPromptEvent = Event & {
  prompt: () => Promise<void>;
  userChoice: Promise<{ outcome: "accepted" | "dismissed" }>;
};

type Platform = "ios" | "android" | "other";

function detectPlatform(): Platform {
  if (typeof window === "undefined") return "other";

  const userAgent = window.navigator.userAgent.toLowerCase();
  const isIOS = /iphone|ipad|ipod/.test(userAgent);
  const isAndroid = /android/.test(userAgent);

  if (isIOS) return "ios";
  if (isAndroid) return "android";
  return "other";
}

function isStandalone(): boolean {
  if (typeof window === "undefined") return false;
  const nav = window.navigator as { standalone?: boolean };
  return (
    window.matchMedia("(display-mode: standalone)").matches ||
    nav.standalone === true ||
    document.referrer.includes("android-app://")
  );
}

export function InstallPrompt() {
  const [deferredPrompt, setDeferredPrompt] =
    useState<BeforeInstallPromptEvent | null>(null);
  const [isVisible, setIsVisible] = useState(false);
  const [isInstalled, setIsInstalled] = useState(false);
  const [platform, setPlatform] = useState<Platform>("other");
  const [showIOSInstructions, setShowIOSInstructions] = useState(false);

  useEffect(() => {
    // Check if already installed
    setIsInstalled(isStandalone());

    // Detect platform
    setPlatform(detectPlatform());

    // Check if user dismissed recently
    const checkDismissed = () => {
      const dismissed = localStorage.getItem("pwa-install-dismissed");
      if (dismissed) {
        const dismissedDate = new Date(dismissed);
        const daysSinceDismissed =
          (Date.now() - dismissedDate.getTime()) / (1000 * 60 * 60 * 24);
        // Show again after 1 day instead of 7 days
        if (daysSinceDismissed < 1) {
          return false;
        }
      }
      return true;
    };

    // Handle beforeinstallprompt (Android/Chrome)
    const handleBeforeInstallPrompt = (e: Event) => {
      e.preventDefault();
      if (checkDismissed() && !isStandalone()) {
        setDeferredPrompt(e as BeforeInstallPromptEvent);
        setIsVisible(true);
      }
    };

    // For iOS, show prompt after a delay if not installed
    if (platform === "ios" && !isStandalone() && checkDismissed()) {
      // Show iOS prompt after 3 seconds on mobile
      const timer = setTimeout(() => {
        if (window.innerWidth <= 768) {
          setIsVisible(true);
        }
      }, 3000);
      return () => clearTimeout(timer);
    }

    // For Android/Chrome, listen for beforeinstallprompt
    if (platform !== "ios") {
      window.addEventListener("beforeinstallprompt", handleBeforeInstallPrompt);

      // Fallback: Show prompt on mobile Android after delay if no beforeinstallprompt
      const fallbackTimer = setTimeout(() => {
        if (
          platform === "android" &&
          window.innerWidth <= 768 &&
          !isStandalone() &&
          checkDismissed()
        ) {
          // Check if deferredPrompt was set in the meantime
          if (!deferredPrompt) {
            setIsVisible(true);
          }
        }
      }, 5000);

      return () => {
        window.removeEventListener("beforeinstallprompt", handleBeforeInstallPrompt);
        clearTimeout(fallbackTimer);
      };
    }
  }, [platform]);

  const handleInstall = async () => {
    if (platform === "ios") {
      setShowIOSInstructions(true);
      return;
    }

    // Android/Chrome: Use native prompt if available
    if (deferredPrompt) {
      try {
        deferredPrompt.prompt();
        const { outcome } = await deferredPrompt.userChoice;

        if (outcome === "accepted") {
          toast.success("Installation en cours...");
          setIsVisible(false);
          setDeferredPrompt(null);
        } else {
          toast.info("Installation annul√©e");
        }
      } catch (error) {
        console.error("Error during install:", error);
        toast.error("Erreur lors de l'installation");
      }
      return;
    }

    // Fallback: Show instructions for manual installation
    if (platform === "android") {
      toast.info("Utilisez le menu de votre navigateur pour installer l'app");
      handleDismiss();
    }
  };

  const handleDismiss = () => {
    setIsVisible(false);
    setShowIOSInstructions(false);
    // Store dismissal in localStorage to avoid showing again for 1 day
    localStorage.setItem(
      "pwa-install-dismissed",
      new Date().toISOString()
    );
  };

  if (isInstalled) {
    return null;
  }

  // iOS Instructions Modal
  if (showIOSInstructions) {
    return (
      <AnimatePresence>
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          className="fixed inset-0 z-50 flex items-center justify-center bg-black/60 p-4 backdrop-blur-sm"
          onClick={handleDismiss}
        >
          <motion.div
            initial={{ scale: 0.9, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            exit={{ scale: 0.9, opacity: 0 }}
            onClick={(e) => e.stopPropagation()}
            className="w-full max-w-md rounded-3xl border border-white/10 bg-[#0b0f18] p-6 shadow-2xl"
          >
            <div className="mb-4 flex items-center justify-between">
              <h3 className="text-lg font-semibold text-white">
                Installer sur iOS
              </h3>
              <button
                onClick={handleDismiss}
                className="text-white/50 hover:text-white"
                aria-label="Fermer"
              >
                <X className="h-5 w-5" />
              </button>
            </div>
            <div className="space-y-4 text-sm text-white/80">
              <div className="flex gap-3">
                <div className="flex h-8 w-8 shrink-0 items-center justify-center rounded-full bg-white/10 text-lg font-bold text-white">
                  1
                </div>
                <div>
                  <p className="font-semibold text-white">Appuyez sur le bouton Partager</p>
                  <p className="text-xs text-white/60">En bas de l&apos;√©cran (ic√¥ne carr√©e avec fl√®che)</p>
                </div>
              </div>
              <div className="flex gap-3">
                <div className="flex h-8 w-8 shrink-0 items-center justify-center rounded-full bg-white/10 text-lg font-bold text-white">
                  2
                </div>
                <div>
                  <p className="font-semibold text-white">S√©lectionnez &quot;Sur l&apos;√©cran d&apos;accueil&quot;</p>
                  <p className="text-xs text-white/60">Faites d√©filer si n√©cessaire</p>
                </div>
              </div>
              <div className="flex gap-3">
                <div className="flex h-8 w-8 shrink-0 items-center justify-center rounded-full bg-white/10 text-lg font-bold text-white">
                  3
                </div>
                <div>
                  <p className="font-semibold text-white">Confirmez l&apos;installation</p>
                  <p className="text-xs text-white/60">L&apos;app appara√Ætra sur votre √©cran d&apos;accueil</p>
                </div>
              </div>
            </div>
            <Button
              className="mt-6 w-full rounded-full"
              onClick={handleDismiss}
            >
              J&apos;ai compris
            </Button>
          </motion.div>
        </motion.div>
      </AnimatePresence>
    );
  }

  // Main install prompt
  if (!isVisible) {
    return null;
  }

  return (
    <AnimatePresence>
      <motion.div
        key="install-prompt-banner"
        initial={{ y: 100, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        exit={{ y: 100, opacity: 0 }}
        className="fixed inset-x-4 bottom-20 z-[45] md:bottom-4 md:left-auto md:right-4 md:w-96"
      >
        <div className="rounded-2xl border border-white/10 bg-[#0b0f18]/95 p-4 shadow-2xl backdrop-blur-xl">
          <div className="flex items-start gap-3">
            <div className="flex-1">
              <h3 className="text-sm font-semibold text-white">
                Installer Dousell Immo
              </h3>
              <p className="mt-1 text-xs text-white/70">
                Ajoutez l&apos;app √† votre √©cran d&apos;accueil pour un acc√®s
                rapide
              </p>
            </div>
            <button
              onClick={handleDismiss}
              className="text-white/50 hover:text-white"
              aria-label="Fermer"
            >
              <X className="h-4 w-4" />
            </button>
          </div>
          <div className="mt-4 flex gap-2">
            <Button
              variant="secondary"
              size="sm"
              className="flex-1 rounded-full border border-white/10 bg-white/5 text-white"
              onClick={handleDismiss}
            >
              Plus tard
            </Button>
            <Button
              size="sm"
              className="flex-1 rounded-full"
              onClick={handleInstall}
            >
              {platform === "ios" ? (
                <>
                  <Share2 className="mr-2 h-4 w-4" />
                  Installer
                </>
              ) : (
                <>
                  <Download className="mr-2 h-4 w-4" />
                  Installer
                </>
              )}
            </Button>
          </div>
        </div>
      </motion.div>
    </AnimatePresence>
  );
}

"use client";

import { useEffect, useState } from "react";
import { Bell, BellOff } from "lucide-react";
import { toast } from "sonner";
import OneSignal from "react-onesignal";

import { Button } from "@/components/ui/button";

export function PushNotifications() {
  const [isSupported, setIsSupported] = useState(false);
  const [isSubscribed, setIsSubscribed] = useState(false);

  useEffect(() => {
    if (typeof window !== "undefined") {
      setIsSupported(true); // OneSignal handles compatibility check internally usually

      // Check initial subscription state
      // Note: OneSignal.User.PushSubscription.optedIn is available in v16+
      // But react-onesignal wrapper might vary. 
      // Safe fallback check:
      const checkSubscription = async () => {
        try {
          const state = await OneSignal.User.PushSubscription.optedIn;
          setIsSubscribed(!!state);
        } catch (_e) {
          console.log("OneSignal not ready yet");
        }
      };

      checkSubscription();

      // Listen for subscription changes if possible or polling
      // OneSignal.User.PushSubscription.addEventListener("change", ...)
    }
  }, []);

  const handleTogglePush = async () => {
    try {
      if (isSubscribed) {
        await OneSignal.User.PushSubscription.optOut();
        setIsSubscribed(false);
        toast.info("Notifications d√©sactiv√©es");
      } else {
        await OneSignal.User.PushSubscription.optIn();
        setIsSubscribed(true);
        toast.success("Notifications activ√©es !");
      }
    } catch (error) {
      console.error("OneSignal error:", error);
      // Fallback: show prompt
      try {
        await OneSignal.Slidedown.promptPush();
      } catch (_e) {
        toast.error("Impossible de modifier les param√®tres de notification");
      }
    }
  };

  if (!isSupported) {
    return null;
  }

  if (isSubscribed) {
    return (
      <Button
        variant="secondary"
        size="sm"
        className="rounded-full border border-white/10 bg-white/5 text-white"
        onClick={handleTogglePush}
      >
        <Bell className="mr-2 h-4 w-4" />
        Notifications activ√©es
      </Button>
    );
  }

  return (
    <Button
      variant="secondary"
      size="sm"
      className="rounded-full border border-white/10 bg-white/5 text-white"
      onClick={handleTogglePush}
    >
      <BellOff className="mr-2 h-4 w-4" />
      Activer les notifications
    </Button>
  );
}

