/***************************  GRAPHICS CARD  ***************************/

export default function GraphicsCard({ sx, children, overLay = false, bgImage, ...rest }: any) {
    const theme: any = useTheme();

    return (
        <Card
            role="img"
            rel="noopener noreferrer"
            aria-label="graphics card"
            elevation={0}
            sx={{
                bgcolor: 'grey.100',
                borderRadius: { xs: 6, sm: 8, md: 10 },
                ...(bgImage && {
                    backgroundImage: `url(${GetImagePath(bgImage)})`,
                    backgroundSize: 'cover',
                    backgroundPosition: 'center'
                }),

                ...(overLay && {
                    position: 'relative',
                    '&:before': {
                        content: `' '`,
                        position: 'absolute',
                        width: 1,
                        height: 1,
                        top: 0,
                        left: 0,
                        background: typeof overLay === 'string' ? overLay : withAlpha(theme.palette.grey[100], 0.75)
                    }
                }),
                ...sx
            }}
            {...rest}
        >
            {overLay ? <Box sx={{ position: 'relative', height: 1 }}>{children}</Box> : children}
        </Card>
    );
}

"use client";

import * as React from "react";
import { useRouter, useSearchParams, usePathname } from "next/navigation";
import { Search, Loader2, User, Home, Building, FileText, Wrench, MessageSquare, Wallet, Scale, FolderOpen, Users, Settings, LayoutDashboard } from "lucide-react";
import { useDebounce } from "@/hooks/use-debounce";
import { Input } from "@/components/ui/input";
import { cn } from "@/lib/utils";

interface Suggestion {
    id: string;
    label: string;
    subLabel?: string;
    type: "tenant" | "property" | "nav";
    url: string;
    icon?: any;
}

const GESTION_NAV_ITEMS = [
    { label: "Dashboard", url: "/gestion", icon: LayoutDashboard },
    { label: "Biens", url: "/gestion/biens", icon: Home },
    { label: "√âtats des Lieux", url: "/gestion/etats-lieux", icon: FileText },
    { label: "Interventions", url: "/gestion/interventions", icon: Wrench },
    { label: "Documents", url: "/gestion/documents", icon: FolderOpen },
    { label: "Messagerie", url: "/gestion/messages", icon: MessageSquare },
    { label: "Juridique", url: "/gestion/documents-legaux", icon: Scale },
    { label: "Comptabilit√©", url: "/gestion/comptabilite", icon: Wallet },
    { label: "√âquipe", url: "/gestion/equipe", icon: Users },
    { label: "Configuration", url: "/gestion/config", icon: Settings },
];

export function GlobalSearch() {
    const router = useRouter();
    const pathname = usePathname();
    const searchParams = useSearchParams();

    const initialQuery = searchParams?.get("q") || "";
    const [query, setQuery] = React.useState(initialQuery);

    React.useEffect(() => {
        setQuery(searchParams?.get("q") || "");
    }, [searchParams]);

    const debouncedQuery = useDebounce(query, 300);
    const [suggestions, setSuggestions] = React.useState<Suggestion[]>([]);
    const [loading, setLoading] = React.useState(false);
    const [isOpen, setIsOpen] = React.useState(false);
    const wrapperRef = React.useRef<HTMLDivElement>(null);

    React.useEffect(() => {
        const handleClickOutside = (event: MouseEvent) => {
            if (wrapperRef.current && !wrapperRef.current.contains(event.target as Node)) {
                setIsOpen(false);
            }
        };
        document.addEventListener("mousedown", handleClickOutside);
        return () => document.removeEventListener("mousedown", handleClickOutside);
    }, []);

    React.useEffect(() => {
        if (debouncedQuery.length < 2) {
            setSuggestions([]);
            return;
        }

        const fetchSuggestions = async () => {
            setLoading(true);
            const allResults: Suggestion[] = [];
            const searchTerm = debouncedQuery.toLowerCase();

            // 1. Static Navigation Suggestions
            if (pathname?.startsWith("/gestion")) {
                const navMatches = GESTION_NAV_ITEMS.filter(item =>
                    item.label.toLowerCase().includes(searchTerm)
                ).map(item => ({
                    id: `nav-${item.label}`,
                    label: item.label,
                    subLabel: "Navigation",
                    type: "nav" as const,
                    url: item.url,
                    icon: item.icon
                }));
                allResults.push(...navMatches);
            }

            try {
                const res = await fetch(`/api/search/suggestions?q=${encodeURIComponent(debouncedQuery)}`);
                if (res.ok) {
                    const data = await res.json();
                    allResults.push(...data);
                }
            } catch (error) {
                console.error("Search error:", error);
            } finally {
                setSuggestions(allResults);
                setIsOpen(true);
                setLoading(false);
            }
        };

        fetchSuggestions();
    }, [debouncedQuery, pathname]);

    const handleSelect = (url: string) => {
        setIsOpen(false);
        router.push(url);
    };

    const handleSearchSubmit = (e?: React.FormEvent) => {
        e?.preventDefault();
        setIsOpen(false);

        if (!query.trim()) return;

        const trimmedQuery = query.trim();

        if (pathname?.startsWith("/gestion") || pathname?.startsWith("/admin")) {
            const params = new URLSearchParams(searchParams?.toString());
            params.set("q", trimmedQuery);
            router.push(`${pathname}?${params.toString()}`);
        } else {
            router.push(`/recherche?q=${encodeURIComponent(trimmedQuery)}`);
        }
    };

    return (
        <div ref={wrapperRef} className="relative w-full">
            <form onSubmit={handleSearchSubmit} className="relative w-full">
                <Search className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-muted-foreground" />
                <Input
                    type="search"
                    enterKeyHint="search"
                    placeholder="Rechercher un bien, une ville..."
                    className="pl-9 bg-white/5 border-white/10 focus-visible:ring-primary/50 focus-visible:ring-offset-0 focus-visible:bg-white/10 transition-all w-full rounded-2xl placeholder:text-white/40"
                    value={query}
                    onChange={(e) => {
                        setQuery(e.target.value);
                        if (e.target.value.length < 2) setIsOpen(false);
                    }}
                    onFocus={() => {
                        if (suggestions.length > 0) setIsOpen(true);
                    }}
                />
            </form>

            {isOpen && (query.length >= 2) && (
                <div className="absolute top-full left-0 right-0 mt-2 rounded-xl border bg-popover p-1 text-popover-foreground shadow-xl outline-none animate-in fade-in-0 zoom-in-95 z-[100] max-h-[250px] sm:max-h-[400px] overflow-y-auto">
                    {loading ? (
                        <div className="flex items-center justify-center p-4 text-sm text-muted-foreground">
                            <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                            Recherche...
                        </div>
                    ) : suggestions.length > 0 ? (
                        <div className="flex flex-col gap-1">
                            <p className="px-2 py-1.5 text-xs font-medium text-muted-foreground">Suggestions</p>
                            {suggestions.map((item) => (
                                <button
                                    key={`${item.type}-${item.id}`}
                                    onClick={() => handleSelect(item.url)}
                                    className="relative flex w-full cursor-pointer select-none items-center rounded-sm px-2 py-1.5 text-sm outline-none hover:bg-accent hover:text-accent-foreground"
                                >
                                    {item.type === 'nav' ? (
                                        <div className="mr-2 flex items-center justify-center w-4 h-4">
                                            {item.icon ? <item.icon className="h-4 w-4 text-orange-500" /> : <LayoutDashboard className="h-4 w-4 text-orange-500" />}
                                        </div>
                                    ) : item.type === 'tenant' ? (
                                        <User className="mr-2 h-4 w-4 text-blue-500 shrink-0" />
                                    ) : (
                                        <Building className="mr-2 h-4 w-4 text-green-500 shrink-0" />
                                    )}
                                    <div className="flex flex-col items-start truncate">
                                        <span className="font-medium truncate w-full text-left">{item.label}</span>
                                        {item.subLabel && <span className="text-xs text-muted-foreground truncate w-full text-left">{item.subLabel}</span>}
                                    </div>
                                </button>
                            ))}
                        </div>
                    ) : (
                        <div className="p-4 text-sm text-muted-foreground text-center">
                            Aucun r√©sultat trouv√©.
                        </div>
                    )}
                </div>
            )}
        </div>
    );
}

"use client";

import { useState } from "react";
import { useRouter, useSearchParams } from "next/navigation";
import { Bell, X, Loader2 } from "lucide-react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import * as z from "zod";

import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { toast } from "sonner";
import { createSearchAlert } from "@/app/(workspace)/compte/alertes/actions";
import type { PropertyFilters } from "@/services/propertyService";

const alertSchema = z.object({
  name: z
    .string()
    .min(3, "Le nom doit contenir au moins 3 caract√®res")
    .max(50, "Le nom ne peut pas d√©passer 50 caract√®res"),
});

type AlertFormData = z.infer<typeof alertSchema>;

interface CreateAlertDialogProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  filters: PropertyFilters;
}

export function CreateAlertDialog({
  open,
  onOpenChange,
  filters,
}: CreateAlertDialogProps) {
  const router = useRouter();
  const searchParams = useSearchParams();
  const [isSubmitting, setIsSubmitting] = useState(false);

  const {
    register,
    handleSubmit,
    formState: { errors },
    reset,
  } = useForm<AlertFormData>({
    resolver: zodResolver(alertSchema),
    defaultValues: {
      name: "",
    },
  });

  const onSubmit = async (data: AlertFormData) => {
    setIsSubmitting(true);

    try {
      const result = await createSearchAlert({
        name: data.name,
        filters,
      });

      if (result.error) {
        toast.error("Erreur", {
          description: result.error,
        });
        return;
      }

      toast.success("Alerte cr√©√©e", {
        description: `Votre alerte "${data.name}" a √©t√© cr√©√©e avec succ√®s.`,
      });

      reset();
      onOpenChange(false);

      // Si on √©tait sur la page de recherche avec ?alert=create, rediriger vers les alertes
      if (searchParams.get("alert") === "create") {
        router.push("/compte/alertes");
        router.refresh();
      }
    } catch (error) {
      console.error("Error creating alert:", error);
      toast.error("Erreur", {
        description: "Une erreur inattendue s'est produite",
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  const formatFilters = () => {
    const parts: string[] = [];
    // Utiliser 'q' pour la recherche textuelle (location)
    const filtersWithQ = filters as { q?: string; location?: string; category?: string; city?: string; type?: string; minPrice?: number; maxPrice?: number; rooms?: number; bedrooms?: number; hasBackupGenerator?: boolean; hasWaterTank?: boolean };
    const searchQuery = filtersWithQ.q || filters.location;
    if (searchQuery && searchQuery.trim()) {
      parts.push(searchQuery.trim());
    }
    if (filters.category) {
      parts.push(filters.category === "vente" ? "Achat" : "Location");
    }
    if (filters.city) {
      parts.push(filters.city);
    }
    if (filters.type) {
      parts.push(filters.type);
    }
    if (filters.minPrice || filters.maxPrice) {
      const priceRange = [
        filters.minPrice ? `${filters.minPrice.toLocaleString()} FCFA` : "",
        filters.maxPrice ? `${filters.maxPrice.toLocaleString()} FCFA` : "",
      ]
        .filter(Boolean)
        .join(" - ");
      if (priceRange) {
        parts.push(priceRange);
      }
    }
    if (filters.rooms) {
      parts.push(`${filters.rooms} pi√®ce${filters.rooms > 1 ? "s" : ""}`);
    }
    if (filters.bedrooms) {
      parts.push(`${filters.bedrooms} chambre${filters.bedrooms > 1 ? "s" : ""}`);
    }
    if (filters.hasBackupGenerator) {
      parts.push("Groupe √©lectrog√®ne");
    }
    if (filters.hasWaterTank) {
      parts.push("Citerne d'eau");
    }
    return parts.length > 0 ? parts.join(", ") : "Tous les biens (aucun filtre)";
  };

  const hasActiveFilters = (() => {
    // V√©rifier si au moins un filtre a une valeur significative
    const filtersWithQ = filters as { q?: string; location?: string; category?: string; city?: string; type?: string; minPrice?: number; maxPrice?: number };
    return !!(
      (filtersWithQ.q && filtersWithQ.q.trim()) ||
      filters.location ||
      filters.category ||
      filters.city ||
      filters.type ||
      filters.minPrice ||
      filters.maxPrice ||
      filters.rooms ||
      filters.bedrooms ||
      filters.hasBackupGenerator ||
      filters.hasWaterTank
    );
  })();

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-md border-white/10 bg-[#05080c] text-white">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2 text-xl">
            <Bell className="h-5 w-5 text-amber-500" />
            Cr√©er une alerte de recherche
          </DialogTitle>
          <DialogDescription className="text-white/60">
            Recevez une notification quand de nouveaux biens correspondent √† vos
            crit√®res de recherche.
          </DialogDescription>
        </DialogHeader>

        <form onSubmit={handleSubmit(onSubmit)} className="space-y-6">
          {/* Nom de l'alerte */}
          <div className="space-y-2">
            <Label htmlFor="name" className="text-white">
              Nom de l&apos;alerte
            </Label>
            <Input
              id="name"
              {...register("name")}
              placeholder="Ex: Appartement √† Dakar"
              className="border-white/10 bg-white/5 text-white placeholder:text-white/40 focus-visible:ring-white/20"
              disabled={isSubmitting}
            />
            {errors.name && (
              <p className="text-sm text-red-400">{errors.name.message}</p>
            )}
          </div>

          {/* Crit√®res de recherche */}
          <div className="space-y-2">
            <Label className="text-white">Crit√®res de recherche</Label>
            <div className="rounded-lg border border-white/10 bg-white/5 p-4">
              <p className="text-sm text-white/80">{formatFilters()}</p>
              {!hasActiveFilters && (
                <p className="mt-2 text-xs text-white/50 italic">
                  Vous recevrez une notification pour tous les nouveaux biens publi√©s.
                </p>
              )}
            </div>
          </div>

          {/* Boutons */}
          <div className="flex gap-3 pt-2">
            <Button
              type="button"
              variant="ghost"
              onClick={() => {
                reset();
                onOpenChange(false);
              }}
              disabled={isSubmitting}
              className="flex-1 border border-white/10 text-white hover:bg-white/10"
            >
              Annuler
            </Button>
            <Button
              type="submit"
              disabled={isSubmitting}
              className="flex-1 bg-amber-500 text-black hover:bg-amber-600"
            >
              {isSubmitting ? (
                <>
                  <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                  Cr√©ation...
                </>
              ) : (
                <>
                  <Bell className="mr-2 h-4 w-4" />
                  Cr√©er l&apos;alerte
                </>
              )}
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}

