    } else {
      setTimeout(() => setSuggestions([]), 0);
    }
  }, [debouncedSearchQuery]);

  const activeFilters = hasActiveFilters(filters);

  // Ouvrir le dialog de cr√©ation d'alerte si ?alert=create est pr√©sent
