  useEffect(() => {
    let ignoreResult = false;

    if (debouncedSearchQuery !== (filters.q ?? "")) {
      const searchFilters = async () => {
        console.log("[SearchExperience] Triggering search for:", debouncedSearchQuery);
        setIsSearching(true);
        const nextFilters = { ...filters, q: debouncedSearchQuery || undefined };

        console.log("[SearchExperience] Updating URL with filters:", nextFilters);
        setFilters(nextFilters);

        try {
          console.log("[SearchExperience] Fetching listings for:", debouncedSearchQuery);
          const data = await getUnifiedListings(nextFilters);

          if (!ignoreResult) {
            console.log("[SearchExperience] Search results received:", data.length);
            setResults(data);
            setCurrentPage(1);
          } else {
            console.log("[SearchExperience] Result ignored (stale call)");
          }
        } catch (error) {
          console.error("[SearchExperience] Search error:", error);
        } finally {
          if (!ignoreResult) {
            setIsSearching(false);
          }
        }
      };
      searchFilters();
    }

    return () => {
      ignoreResult = true;
    };
  }, [debouncedSearchQuery, filters, setFilters]);

  const applyFilters = useCallback(async (nextFilters: PropertyFilters) => {
    console.log("[SearchExperience] Applying filters:", nextFilters);
    setIsSearching(true);
    setFilters(nextFilters);
    try {
      const data = await getUnifiedListings(nextFilters);
      console.log("[SearchExperience] Filtered results received:", data.length);
      setResults(data);
      setCurrentPage(1);
    } catch (error) {
      console.error("[SearchExperience] Filter application error:", error);
    } finally {
      setIsSearching(false);
    }
  }, [setFilters]);

  // M√©moiser le nombre de r√©sultats et les r√©sultats pagin√©s
  const resultCount = useMemo(() => results.length, [results.length]);

  const paginatedResults = useMemo(() => {
    const start = (currentPage - 1) * ITEMS_PER_PAGE;
    return results.slice(start, start + ITEMS_PER_PAGE);
  }, [results, currentPage]);

  const totalPages = Math.ceil(resultCount / ITEMS_PER_PAGE);

  // Effet pour d√©filer vers le haut des r√©sultats lors d'un changement de page
  useEffect(() => {
    if (currentPage > 1) {
      const resultsElement = document.getElementById("search-results-top");
      if (resultsElement) {
        resultsElement.scrollIntoView({ behavior: "smooth", block: "start" });
      }
    }
  }, [currentPage]);

  return (
    <div className="relative space-y-6 pb-32">
      <div className="relative z-50 rounded-[28px] border border-white/5 bg-black/40 p-4 backdrop-blur-xl">
        <div className="flex flex-col gap-3 sm:flex-row sm:items-center">
          <form
            onSubmit={(e) => {
              e.preventDefault();
              // On peut aussi forcer un blur ici pour fermer le clavier
              (document.activeElement as HTMLElement)?.blur();
            }}
            className="flex flex-1 items-center gap-3 rounded-2xl border border-white/10 bg-white/5 px-4 py-2"
          >
            <SearchIcon className={`h-5 w-5 ${isSearching ? "text-primary animate-pulse" : "text-white/50"}`} />
            <div className="relative flex-1">
              <Input
                type="search"
                enterKeyHint="search"
                value={searchQuery}
                onChange={(event) => {
                  setSearchQuery(event.target.value);
                  setShowSuggestions(true);
                }}
                onFocus={() => setShowSuggestions(true)}
                onBlur={() => setTimeout(() => setShowSuggestions(false), 200)}
                placeholder="Ville ou code postal"
                className="border-none bg-transparent p-0 text-white placeholder:text-white/50 focus-visible:ring-0"
              />
              {showSuggestions && suggestions.length > 0 && (
                <div className="absolute top-full left-0 mt-2 w-full origin-top-left rounded-xl border border-white/10 bg-[#0F172A] p-1 shadow-2xl shadow-black/50 z-[60] overflow-y-auto max-h-[40vh] pb-safe">
                  {suggestions.map((suggestion, index) => (
                    <button
                      key={index}
                      type="button"
                      className="w-full rounded-lg px-4 py-3 text-left text-sm text-white/90 hover:bg-white/10 hover:text-white transition-colors cursor-pointer flex items-center gap-2"
                      onMouseDown={(e) => {
                        e.preventDefault(); // Emp√™che la perte de focus de l'input
                        setSearchQuery(suggestion);
                        setShowSuggestions(false);
                      }}
                    >
                      <MapPin className="h-4 w-4 text-white/40 shrink-0" />
                      <span className="truncate">{suggestion}</span>
                    </button>
                  ))}
                </div>
              )}
            </div>
          </form>
          <Button
            type="button"
            variant="secondary"
            className="rounded-2xl border border-white/10 bg-white/10 text-white"
            onClick={() => setDrawerOpen(true)}
          >
            <Filter className="mr-2 h-4 w-4" />
            Filtres
            {activeFilters && (
              <span className="ml-2 rounded-full bg-primary px-2 py-0.5 text-xs font-semibold text-primary-foreground">
                ‚óè
              </span>
            )}
          </Button>
          {user && (
            <Button
              type="button"
              variant="secondary"
              className="rounded-2xl border border-white/10 bg-white/10 text-white"
              onClick={() => setAlertDialogOpen(true)}
            >
              <Bell className="mr-2 h-4 w-4" />
              Cr√©er une alerte
            </Button>
          )}
        </div>
        <p className="mt-3 text-sm text-white/60">
          {isSearching ? (
            <span className="animate-pulse">Recherche en cours...</span>
          ) : (
            <>{resultCount} bien{resultCount > 1 ? "s" : ""} trouv√©{resultCount > 1 ? "s" : ""}</>
          )}
        </p>
      </div>

      {view === "list" ? (
        results.length === 0 ? (
          <EmptyState
            title="Aucun bien ne correspond √† vos crit√®res."
            description={
              activeFilters
                ? "Essayez de modifier vos crit√®res de recherche ou de r√©initialiser les filtres pour voir plus de r√©sultats."
                : "Nous ajoutons r√©guli√®rement de nouveaux biens. Revenez bient√¥t pour d√©couvrir les derni√®res offres !"
            }
            actionLabel={activeFilters ? "R√©initialiser les filtres" : undefined}
            onAction={
              activeFilters
                ? () => {
                  console.log("[SearchExperience] Clearing all filters");
                  const clearedFilters: PropertyFilters = {};
                  setSearchQuery("");
                  setFilters(clearedFilters);
                  applyFilters(clearedFilters);
                }
                : undefined
            }
          />
        ) : (
          <div id="search-results-top" className="scroll-mt-24">
            <div className="grid grid-cols-1 gap-6 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 [&>*]:w-full">
              {paginatedResults.map((property) => (
                <PropertyCardUnified key={property.id} property={property} />
              ))}
            </div>

            {totalPages > 1 && (
              <div className="mt-12 flex justify-center">
                <Pagination>
                  <PaginationContent>
                    <PaginationItem>
                      {currentPage > 1 ? (
                        <PaginationPrevious
                          onClick={() => setCurrentPage(prev => prev - 1)}
                        />
                      ) : (
                        <div className="pointer-events-none opacity-30">
                          <PaginationPrevious />
                        </div>
                      )}
                    </PaginationItem>

                    {/* Logic to show page numbers with ellipsis */}
                    {Array.from({ length: totalPages }, (_, i) => i + 1)
                      .filter(page => {
                        // All√©ger la pagination sur mobile
                        if (totalPages <= 5) return true;
                        if (page === 1 || page === totalPages) return true;
                        if (Math.abs(page - currentPage) <= 1) return true;
                        return false;
                      })
                      .map((page, index, array) => (
                        <React.Fragment key={page}>
                          {index > 0 && array[index - 1] !== page - 1 && (
                            <PaginationItem>
                              <PaginationEllipsis />
                            </PaginationItem>
                          )}
                          <PaginationItem>
                            <PaginationLink
                              isActive={currentPage === page}
                              onClick={() => setCurrentPage(page)}
                            >
                              {page}
                            </PaginationLink>
                          </PaginationItem>
                        </React.Fragment>
                      ))}

                    <PaginationItem>
                      {currentPage < totalPages ? (
                        <PaginationNext
                          onClick={() => setCurrentPage(prev => prev + 1)}
                        />
                      ) : (
                        <div className="pointer-events-none opacity-30">
                          <PaginationNext />
                        </div>
                      )}
                    </PaginationItem>
                  </PaginationContent>
                </Pagination>
              </div>
            )}
          </div>
        )
      ) : (
        <MapView
          properties={results}
          onClose={() => setView("list")}
          searchQuery={searchQuery}
          onSearchChange={setSearchQuery}
        />
      )}

      <Button
        type="button"
        className="fixed bottom-28 right-6 z-30 rounded-full px-5 py-3 shadow-xl md:bottom-8"
        onClick={() => setView((prev) => (prev === "list" ? "map" : "list"))}
      >
        {view === "list" ? (
          <>
            <Map className="mr-2 h-4 w-4" /> Vue carte
          </>
        ) : (
          <>
            <SearchIcon className="mr-2 h-4 w-4" /> Vue liste
          </>
        )}
      </Button>

      <FilterDrawer
        open={drawerOpen}
        onOpenChange={setDrawerOpen}
        filters={filters}
        onApply={applyFilters}
      />

      {user && (
        <CreateAlertDialog
          open={alertDialogOpen}
          onOpenChange={setAlertDialogOpen}
          filters={filters}
        />
      )}

      <ManagementPromoCard />
    </div>
  );
};

