import * as React from "react";
import { ThemeProvider as NextThemesProvider, useTheme as useNextTheme } from "next-themes";
import { createContext, useContext, useEffect, useState } from "react";

/**
 * Unified Theme Provider
 * Handles hydration stabilization and provides a stable useTheme hook
 */
export function ThemeProvider({
    children,
    ...props
}: React.ComponentProps<typeof NextThemesProvider>) {
    return (
        <NextThemesProvider {...props}>
            {children}
        </NextThemesProvider>
    );
}

/**
 * Enhanced useTheme hook
 * Provides 'mounted' state and 'isDark' helper to avoid hydration mismatches
 */
export function useTheme() {
    const { theme, setTheme, resolvedTheme } = useNextTheme();
    const [mounted, setMounted] = useState(false);

    useEffect(() => {
        setMounted(true);
    }, []);

    const toggleTheme = () => {
        setTheme(resolvedTheme === "dark" ? "light" : "dark");
    };

    // Stable defaults during hydration
    if (!mounted) {
        return {
            theme: "dark" as const,
            setTheme,
            resolvedTheme: "dark" as const,
            toggleTheme,
            isDark: true,
            mounted: false
        };
    }

    return {
        theme: (theme || "dark") as string,
        setTheme,
        resolvedTheme: (resolvedTheme || "dark") as string,
        toggleTheme,
        isDark: resolvedTheme === "dark",
        mounted: true
    };
}

"use client";
import React, { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import Link, { LinkProps } from "next/link";
import Image from "next/image";
import { cn } from "@/lib/utils";

const transition: any = {
  type: "spring",
  mass: 0.5,
  damping: 11.5,
  stiffness: 100,
  restDelta: 0.001,
  restSpeed: 0.001,
};

export const MenuItem = ({
  setActive,
  active,
  item,
  children,
}: {
  setActive: (item: string) => void;
  active: string | null;
  item: string;
  children?: React.ReactNode;
}) => {
  return (
    <div onMouseEnter={() => setActive(item)} className="relative">
      <motion.p
        transition={{ duration: 0.3 }}
        className="cursor-pointer text-gray-300 hover:text-[#F4C430] text-sm font-medium transition-colors"
      >
        {item}
      </motion.p>
      {active !== null && (
        <motion.div
          initial={{ opacity: 0, scale: 0.85, y: 10 }}
          animate={{ opacity: 1, scale: 1, y: 0 }}
          transition={transition}
        >
          {active === item && (
            <div className="absolute top-full left-1/2 transform -translate-x-1/2">
              {/* Invisible bridge to prevent hover gap - covers the space between trigger and dropdown */}
              <div className="h-4 w-full" />
              <motion.div
                transition={transition}
                layoutId="active"
                className="bg-black/80 backdrop-blur-md rounded-2xl overflow-hidden border border-[#F4C430]/20 shadow-2xl"
              >
                <motion.div layout className="w-max h-full p-4">
                  {children}
                </motion.div>
              </motion.div>
            </div>
          )}
        </motion.div>
      )}
    </div>
  );
};

export type NavItem = {
  title: string;
  href: string;
};

export type ProductItemType = {
  title: string;
  href: string;
  src: string;
  description: string;
};

export type LogoConfig = {
