"use client";

import { motion } from "framer-motion";
import { type ReactNode, useState, useEffect } from "react";

type FadeInProps = {
  children: ReactNode;
  delay?: number;
  direction?: "up" | "down" | "none";
  className?: string;
};

/**
 * Composant pour faire appara√Ætre du contenu au scroll avec un effet de fade
 */
export function FadeIn({
  children,
  delay = 0,
  direction = "up",
  className,
}: FadeInProps) {
  const [mounted, setMounted] = useState(false);
  useEffect(() => setMounted(true), []);

  const yOffset = direction === "up" ? 20 : direction === "down" ? -20 : 0;

  return (
    <motion.div
      initial={mounted ? { opacity: 0, y: yOffset } : undefined}
      whileInView={mounted ? { opacity: 1, y: 0 } : undefined}
      viewport={{ once: true, margin: "-50px" }}
      transition={{ duration: 0.5, delay, ease: "easeOut" as const }}
      className={className}
    >
      {children}
    </motion.div>
  );
}

type StaggerContainerProps = {
  children: ReactNode;
  className?: string;
  staggerDelay?: number;
};

/**
 * Wrapper pour les listes/grids qui permet aux enfants d'appara√Ætre en cascade
 */
export function StaggerContainer({
  children,
  className,
  staggerDelay = 0.1,
}: StaggerContainerProps) {
  const [mounted, setMounted] = useState(false);
  useEffect(() => setMounted(true), []);

  return (
    <motion.div
      initial={mounted ? "hidden" : undefined}
      whileInView={mounted ? "visible" : undefined}
      viewport={{ once: true, margin: "-50px" }}
      variants={{
        hidden: { opacity: 0 },
        visible: {
          opacity: 1,
          transition: {
            staggerChildren: staggerDelay,
          },
        },
      }}
      className={className}
    >
      {children}
    </motion.div>
  );
}

/**
 * Variante pour les enfants du StaggerContainer
 */
export const staggerItem = {
  hidden: { opacity: 0, y: 20 },
  visible: {
    opacity: 1,
    y: 0,
    transition: {
      duration: 0.5,
      ease: "easeOut" as const,
    },
  },
};

type ScaleOnHoverProps = {
  children: ReactNode;
  className?: string;
  scale?: number;
  tapScale?: number;
};

/**
 * Composant pour les cartes et boutons avec effet de scale au survol
 * @deprecated Utilisez `Touchable` pour un comportement plus natif
 */
export function ScaleOnHover({
  children,
  className,
  scale = 1.02,
  tapScale = 0.98,
}: ScaleOnHoverProps) {
  const [mounted, setMounted] = useState(false);
  useEffect(() => setMounted(true), []);

  return (
    <motion.div
      whileHover={mounted ? { scale } : undefined}
      whileTap={mounted ? { scale: tapScale } : undefined}
      transition={{
        type: "spring",
        stiffness: 400,
        damping: 17,
      }}
      className={className}
    >
      {children}
    </motion.div>
  );
}

