"use client";

import Link from "next/link";
import { usePathname } from "next/navigation";
import { useState, useRef, useEffect, useMemo } from "react";
import { useOwnerUnreadCounts } from "@/hooks/use-unread-counts";
import { createClient } from "@/utils/supabase/client";
import {
  Building2,
  Home,
  Wrench,
  Wallet,
  User,
  FileText,
  CreditCard,
  BarChart3,
  Users,
  Shield,
  Heart,
  Settings,
} from "lucide-react";
import { cn } from "@/lib/utils";
import type { LucideIcon } from "lucide-react";

interface NavItem {
  href: string;
  icon: LucideIcon;
  label: string;
}

// Navigation pour propri√©taires (/gestion) - 4 items principaux
const gestionNavItems: NavItem[] = [
  { href: "/gestion", icon: BarChart3, label: "Dashboard" },
  { href: "/gestion/biens", icon: Building2, label: "Biens" },
  { href: "/gestion/interventions", icon: Wrench, label: "Travaux" },
  { href: "/gestion/comptabilite", icon: Wallet, label: "Compta" },
];

// Navigation pour locataires (/locataire)
const locataireNavItems: NavItem[] = [
  { href: "/locataire", icon: Home, label: "Accueil" },
  { href: "/locataire/documents", icon: FileText, label: "Documents" },
  { href: "/locataire/paiements", icon: CreditCard, label: "Paiements" },
  { href: "/locataire/maintenance", icon: Wrench, label: "Demandes" },
];

// Navigation pour admin (/admin)
const adminNavItems: NavItem[] = [
  { href: "/admin", icon: BarChart3, label: "Dashboard" },
  { href: "/admin/users", icon: Users, label: "Users" },
  { href: "/admin/verifications", icon: Shield, label: "V√©rifs" },
  { href: "/admin/moderation", icon: Settings, label: "Mod√©ration" },
];

// Navigation pour compte (/compte)
const compteNavItems: NavItem[] = [
  { href: "/compte", icon: User, label: "Profil" },
  { href: "/compte/mes-biens", icon: Building2, label: "Mes Biens" },
  { href: "/compte/favoris", icon: Heart, label: "Favoris" },
  { href: "/compte/parametres", icon: Settings, label: "Param√®tres" },
];

export function WorkspaceBottomNav() {
  const pathname = usePathname();
  const [isVisible, setIsVisible] = useState(true);
  const [mounted, setMounted] = useState(false);
  const lastScrollY = useRef(0);
  const ticking = useRef(false);
  const scrollContainerRef = useRef<Element | null>(null);
  const scrollHandlerRef = useRef<(() => void) | null>(null);
  const [userId, setUserId] = useState<string | null>(null);
  const [teamId, setTeamId] = useState<string | null>(null);

  useEffect(() => {
    setMounted(true);
    // Get user and team for badge counts
    const supabase = createClient();
    supabase.auth.getUser().then(({ data: { user } }) => {
      if (user) {
        setUserId(user.id);
        // Get active team from cookie or team_members
        const savedTeam = document.cookie
          .split("; ")
          .find((c) => c.startsWith("current_team_id="))
          ?.split("=")[1];
        if (savedTeam) setTeamId(savedTeam);
      }
    });
  }, []);

  const badgeCounts = useOwnerUnreadCounts(
    pathname?.startsWith("/gestion") ? userId : null,
    teamId
  );


  // D√©terminer le contexte et les items de navigation
  const navItems = useMemo(() => {
    if (pathname?.startsWith("/gestion")) return gestionNavItems;
    if (pathname?.startsWith("/locataire")) return locataireNavItems;
    if (pathname?.startsWith("/admin")) return adminNavItems;
    return compteNavItems;
  }, [pathname]);

  useEffect(() => {
    // Attendre que le DOM soit stable avant de chercher le conteneur
    // (√©vite les race conditions sur mobile lors de la navigation)
    const timeoutId = setTimeout(() => {
      try {
        const scrollContainer = document.querySelector("main.overflow-y-auto");
        if (!scrollContainer) return;

        // Initialisation
        const initVisibility = () => {
          const currentScroll = scrollContainer.scrollTop;
          lastScrollY.current = currentScroll;

          if (currentScroll > 100) {
            setTimeout(() => setIsVisible(false), 0);
          } else {
            setTimeout(() => setIsVisible(true), 0);
          }
        };

        initVisibility();

        const handleScroll = () => {
          if (!ticking.current) {
            requestAnimationFrame(() => {
              const currentScrollY = scrollContainer.scrollTop;
              const scrollDelta = currentScrollY - lastScrollY.current;

              if (Math.abs(scrollDelta) > 10) {
                if (scrollDelta > 0 && currentScrollY > 100) {
                  setIsVisible(false);
                } else if (scrollDelta < 0) {
                  setIsVisible(true);
                }
                lastScrollY.current = currentScrollY;
              }

              ticking.current = false;
            });
            ticking.current = true;
          }
        };

        scrollContainer.addEventListener("scroll", handleScroll, { passive: true });

        // Stocker la ref pour le cleanup
        scrollContainerRef.current = scrollContainer;
        scrollHandlerRef.current = handleScroll;
      } catch (_err) {
        // Silently ignore DOM errors on mobile navigation
      }
    }, 150);

    return () => {
      clearTimeout(timeoutId);
      const container = scrollContainerRef.current;
      const handler = scrollHandlerRef.current;
      if (container && handler) {
        container.removeEventListener("scroll", handler);
      }
    };
  }, [pathname]);

  // Toujours visible au changement de page
  useEffect(() => {
    setTimeout(() => setIsVisible(true), 0);
    lastScrollY.current = 0;
  }, [pathname]);

  if (!mounted) return null;

  return (
    <nav

      className={cn(
        "fixed inset-x-0 bottom-0 z-40 border-t border-border bg-background/80 backdrop-blur-lg lg:hidden print:hidden",
        "transition-transform duration-300 ease-out",
        !isVisible && "translate-y-full"
      )}
      style={{
        paddingBottom: "env(safe-area-inset-bottom)",
      }}
    >
      <div className="flex h-16 items-center justify-between px-4">
        {navItems.map((item) => {
          const isActive =
            pathname === item.href ||
            (item.href !== "/gestion" &&
              item.href !== "/locataire" &&
              item.href !== "/admin" &&
              item.href !== "/compte" &&
              pathname?.startsWith(`${item.href}/`));

          const Icon = item.icon;

          // Badge count for this item
          const badgeCount = item.href === "/gestion/interventions"
            ? badgeCounts.pendingMaintenance
            : item.href === "/gestion/comptabilite"
              ? 0
              : 0;

          return (
            <Link
              key={item.href}
              href={item.href}
              prefetch={false} // Disable prefetch to prevent stale RSC cache
              className="flex flex-1 flex-col items-center justify-center gap-1 min-w-0"
            >
              <span
                className={cn(
                  "relative inline-flex h-6 w-6 shrink-0 items-center justify-center rounded-lg transition-all duration-200",
                  isActive
                    ? "text-[#F4C430] bg-[#F4C430]/10"
                    : "text-muted-foreground"
                )}
              >
                <Icon
                  className="h-5 w-5"
                  strokeWidth={isActive ? 2.5 : 1.5}
                />
                {badgeCount > 0 && (
                  <span className="absolute -top-1.5 -right-2.5 flex h-4 min-w-4 items-center justify-center rounded-full bg-red-500 px-0.5 text-[9px] font-bold text-white">
                    {badgeCount > 9 ? "9+" : badgeCount}
                  </span>
                )}
              </span>
              <span
                className={cn(
                  "truncate w-full text-center text-[10px] font-medium transition-colors duration-200",
                  isActive ? "text-[#F4C430] font-semibold" : "text-muted-foreground"
                )}
              >
                {item.label}
              </span>
            </Link>
          );
        })}
      </div>
    </nav>
  );
}

"use client";

import Link from "next/link";
import Image from "next/image";
import { usePathname, useRouter } from "next/navigation";
import { Bell, Search, LogOut, ArrowLeft, Sun, Moon, Menu } from "lucide-react";
import { Button } from "@/components/ui/button";
import { GlobalSearch } from "@/components/search/GlobalSearch";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { useTheme } from "@/components/theme-provider";
import { useMemo, Suspense } from "react";
import { NotificationBell } from "@/components/layout/notification-bell";
import { OwnerRoleSwitcher } from "@/components/workspace/OwnerRoleSwitcher";

interface WorkspaceHeaderProps {
  user?: {
    id?: string;
    email?: string;
    user_metadata?: {
      full_name?: string;
      avatar_url?: string;
    };
  } | null;
  onMenuClick?: () => void;
}

export function WorkspaceHeader({ user, onMenuClick }: WorkspaceHeaderProps) {
  const pathname = usePathname();
  const router = useRouter();
  const { isDark, toggleTheme } = useTheme();


  // D√©terminer le titre selon le contexte
  const contextTitle = useMemo(() => {
    if (pathname?.startsWith("/gestion")) return "Gestion Locative";
    if (pathname?.startsWith("/locataire")) return "Espace Locataire";
    if (pathname?.startsWith("/admin")) return "Administration";
    return "Mon Compte";
  }, [pathname]);

  const userInitials = useMemo(() => {
    const name = user?.user_metadata?.full_name || user?.email || "U";
    return name
      .split(" ")
      .map((n) => n[0])
      .join("")
      .toUpperCase()
      .slice(0, 2);
  }, [user]);

  const handleSignOut = async () => {
    // Appeler l'API de d√©connexion
    router.push("/auth/signout");
  };

  return (
    <header
      className="shrink-0 border-b border-border bg-background/95 backdrop-blur supports-[backdrop-filter]:bg-background/60 z-40 transition-all pwa-header-safe"
    >
      <div className="flex h-14 items-center justify-between px-4 lg:px-6">
        {/* Left: Menu (mobile) + Logo + Retour site */}
        <div className="flex items-center gap-2 md:gap-4">
          {/* Mobile Menu Trigger */}
          <Button
            variant="ghost"
            size="icon"
            className="lg:hidden h-9 w-9 -ml-1 text-foreground"
            onClick={onMenuClick}
          >
            <Menu className="h-5 w-5" />
            <span className="sr-only">Menu</span>
          </Button>
          {/* Logo avec lien retour vitrine */}
          <Link
            href="/"
            className="flex items-center gap-2 text-muted-foreground hover:text-foreground transition-colors group"
          >
            <Image
              src="/icons/icon-192.png"
              width={28}
              height={28}
              alt="Dousell Immo"
              className="rounded"
            />
            <span className="hidden sm:flex items-center gap-1 text-xs">
              <ArrowLeft className="h-3 w-3 opacity-0 -translate-x-1 group-hover:opacity-100 group-hover:translate-x-0 transition-all" />
              <span>Retour au site</span>
            </span>
          </Link>

          {/* S√©parateur + Titre contexte */}
          <div className="hidden md:flex items-center gap-3">
            <div className="h-6 w-px bg-border" />
            <span className="text-sm font-medium text-primary">
              {contextTitle}
            </span>
          </div>
        </div>

        {/* Center: Search (desktop) - Functional */}
        <div className="hidden lg:flex flex-1 max-w-md mx-8">
          <Suspense fallback={<div className="h-9 w-full bg-muted/50 rounded-md animate-pulse" />}>
            <GlobalSearch />
          </Suspense>
        </div>

        {/* Right: Actions */}
        <div className="flex items-center gap-2">
          {/* Theme Toggle */}
          <Button
            variant="ghost"
            size="icon"
            className="h-9 w-9 text-foreground transition-transform duration-200 active:scale-95 hover:bg-accent hover:text-accent-foreground group"
            onClick={toggleTheme}
          >
            {isDark ? (
              <Sun className="h-4 w-4 transition-colors group-hover:text-inherit" />
            ) : (
              <Moon className="h-4 w-4 transition-colors group-hover:text-inherit" />
            )}
            <span className="sr-only">Basculer le th√®me</span>
          </Button>

          {/* Notifications */}
          {/* Notifications component - handles dropdown & realtime */}
          <NotificationBell
            userId={user?.id || null}
            className="hover:bg-accent hover:text-accent-foreground [&_svg]:transition-colors"
          />

          {/* User Menu */}
          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button variant="ghost" className="relative h-9 w-9 rounded-full transition-transform duration-200 hover:scale-110 active:scale-95">
                <Avatar className="h-9 w-9">
                  <AvatarImage
                    src={user?.user_metadata?.avatar_url}
                    alt={user?.user_metadata?.full_name || "Utilisateur"}
                  />
                  <AvatarFallback className="bg-slate-200 dark:bg-primary/10 text-slate-700 dark:text-primary text-xs font-medium">
                    {userInitials}
                  </AvatarFallback>
                </Avatar>
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent className="w-56" align="end" forceMount>
              <DropdownMenuLabel className="font-normal">
                <div className="flex flex-col space-y-1">
                  <p className="text-sm font-medium leading-none">
                    {user?.user_metadata?.full_name || "Utilisateur"}
                  </p>
                  <p className="text-xs leading-none text-muted-foreground">
                    {user?.email}
                  </p>
                </div>
              </DropdownMenuLabel>
              <DropdownMenuSeparator />
              <DropdownMenuItem asChild>
                <Link href="/compte">Mon profil</Link>
              </DropdownMenuItem>
              <DropdownMenuItem asChild>
                <Link href="/compte/parametres">Param√®tres</Link>
              </DropdownMenuItem>
              <DropdownMenuSeparator />
              {/* Switch to tenant space if owner is also a tenant */}
              <OwnerRoleSwitcher />
              <DropdownMenuItem asChild>
                <Link href="/">Retour au site</Link>
              </DropdownMenuItem>
              <DropdownMenuSeparator />
              <DropdownMenuItem
                className="text-destructive focus:text-destructive"
                onClick={handleSignOut}
              >
                <LogOut className="mr-2 h-4 w-4" />
                D√©connexion
              </DropdownMenuItem>
            </DropdownMenuContent>
          </DropdownMenu>
        </div>
      </div>
    </header>
  );
}

"use client";

import { usePathname } from "next/navigation";
import Link from "next/link";
import { cn } from "@/lib/utils";
import {
  LayoutDashboard,
  Building2,
  ClipboardList,
  Wrench,
  MessageSquare,
  FolderOpen,
  Scale,
  Wallet,
  Settings,
  User,
  Bell,
  Home,
  FileText,
  CreditCard,
  Users,
  Shield,
  BarChart3,
  ChevronLeft,
  Menu,
  SlidersHorizontal,
  X,
} from "lucide-react";
import { useState, useMemo, useRef, useCallback, useEffect } from "react";
import { useOwnerUnreadCounts } from "@/hooks/use-unread-counts";
import { createClient } from "@/utils/supabase/client";
import { Button } from "@/components/ui/button";
import { Sheet, SheetContent, SheetTrigger, SheetClose, SheetTitle } from "@/components/ui/sheet";
import { TeamSwitcher } from "./TeamSwitcher";
import { TemporaryAccessWidget } from "./TemporaryAccessWidget";
import { LockedSidebarItem } from "./LockedSidebarItem";
import { AccessRequestModal, useAccessRequestModal } from "@/components/modals/AccessRequestModal";
import type { TeamPermissionKey } from "@/lib/team-permissions";
import type { WorkspaceTeamData } from "@/types/team";

import type { LucideIcon } from "lucide-react";

interface NavItem {
  href: string;
  icon: LucideIcon;
  label: string;
  requiredPermission?: TeamPermissionKey; // Permission requise pour acc√©der
  requiredTier?: 'pro' | 'enterprise'; // Tier minimum requis
}

// Navigation pour propri√©taires (/gestion) avec permissions
const gestionNavItems: NavItem[] = [
  { href: "/gestion", icon: LayoutDashboard, label: "Dashboard" }, // Accessible √† tous
  { href: "/gestion/biens", icon: Home, label: "Biens", requiredPermission: "properties.view" },
  { href: "/gestion/etats-lieux", icon: ClipboardList, label: "√âtats des Lieux", requiredPermission: "inventory.view" },
  { href: "/gestion/interventions", icon: Wrench, label: "Interventions", requiredPermission: "maintenance.view", requiredTier: 'pro' },
  { href: "/gestion/documents", icon: FolderOpen, label: "Documents", requiredPermission: "documents.view" },
  { href: "/gestion/messages", icon: MessageSquare, label: "Messagerie" }, // Accessible √† tous
  { href: "/gestion/documents-legaux", icon: Scale, label: "Juridique", requiredPermission: "documents.generate" },
  { href: "/gestion/comptabilite", icon: Wallet, label: "Comptabilit√©", requiredPermission: "payments.view", requiredTier: 'pro' },
];

// Navigation pour locataires (/locataire)
const locataireNavItems: NavItem[] = [
  { href: "/locataire", icon: Home, label: "Mon Espace" },
  { href: "/locataire/documents", icon: FileText, label: "Documents" },
  { href: "/locataire/paiements", icon: CreditCard, label: "Paiements" },
  { href: "/locataire/maintenance", icon: Wrench, label: "Maintenance" },
  { href: "/locataire/messages", icon: MessageSquare, label: "Messages" },
];

// Navigation pour admin (/admin)
const adminNavItems: NavItem[] = [
  { href: "/admin", icon: BarChart3, label: "Dashboard" },
  { href: "/admin/users", icon: Users, label: "Utilisateurs" },
  { href: "/admin/verifications", icon: Shield, label: "V√©rifications" },
  { href: "/admin/moderation", icon: Scale, label: "Mod√©ration" },
  { href: "/admin/roles", icon: Settings, label: "R√¥les" },
];

// Navigation pour compte (/compte)
const compteNavItems: NavItem[] = [
  { href: "/compte", icon: User, label: "Mon Profil" },
  { href: "/compte/mes-biens", icon: Building2, label: "Mes Biens" },
  { href: "/compte/favoris", icon: Home, label: "Favoris" },
  { href: "/compte/alertes", icon: Bell, label: "Alertes" },
  { href: "/compte/parametres", icon: Settings, label: "Param√®tres" },
];

interface SidebarContentProps {
  isCollapsed: boolean;
  onCollapse?: () => void;
  isMobile?: boolean;
  onMobileNavigate?: () => void;
  teams?: WorkspaceTeamData[];
  currentTeamId?: string;
  onSwitchTeam?: (teamId: string) => Promise<void>;
  onRequestAccess?: (permission: TeamPermissionKey, label: string) => void;
  badgeCounts?: { unreadMessages: number; pendingMaintenance: number };
}

function SidebarContent({
  isCollapsed,
  onCollapse,
  isMobile = false,
  onMobileNavigate,
  teams = [],
  currentTeamId,
  onSwitchTeam,
  onRequestAccess,
  badgeCounts = { unreadMessages: 0, pendingMaintenance: 0 },
}: SidebarContentProps) {
  const pathname = usePathname();
  const isGestionRoute = pathname?.startsWith("/gestion");

  // Trouver l'√©quipe courante pour afficher son nom
  const currentTeam = teams.find((t) => t.id === currentTeamId);

  // D√©terminer le contexte et les items de navigation
  const { navItems, title } = useMemo(() => {
    if (pathname?.startsWith("/gestion")) {
      // Utiliser le nom de l'√©quipe/agence si disponible, sinon fallback
      const teamName = currentTeam?.name || "Gestion Locative";
      return { navItems: gestionNavItems, title: teamName };
    }
    if (pathname?.startsWith("/locataire")) {
      return { navItems: locataireNavItems, title: "Espace Locataire" };
    }
    if (pathname?.startsWith("/admin")) {
      return { navItems: adminNavItems, title: "Administration" };
    }
    return { navItems: compteNavItems, title: "Mon Compte" };
  }, [pathname, currentTeam]);

  return (
    <div className="flex flex-col h-full">
      {/* Header Sidebar */}
      <div className={cn(
        "h-14 flex items-center justify-between border-b border-border shrink-0",
        // Consistent left padding for title or back button
        "pl-4 pr-2"
      )}>
        {(!isCollapsed || isMobile) && (
          <span className="text-sm font-semibold text-foreground truncate">
            {title}
          </span>
        )}

        {isMobile && (
          <SheetClose asChild>
            <Button variant="ghost" size="icon" className="h-8 w-8 shrink-0 text-foreground">
              <X className="h-4 w-4" />
            </Button>
          </SheetClose>
        )}

        {!isMobile && (
          <Button
            variant="ghost"
            size="icon"
            className="h-8 w-8 shrink-0 text-foreground"
            onClick={onCollapse}
          >
            <ChevronLeft className={cn(
              "h-4 w-4 transition-transform",
              isCollapsed && "rotate-180"
            )} />
          </Button>
        )}
      </div>

      {/* Team Switcher (only for /gestion routes) */}
      {isGestionRoute && teams.length > 0 && (
        <div className={cn(
          "p-2 border-b border-border shrink-0",
          isCollapsed && !isMobile && "flex items-center justify-center"
        )}>
          <TeamSwitcher
            teams={teams}
            currentTeamId={currentTeamId || teams[0]?.id}
            isCollapsed={isCollapsed && !isMobile}
            onSwitchTeam={onSwitchTeam}
          />
        </div>
      )}

      {/* Navigation */}
      <nav className="flex-1 overflow-y-auto p-2 space-y-1">
        {navItems.map((item) => {
          // Compute badge for this item
          const badgeCount = item.href === "/gestion/messages"
            ? badgeCounts.unreadMessages
            : item.href === "/gestion/interventions"
              ? badgeCounts.pendingMaintenance
              : 0;
          const isActive = pathname === item.href ||
            (item.href !== "/gestion" &&
              item.href !== "/locataire" &&
              item.href !== "/admin" &&
              item.href !== "/compte" &&
              pathname?.startsWith(`${item.href}/`));

          // Utiliser LockedSidebarItem pour les items avec permission requise OU tier requis
          if ((item.requiredPermission || item.requiredTier) && isGestionRoute) {
            return (
              <LockedSidebarItem
                key={item.href}
                href={item.href}
                icon={item.icon}
                label={item.label}
                isActive={isActive}
                isCollapsed={isCollapsed}
                isMobile={isMobile}
                requiredPermission={item.requiredPermission}
                requiredTier={item.requiredTier}
                currentTeamId={currentTeamId}
                currentTeamTier={currentTeam?.subscription_tier}
                currentTeamStatus={currentTeam?.subscription_status}
                onNavigate={() => isMobile && onMobileNavigate?.()}
                onRequestAccess={onRequestAccess}
                badgeCount={badgeCount}
              />
            );
          }

          // Item normal sans restriction
          return (
            <Link
              key={item.href}
              href={item.href}
              prefetch={false} // Disable prefetch to prevent stale RSC cache
              onClick={() => isMobile && onMobileNavigate?.()}
              className={cn(
                "flex items-center rounded-lg transition-all duration-200 group h-11",
                "px-[14px]",
                isActive
                  ? "bg-[#0F172A] text-white shadow-md font-medium dark:bg-primary/10 dark:text-primary"
                  : "text-muted-foreground hover:bg-accent hover:text-accent-foreground"
              )}
              title={isCollapsed && !isMobile ? item.label : undefined}
            >
              {(() => {
                const Icon = item.icon;
                return (
                  <span className="relative shrink-0">
                    <Icon className={cn(
                      "h-5 w-5 transition-all",
                      isActive ? "text-white dark:text-primary" : "text-muted-foreground group-hover:text-foreground"
                    )} />
                    {badgeCount > 0 && isCollapsed && !isMobile && (
                      <span className="absolute -top-1.5 -right-2 flex h-4 min-w-4 items-center justify-center rounded-full bg-red-500 px-0.5 text-[9px] font-bold text-white">
                        {badgeCount > 9 ? "9+" : badgeCount}
                      </span>
                    )}
                  </span>
                );
              })()}
              {(!isCollapsed || isMobile) && (
                <>
                  <span className="text-sm truncate ml-3 flex-1">{item.label}</span>
                  {badgeCount > 0 && (
                    <span className="flex h-5 min-w-5 items-center justify-center rounded-full bg-red-500 px-1.5 text-[10px] font-bold text-white ml-auto">
                      {badgeCount > 9 ? "9+" : badgeCount}
                    </span>
                  )}
                </>
