/**
 * üîÑ Mise √† jour d'une propri√©t√© avec invalidation
 *
 * CRUCIAL : Quand un propri√©taire modifie son bien, on doit :
 * 1. Mettre √† jour la DB
 * 2. Invalider TOUS les caches concern√©s
 */
export async function updateProperty(id: string, newData: any) {
  const supabase = await createClient();

  // 1. Mise √† jour DB (Supabase)
  const { error } = await supabase.from('properties').update(newData).eq('id', id);

  if (error) throw error;

  // 2. INVALIDATION IMM√âDIATE (Le "Chef crie" üì¢)

  const keysToInvalidate = [
    'all_properties_public', // Liste globale
    `detail:${id}`, // D√©tail de ce bien
  ];

  // Si la ville a chang√©, invalider les deux villes
  if (newData.city) {
    keysToInvalidate.push(`city:${newData.city}`);
  }

  // Si le statut a chang√© (published/draft), invalider liste
  if (newData.status) {
    keysToInvalidate.push('all_properties_public');
  }

  await invalidateCacheBatch(keysToInvalidate, 'properties');

  console.log(`‚úÖ Property ${id} updated and cache invalidated`);
  return { success: true };
}

