/**
 * üí∞ Paiement de loyer avec protection contre double paiement
 *
 * Sc√©nario :
 * - User clique 2 fois vite sur "Payer"
 * - Sans verrou : 2 paiements cr√©√©s ‚ùå
 * - Avec verrou : 2√®me clic rejet√© ‚úÖ
 */
export async function payRent(leaseId: string, amount: number) {
  // On utilise withLock pour auto-gestion du verrou
  return withLock(
    `payment:${leaseId}`,
    async () => {
      const supabase = await createClient();

      // 1. V√©rifier que le bail existe et n'est pas d√©j√† pay√© ce mois
      const { data: lease } = await supabase
        .from('leases')
        .select('*')
        .eq('id', leaseId)
        .single();

      if (!lease) {
        throw new Error('Bail introuvable');
      }

      // 2. Cr√©er le paiement dans PayDunya
      // ... (votre logique existante)

      // 3. Enregistrer dans la DB
      const { data: payment, error } = await supabase
        .from('rental_payments')
        .insert({
          lease_id: leaseId,
          amount,
          status: 'pending',
          payment_date: new Date().toISOString(),
        })
        .select()
        .single();

      if (error) throw error;

      // 4. Invalider le cache des paiements du locataire
      await invalidateCache(`payments:lease:${leaseId}`, 'rentals');

      return { paymentId: payment.id };
    },
    {
      expireSeconds: 30, // Max 30s pour traiter le paiement
      retries: 1, // Ne pas r√©essayer (c'est volontaire)
    }
  );
}

