/**
 * üìÖ R√©servation de visite avec protection contre double booking
 */
export async function bookVisit(propertyId: string, slot: string, userId: string) {
  return withLock(
    `visit:${propertyId}:${slot}`,
    async () => {
      const supabase = await createClient();

      // 1. V√©rifier que le cr√©neau est encore disponible
      const { data: existingBooking } = await supabase
        .from('visit_bookings')
        .select('*')
        .eq('property_id', propertyId)
        .eq('slot', slot)
        .maybeSingle();

      if (existingBooking) {
        throw new Error('Ce cr√©neau est d√©j√† r√©serv√©');
      }

      // 2. Cr√©er la r√©servation
      const { data: booking, error } = await supabase
        .from('visit_bookings')
        .insert({
          property_id: propertyId,
          user_id: userId,
          slot,
          status: 'confirmed',
        })
        .select()
        .single();

      if (error) throw error;

      // 3. Invalider le cache des cr√©neaux disponibles
      await invalidateCache(`available_slots:${propertyId}`, 'visits');

      return { bookingId: booking.id };
    },
    {
      expireSeconds: 10, // Tr√®s rapide pour la r√©servation
      retries: 3, // On peut r√©essayer
    }
  );
}

