    totalCollected: number; // Bas√© sur les versements r√©els (amount_paid ou amount_due si paid)
    totalExpenses: number;  // Somme des d√©penses (expenses)
    actualNetProfit: number; // Cashflow r√©el (Collected - Expenses)
    projectedNetProfit: number; // Performance projet√©e (Expected - Expenses)
    hasTemporaryDebt: boolean; // True si Expenses > Collected
