    paidCount: number;      // Nombre de loyers pay√©s
    pendingAmount: number;  // Montant en attente (FCFA)
    overdueAmount: number;  // Montant en retard (FCFA)
    avgDelayDays: number;   // Retard moyen (jours)
}

export interface MonthlyFinancialSummary extends FinancialKPIs {
    month: number;
    year: number;
    future: number;         // Loyers √† venir
}

// Interfaces minimales pour d√©couplage
export interface LeaseInput {
    id: string;
    monthly_amount: number;
    status: string; // 'active' | 'terminated' | 'pending' usually (string from DB)
    start_date: string | null;
    billing_day: number; // Used for overdue calculation
}

export interface TransactionInput {
    id: string; // Pour unicit√©
    lease_id: string | null;
    amount_due: number;
    amount_paid?: number | null; // La cl√© du correctif
    status: string; // 'pending' | 'paid' | 'overdue' etc.
    period_date?: string | Date; // Date de r√©f√©rence "due_date" ou p√©riode
    created_at?: string;
    paid_at?: string | Date; // Date du paiement effectif
}

export interface ExpenseInput {
    id: string;
    amount: number;
    expense_date: string;
    lease_id?: string | null;
}

/**
 * Calcule les KPI financiers pour un mois donn√©
 * Source de v√©rit√© : Lease (Bail) pour le d√ª, Transaction pour l'encaiss√©.
 */
export function calculateFinancials(
    leases: LeaseInput[],
    transactions: TransactionInput[],
    expenses: ExpenseInput[],
    targetDate: Date
): FinancialKPIs {

    let totalExpected = 0;
    let totalCollected = 0;
    let totalExpenses = 0;

    let paidCount = 0;
    let pendingCount = 0;
    let overdueCount = 0;

    let pendingAmount = 0;
    let overdueAmount = 0;

    const GRACE_PERIOD_DAYS = 5; // Tol√©rance avant de marquer "Overdue"

    let totalDelayDays = 0;
    let delaySamples = 0;

    // Pour v√©rifier si le mois cible est le mois courant
    const now = new Date();
    // Comparaison stricte mois/ann√©e
    const _isTargetMonthCurrent = (
        targetDate.getMonth() === now.getMonth() &&
        targetDate.getFullYear() === now.getFullYear()
    );

    // 1. It√©ration sur les BAUX (Source de v√©rit√© pour l'Attendu)
    leases.forEach(lease => {
        if (lease.status === 'active') {

            // R√®gle 1 : L'attendu vient du bail
            const monthlyRent = Number(lease.monthly_amount) || 0;
            totalExpected += monthlyRent;

            // 2. Chercher la transaction correspondante
            const leaseTransaction = transactions.find(t => t.lease_id === lease.id);
            const billingDay = lease.billing_day || 5;

            // Dates cl√©s
            const dueDate = new Date(targetDate.getFullYear(), targetDate.getMonth(), billingDay);
            const graceDate = new Date(dueDate);
            graceDate.setDate(dueDate.getDate() + GRACE_PERIOD_DAYS);

            // Pour le calcul de retard courant ("maintenant")
            const checkDate = new Date();

            if (leaseTransaction) {
                // R√®gle 2 : On prend ce qui a √©t√© R√âELLEMENT pay√©
                let paidAmount = 0;
                // Support partiel : si status 'paid', on suppose tout pay√© sauf si amount_paid d√©fini
                if (leaseTransaction.amount_paid !== undefined && leaseTransaction.amount_paid !== null) {
                    paidAmount = Number(leaseTransaction.amount_paid);
                } else if (leaseTransaction.status === 'paid') {
                    paidAmount = Number(leaseTransaction.amount_due) || monthlyRent;
                }

                totalCollected += paidAmount;
                const remainingDue = Math.max(0, monthlyRent - paidAmount);

                // --- LOGIQUE DE STATUT STRICT ---
                const isPaidFull = remainingDue <= 0;

                if (isPaidFull) {
                    paidCount++;
                    // Calcul d√©lai (si pay√©)
                    if (leaseTransaction.paid_at) {
                        const paidDate = new Date(leaseTransaction.paid_at);
                        // On ne compte que les RETARDS (diff > 0)
                        if (paidDate > dueDate) {
                            const diffTime = paidDate.getTime() - dueDate.getTime();
                            const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
                            const safeDelay = Math.max(0, diffDays);

                            if (safeDelay > 0) {
                                totalDelayDays += safeDelay;
                                delaySamples++;
                            }
                        } else {
                            // Pay√© √† l'heure ou en avance : On NE COMPTE PAS dans la moyenne des RETARDS
                            // (Selon feedback user : ne pas fausser la moyenne avec des 0)
                        }
                    } else if (leaseTransaction.status === 'paid') {
                        // Pay√© sans date ? On suppose √† l'heure => 0 retard
                        delaySamples++;
                    }
                } else {
                    // Partiel ou Pas pay√©
                    // Overdue ?
                    let isOverdue = false;

                    if (isAfter(new Date(), graceDate)) {
                        // Grace period d√©pass√©e -> Overdue
                        isOverdue = true;
                    }
                    // Si futur -> Pas overdue
                    if (isAfter(startOfMonth(targetDate), endOfMonth(checkDate))) {
                        isOverdue = false;
                    }

                    if (isOverdue) {
                        overdueCount++;
                        overdueAmount += remainingDue;

                        // Retard en cours
                        const diffTime = checkDate.getTime() - dueDate.getTime();
                        const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
                        totalDelayDays += diffDays;
                        delaySamples++;
                    } else {
                        pendingCount++;
                        pendingAmount += remainingDue;
                    }
                }
            } else {
                // Pas de transaction
                const isActiveDelay = isAfter(new Date(), graceDate);
                const isFuture = isAfter(startOfMonth(targetDate), endOfMonth(checkDate));

                if (isActiveDelay && !isFuture) {
                    overdueCount++;
                    overdueAmount += monthlyRent;
                    // Retard en cours
                    const diffTime = checkDate.getTime() - dueDate.getTime();
                    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
                    totalDelayDays += diffDays;
                    delaySamples++;
                } else {
                    pendingCount++;
                    pendingAmount += monthlyRent;
                }
            }
        }
    });

    // 4. Calcul des D√âPENSES
    expenses.forEach(expense => {
        totalExpenses += Number(expense.amount) || 0;
    });

    // Calcul des ratios et profits
    const collectionRate = totalExpected > 0
        ? Math.round((totalCollected / totalExpected) * 100)
        : 0;

    const actualNetProfit = totalCollected - totalExpenses;
    const projectedNetProfit = totalExpected - totalExpenses;
    const hasTemporaryDebt = totalExpenses > totalCollected;

    // Calcul D√©lai Moyen
    const avgDelayDays = delaySamples > 0
        ? Math.round(totalDelayDays / delaySamples)
        : 0;

    return {
        totalExpected,
        totalCollected,
        totalExpenses,
        actualNetProfit,
        projectedNetProfit,
        hasTemporaryDebt,
        collectionRate,
        occupancyRate: 0,
        pendingCount,
        overdueCount,
        paidCount,
        pendingAmount,
        overdueAmount,
        avgDelayDays
    };
}

/**
 * Calcule les KPI pour toute une ann√©e (breakdown mensuel)
 */
export function calculateYearlyFinancials(
    leases: LeaseInput[],
    transactions: TransactionInput[],
    expenses: ExpenseInput[],
    year: number
): MonthlyFinancialSummary[] {
    const months: MonthlyFinancialSummary[] = [];
    const today = new Date();
    const currentMonth = today.getMonth() + 1;
    const currentYear = today.getFullYear();

    for (let month = 1; month <= 12; month++) {
        const targetDate = new Date(year, month - 1, 1);

        // Filtrer les transactions pour ce mois sp√©cifique
        const monthlyTransactions = transactions.filter(t => {
            if (t.period_date) {
                const d = typeof t.period_date === 'string' ? new Date(t.period_date) : t.period_date;
                return d.getMonth() + 1 === month && d.getFullYear() === year;
            }
            return (t as any).period_month === month && (t as any).period_year === year;
        });

        // Filtrer les d√©penses pour ce mois sp√©cifique
        const monthlyExpenses = (expenses || []).filter(e => {
            const d = new Date(e.expense_date);
            return d.getMonth() + 1 === month && d.getFullYear() === year;
        });

        const financials = calculateFinancials(leases, monthlyTransactions, monthlyExpenses, targetDate);

        // Gestion sp√©cifique du "Futur"
        let future = 0;
        const isFuture = year > currentYear || (year === currentYear && month > currentMonth);

        if (isFuture) {
            // Dans le futur, tout ce qui est "attendu" est consid√©r√© comme "future"
            // et on remet √† z√©ro les autres compteurs pour la clart√© visuelle
            future = financials.totalExpected;
            financials.pendingCount = 0;
            financials.overdueCount = 0;
            financials.pendingAmount = 0;
            financials.overdueAmount = 0;
        }

        months.push({
            ...financials,
            month,
            year,
            future
        });
    }

    return months;
}

/**
 * Calcule le statut d'affichage d'une transaction (synchronis√© avec l'UI)
 *
 * @param transactionStatus - Statut en base de donn√©es
 * @param billingDay - Jour de facturation (d√©faut: 5)
 * @param isCurrentMonth - Si on regarde le mois en cours
 * @returns 'paid' | 'pending' | 'overdue'
 */
export function calculateDisplayStatus(
    transactionStatus: string,
    billingDay: number = 5,
    isCurrentMonth: boolean = true
): 'paid' | 'pending' | 'overdue' {
    if (transactionStatus === 'paid') {
        return 'paid';
    }

    // Pour les transactions non pay√©es, v√©rifier si en retard
    const currentDay = new Date().getDate();
    if (isCurrentMonth && currentDay > billingDay) {
        return 'overdue';
    }

    return 'pending';
}

/**
 * PILIER 2 : Le Gardien (Write-Security)
 * V√©rifie strictement si un email est d√©j√† utilis√© pour un bail ACTIF
 */
export async function validateTenantCreation(
    email: string,
    supabaseClient: SupabaseClient,
    ownerId: string
): Promise<{ valid: boolean; error?: string }> {
    if (!email) return { valid: true }; // Pas d'email, pas de conflit

    const { data: existingLeases, error } = await supabaseClient
        .from('leases')
        .select('id, tenant_name')
        .eq('owner_id', ownerId)
        .ilike('tenant_email', email.trim())
        .neq('status', 'terminated') // On ignore les anciens baux r√©sili√©s
        .limit(1);

    if (error) {
        console.error("FinanceGuard Error:", error);
        return { valid: false, error: "Erreur de v√©rification des doublons" };
    }

    if (existingLeases && existingLeases.length > 0) {
        return {
            valid: false,
            error: `L'adresse email choisie est d√©j√† utilis√©e par "${existingLeases[0].tenant_name}". Veuillez en choisir une autre.`
        };
    }

    return { valid: true };
}

import { createClient } from '@/utils/supabase/server';

type DocumentType = 'titre_propriete' | 'bail' | 'cni' | 'facture' | 'attestation' | 'autre' | 'quittance' | 'etat_lieux' | 'facture_travaux' | 'maintenance';

interface StoreDocumentParams {
    userId: string; // Propri√©taire ou Locataire principal
    teamId?: string;
    fileBuffer: Uint8Array;
    fileName: string;
    bucketName: 'lease-contracts' | 'receipts' | 'user-documents' | 'properties';
    documentType: DocumentType;
    metadata: {
        leaseId?: string;
        propertyId?: string;
        tenantName?: string;
        transactionId?: string; // Pour les quittances
        requestId?: string; // Pour les maintenance
        description?: string;
    }
}

/**
 * Upload un fichier buffer vers Supabase Storage et cr√©e l'entr√©e user_documents
 */
export async function storeDocumentInGED(params: StoreDocumentParams, supabaseClient?: any) {
    // Si un client est pass√© (ex: avec service role), on l'utilise, sinon on en cr√©e un nouveau
    const supabase = supabaseClient || await createClient();
    const {
        userId, teamId, fileBuffer, fileName, bucketName, documentType, metadata
    } = params;

    try {
        // 1. Upload vers Storage
        // On assainit le nom de fichier
        const sanitizedFileName = fileName.replace(/[^a-zA-Z0-9-_\.]/g, '_');
        const filePath = `${userId}/${Date.now()}_${sanitizedFileName}`;

        let { data: uploadData, error: uploadError } = await supabase.storage
            .from(bucketName)
            .upload(filePath, fileBuffer, {
                contentType: 'application/pdf',
                upsert: true
            });

        // Retry: Auto-create bucket if missing (requires admin/service_role client)
        if (uploadError && uploadError.message.includes("Bucket not found")) {
            console.log(`‚ö†Ô∏è Bucket '${bucketName}' introuvable. Tentative de cr√©ation...`);
            const { error: createError } = await supabase.storage.createBucket(bucketName, {
                public: false,
                fileSizeLimit: 52428800, // 50MB
                allowedMimeTypes: ['application/pdf', 'image/png', 'image/jpeg']
            });

            if (createError) {
                console.error("‚ùå √âchec cr√©ation bucket:", createError);
                return { success: false, error: "Bucket introuvable et impossible √† cr√©er: " + createError.message };
            }

            // Retry upload
            const retry = await supabase.storage
                .from(bucketName)
                .upload(filePath, fileBuffer, {
                    contentType: 'application/pdf',
                    upsert: true
                });

            uploadData = retry.data;
            uploadError = retry.error;
        }

        if (uploadError) {
            console.error("Erreur Upload Storage:", uploadError);
            return { success: false, error: uploadError.message };
        }

        // 2. G√©n√©rer l'URL publique ou sign√©e (selon le bucket)
        // Pour 'properties' c'est public, pour 'lease-contracts' priv√©
        let fileUrl = "";

        if (bucketName === 'properties') {
            const { data: publicUrlData } = supabase.storage
                .from(bucketName)
                .getPublicUrl(filePath);
            fileUrl = publicUrlData.publicUrl;
        } else {
            // Pour les buckets priv√©s, on stocke le chemin interne et on g√©n√©rera des URLs sign√©es √† la vol√©e
            // OU on g√©n√®re une URL sign√©e longue dur√©e (mais risqu√©).
            // Mieux : Stocker le chemin relatif ou l'URL interne qui sera trait√©e par le client/serveur
            // Ici on va stocker le path complet pour r√©f√©rence
            fileUrl = uploadData.path;
        }

        // 3. Cr√©er l'entr√©e dans user_documents
        let { data: doc, error: dbError } = await supabase
            .from('user_documents')
            .insert([{
                user_id: userId,
                description: metadata.description || fileName,
                file_name: fileName,
                file_type: documentType, // Utilise la colonne file_type d√©finie dans la migration
                file_path: uploadData.path,
                file_size: fileBuffer.length,
                mime_type: 'application/pdf',
                entity_type: documentType === 'bail' ? 'lease' : documentType === 'quittance' ? 'payment' : documentType === 'maintenance' ? 'maintenance_request' : 'other',
                entity_id: documentType === 'quittance' ? metadata.transactionId : documentType === 'maintenance' ? metadata.requestId : (metadata.leaseId || metadata.transactionId),
                property_id: metadata.propertyId,
                lease_id: metadata.leaseId,
                category: documentType, // On utilise documentType comme cat√©gorie par d√©faut
                source: 'manual',
                created_at: new Date().toISOString()
            }])
            .select()
            .single();

        // 3b. Fallback automatique agressif si l'insertion √©choue pour N'IMPORTE quelle raison (contrainte, colonne manquante, etc.)
        if (dbError) {
            console.warn("‚ö†Ô∏è Fallback GED: √âchec insertion standard, tentative en mode compatibilit√© minimale.", dbError.message);

            // On tente une insertion avec UNIQUEMENT les champs pr√©sents dans la version de base (20251224)
            // file_type doit √™tre 'autre' (car 'maintenance' peut ne pas exister)
            // source doit √™tre 'manual' (car 'generated' peut ne pas exister)
            // Pas de description, category, entity_type, etc.
            const { data: retryDoc, error: retryError } = await supabase
                .from('user_documents')
                .insert([{
                    user_id: userId,
                    // description: metadata.description || fileName, // On retire description au cas o√π
                    file_name: fileName,
                    file_type: 'autre',
                    file_path: uploadData.path,
                    file_size: fileBuffer.length,
                    mime_type: 'application/pdf',
                    // source: 'generated', // On retire generated au cas o√π
                    source: 'manual',
                    created_at: new Date().toISOString()
                }])
                .select()
                .single();

            // Si le fallback r√©ussit, on √©crase l'erreur et le doc
            if (!retryError) {
                console.log("‚úÖ Fallback GED r√©ussi (mode compatibilit√©).");
                doc = retryDoc;
                dbError = null;
            } else {
                console.error("‚ùå Echec total GED (m√™me en fallback):", retryError);
            }
        }

        if (dbError) {
            console.error("Erreur Insert DB user_documents:", dbError);
            // On pourrait delete le fichier upload√©, mais bon...
            return { success: false, error: dbError.message };
        }

        return { success: true, document: doc, filePath: uploadData.path, fileUrl };

    } catch (error) {
        console.error("Exception storeDocumentInGED:", error);
        return { success: false, error: error instanceof Error ? error.message : "Erreur inconnue" };
    }
}

"use client";

/**
 * useFavoritesSync Hook
 *
 * Handles synchronization of localStorage favorites to server after login.
 *
 * Features:
 * - Auto-detects localStorage favorites on mount
 * - Triggers sync after login/registration
 * - Shows toast notifications for sync results
 * - Merges server favorites back to localStorage
 *
 * Per WORKFLOW_PROPOSAL.md sections 494-583
 */

import { useEffect, useCallback, useRef } from "react";
import { useRouter } from "next/navigation";
import { toast } from "sonner";
import { useFavoritesStore } from "@/store/use-store";
import {
  syncFavoritesAction,
  getServerFavoritesAction,
  FAVORITES_LIMITS,
} from "@/lib/favorites-sync";

const SYNC_FLAG_KEY = "dousell-favorites-synced";

interface UseFavoritesSyncOptions {
  /** User ID if authenticated */
  userId?: string | null;
  /** Trigger sync immediately (e.g., after login) */
  triggerSync?: boolean;
  /** Show toast notifications */
  showToasts?: boolean;
}

interface UseFavoritesSyncReturn {
  /** Manually trigger sync */
  syncNow: () => Promise<void>;
  /** Check if should prompt login */
  shouldPromptLogin: boolean;
  /** Number of local favorites */
  localCount: number;
}

export function useFavoritesSync(options: UseFavoritesSyncOptions = {}): UseFavoritesSyncReturn {
  const { userId, triggerSync = false, showToasts = true } = options;
  const _router = useRouter();
  const { favorites, addFavorite, removeFavorite } = useFavoritesStore();
  const syncInProgress = useRef(false);
  const hasSynced = useRef(false);

  const localCount = favorites.length;
  const shouldPromptLogin = !userId && localCount >= FAVORITES_LIMITS.maxLocalStoragePrompt;

  /**
   * Sync localStorage favorites to server
   */
  const syncNow = useCallback(async () => {
    if (!userId || syncInProgress.current) return;

    // Check if already synced this session
    const syncedFlag = sessionStorage.getItem(SYNC_FLAG_KEY);
    if (syncedFlag === userId && !triggerSync) {
      return;
    }

    syncInProgress.current = true;

    try {
      const localIds = favorites.map((p) => p.id);

      // Skip if no local favorites
      if (localIds.length === 0) {
        // Still fetch server favorites to merge
        const serverIds = await getServerFavoritesAction();
        if (serverIds.length > 0 && showToasts) {
          toast.info(`${serverIds.length} favoris restaur√©s depuis le serveur`);
        }
        sessionStorage.setItem(SYNC_FLAG_KEY, userId);
        return;
      }

      // Sync local to server
      const result = await syncFavoritesAction(localIds);

      if (result.rateLimited) {
        if (showToasts) {
          toast.error(result.error);
        }
        return;
      }

      if (result.success) {
        // Mark as synced
        sessionStorage.setItem(SYNC_FLAG_KEY, userId);

        // Show notification
        if (showToasts) {
          if (result.synced > 0) {
            toast.success(`${result.synced} favoris synchronis√©s`);
          }
          if (result.trimmed > 0) {
            toast.info(`${result.trimmed} favoris non synchronis√©s (limite atteinte)`);
          }
        }

        // Fetch server favorites to get complete list
        const _serverIds = await getServerFavoritesAction();

        // We keep localStorage for offline access, but server is source of truth
        // Clear sync flag to prevent re-sync on refresh
        hasSynced.current = true;
      } else if (result.error && showToasts) {
        toast.error(result.error);
      }
    } catch (error) {
      console.error("[useFavoritesSync] Sync error:", error);
      if (showToasts) {
        toast.error("Erreur de synchronisation des favoris");
      }
    } finally {
      syncInProgress.current = false;
    }
  }, [userId, favorites, showToasts, triggerSync]);

  // Auto-sync on mount if authenticated and has local favorites
  useEffect(() => {
    if (userId && favorites.length > 0 && !hasSynced.current) {
      // Small delay to avoid sync during navigation
      const timer = setTimeout(() => {
        syncNow();
      }, 1000);
      return () => clearTimeout(timer);
    }
  }, [userId, syncNow, favorites.length]);

  // Trigger sync immediately if requested
  useEffect(() => {
    if (triggerSync && userId) {
      syncNow();
    }
  }, [triggerSync, userId, syncNow]);

  return {
    syncNow,
    shouldPromptLogin,
    localCount,
  };
}

/**
 * Hook for showing login prompt when favorites limit reached
 */
export function useFavoritesLoginPrompt() {
  const router = useRouter();
  const { favorites } = useFavoritesStore();

  const promptLogin = useCallback(() => {
    toast.info("Connectez-vous pour sauvegarder vos favoris", {
      action: {
        label: "Se connecter",
        onClick: () => router.push("/login?next=/favoris"),
      },
      duration: 5000,
    });
  }, [router]);

  const checkAndPrompt = useCallback(
    (currentCount: number) => {
      // Prompt after 3 favorites added
      if (currentCount === 3 || currentCount === 5 || currentCount === FAVORITES_LIMITS.maxLocalStoragePrompt) {
        promptLogin();
      }
    },
    [promptLogin]
  );

  return {
    promptLogin,
    checkAndPrompt,
    shouldPrompt: favorites.length >= FAVORITES_LIMITS.maxLocalStoragePrompt,
  };
}

"use client";

import { create } from "zustand";
import { submitAccessRequest } from "@/app/(workspace)/gestion/access-control/actions";

interface AccessModalState {
  isOpen: boolean;
  featureName: string;
  permissionKey: string;
  isSubmitting: boolean;
  reason: string;

  // Actions
  openRequestModal: (featureName: string, permissionKey: string) => void;
  close: () => void;
  setReason: (reason: string) => void;
  submitRequest: (teamId: string) => Promise<{ success: boolean; error?: string }>;
}

export const useAccessModal = create<AccessModalState>((set, get) => ({
  isOpen: false,
  featureName: "",
  permissionKey: "",
  isSubmitting: false,
  reason: "",

  openRequestModal: (featureName: string, permissionKey: string) => {
    set({
      isOpen: true,
      featureName,
      permissionKey,
      reason: "",
    });
  },

  close: () => {
    set({
      isOpen: false,
      featureName: "",
      permissionKey: "",
      reason: "",
      isSubmitting: false,
    });
  },

  setReason: (reason: string) => {
    set({ reason });
  },

  submitRequest: async (teamId: string) => {
    const { permissionKey, reason } = get();

    set({ isSubmitting: true });

    try {
      const result = await submitAccessRequest({
        teamId,
        permission: permissionKey,
        reason: reason || undefined,
      });

      if (result.data?.success) {
        get().close();
        return { success: true };
      }

      return { success: false, error: result.error || "Erreur lors de la demande" };
    } catch (error: any) {
      return { success: false, error: error.message };
    } finally {
      set({ isSubmitting: false });
    }
  },
}));

"use client";

/**
 * Hook usePermission
 *
 * V√©rifie si l'utilisateur a une permission (via r√¥le ou permission temporaire)
 * et fournit une interface pour demander l'acc√®s si n√©cessaire.
 */

import { useState, useEffect, useCallback } from 'react';
import type { TeamPermissionKey } from '@/lib/team-permissions';
import { useTeam } from '@/lib/hooks/useTeam';
import { createClient } from '@/utils/supabase/client';
import { submitAccessRequest } from '@/app/(workspace)/gestion/access-control/actions';

export interface UsePermissionResult {
    /**
     * Si l'utilisateur a la permission (via r√¥le ou temporaire)
     */
    hasPermission: boolean;

    /**
     * Si la v√©rification est en cours
     */
    isLoading: boolean;

    /**
     * Erreur lors de la v√©rification
     */
    error: string | null;

    /**
     * Si une permission temporaire est active
     */
    hasTemporaryAccess: boolean;

    /**
     * Date d'expiration de la permission temporaire (si applicable)
     */
    temporaryAccessExpiresAt: Date | null;

    /**
     * Demander l'acc√®s temporaire
     */
    requestAccess: (reason?: string) => Promise<{ success: boolean; error?: string }>;

    /**
     * Rafra√Æchir le statut de la permission
     */
    refresh: () => Promise<void>;
}

/**
 * Hook pour v√©rifier et g√©rer les permissions
 *
 * @param permission - La permission √† v√©rifier
 * @param options - Options optionnelles
 * @returns UsePermissionResult
 *
 * @example
 * ```tsx
 * const { hasPermission, requestAccess, isLoading } = usePermission('leases.edit');
 *
 * if (isLoading) return <Spinner />;
 *
 * if (!hasPermission) {
 *   return <Button onClick={() => requestAccess('J\'ai besoin d\'√©diter ce bail')}>
 *     Demander l'acc√®s
 *   </Button>;
 * }
 *
 * return <LeaseEditForm />;
 * ```
 */
export function usePermission(
    permission: TeamPermissionKey,
    options?: {
        /**
         * Si true, ne v√©rifie pas automatiquement au montage
         */
        manual?: boolean;
    }
): UsePermissionResult {
    const { team_id: teamId, user_id, user } = useTeam();
    const [hasPermission, setHasPermission] = useState(false);
    const [isLoading, setIsLoading] = useState(!options?.manual);
    const [error, setError] = useState<string | null>(null);
    const [hasTemporaryAccess, setHasTemporaryAccess] = useState(false);
    const [temporaryAccessExpiresAt, setTemporaryAccessExpiresAt] = useState<Date | null>(null);

    /**
     * V√©rifie la permission (r√¥le + temporaire)
     */
    const checkPermission = useCallback(async () => {
        if (!teamId || !user_id || !permission) {
            setHasPermission(false);
            setIsLoading(false);
            return;
        }

        setIsLoading(true);
        setError(null);

        try {
            const supabase = createClient();

            // 1. V√©rifier la permission du r√¥le (c√¥t√© client, simple check)
            // Note: Le vrai check est c√¥t√© serveur, mais on peut optimiser l'UX
            // en affichant directement si le r√¥le a la permission

            // 2. V√©rifier les permissions temporaires actives
            const { data: tempPermissions, error: tempError } = await supabase.rpc(
                'get_active_temporary_permissions',
                {
                    p_team_id: teamId,
                    p_user_id: user_id,
                }
            );

            if (tempError) {
                console.error('[usePermission] Error fetching temporary permissions:', tempError);
                setError(tempError.message);
                setHasPermission(false);
                return;
            }

            // Chercher si la permission demand√©e est dans les permissions temporaires
            const tempPerm = tempPermissions?.find((p: any) => p.permission === permission);

            if (tempPerm) {
                setHasTemporaryAccess(true);
                setTemporaryAccessExpiresAt(new Date(tempPerm.expires_at));
                setHasPermission(true);
            } else {
                setHasTemporaryAccess(false);
                setTemporaryAccessExpiresAt(null);

                // Si pas de permission temporaire, v√©rifier via le r√¥le
                // (On fait confiance au backend pour la vraie v√©rification)
                // Pour l'instant on assume que le user n'a pas la permission
                // √Ä am√©liorer avec un appel serveur si n√©cessaire
                setHasPermission(false);
            }
        } catch (err: any) {
            console.error('[usePermission] Unexpected error:', err);
            setError(err.message || 'Erreur inattendue');
            setHasPermission(false);
        } finally {
            setIsLoading(false);
        }
    }, [teamId, user_id, permission]);

    /**
     * Demander l'acc√®s temporaire
     */
    const requestAccess = useCallback(async (reason?: string) => {
        if (!teamId || !permission) {
            return { success: false, error: 'Contexte invalide' };
        }

        try {
            const result = await submitAccessRequest({
                teamId,
                permission,
                reason,
            });

            if (result.data?.success) {
                // Rafra√Æchir apr√®s la demande
                await checkPermission();
                return { success: true };
            }

            return { success: false, error: result.error || "Erreur lors de la demande" };
        } catch (err: any) {
            console.error('[usePermission] Request access error:', err);
            return {
                success: false,
                error: err.message || 'Erreur lors de la demande d\'acc√®s',
            };
        }
    }, [teamId, permission, checkPermission]);

    /**
     * Rafra√Æchir le statut
     */
    const refresh = useCallback(async () => {
        await checkPermission();
    }, [checkPermission]);

    // V√©rifier au montage (sauf si manual)
    useEffect(() => {
        if (!options?.manual) {
            checkPermission();
        }
    }, [checkPermission, options?.manual]);

    return {
        hasPermission,
        isLoading,
        error,
        hasTemporaryAccess,
        temporaryAccessExpiresAt,
        requestAccess,
        refresh,
    };
}

/**
 * Hook simplifi√© pour v√©rifier plusieurs permissions
 */
export function usePermissions(permissions: TeamPermissionKey[]) {
    // eslint-disable-next-line react-hooks/rules-of-hooks
    const results = permissions.map((permission) => usePermission(permission));

    return {
        hasAll: results.every((r) => r.hasPermission),
        hasAny: results.some((r) => r.hasPermission),
        isLoading: results.some((r) => r.isLoading),
        permissions: results,
    };
}

'use client';

import { useQuery } from '@tanstack/react-query';
import { createClient } from '@/utils/supabase/client';
import { useAuth } from '@/hooks/use-auth';

interface TeamContext {
    team_id: string | null;
    user_id: string | null;
    user: any;
    loading: boolean;
}

/**
 * Hook pour r√©cup√©rer l'ID de l'√©quipe de l'utilisateur actuel
 */
export function useTeam(): TeamContext {
    const { user, loading: authLoading } = useAuth();
    const supabase = createClient();

    const { data: teamMember, isLoading: teamLoading } = useQuery({
        queryKey: ['user-team', user?.id],
        queryFn: async () => {
            if (!user) return null;
            const { data, error } = await supabase
                .from('team_members')
                .select('team_id')
                .eq('user_id', user.id)
                .maybeSingle();

            if (error) throw error;
            return data;
        },
        enabled: !!user,
    });

    return {
        team_id: teamMember?.team_id || null,
        user_id: user?.id || null,
        user,
        loading: authLoading || teamLoading,
    };
}

import { createClient } from '@supabase/supabase-js';
import { Database } from '@/types/supabase';
import crypto from 'crypto';
import Fuse from 'fuse.js';

export type ResourceType = 'expense' | 'lease' | 'transaction';

export interface ImportRow {
    [key: string]: any;
}

export class ImportService {
    constructor(private supabase: ReturnType<typeof createClient<Database>>) { }

    /**
     * G√©n√®re un hash SHA256 unique pour une ligne de donn√©es afin d'√©viter les doublons.
     */
    generateImportHash(data: ImportRow, teamId: string): string {
        const rawString = JSON.stringify(data) + teamId;
        return crypto.createHash('sha256').update(rawString).digest('hex');
    }

    /**
     * Envoie les donn√©es dans la table de staging pour validation.
     */
    async stageData({
        teamId,
        userId,
        resourceType,
        rows
    }: {
        teamId: string;
        userId: string;
        resourceType: ResourceType;
        rows: ImportRow[];
    }) {
        const stagingEntries = rows.map(row => ({
            team_id: teamId,
            imported_by: userId,
            resource_type: resourceType,
            raw_data: row,
            import_hash: this.generateImportHash(row, teamId),
            status: 'pending' as const
        }));

        const { data, error } = await (this.supabase as any)
            .from('imports_staging')
            .upsert(stagingEntries, { onConflict: 'team_id, import_hash' })
            .select();

        if (error) throw error;
        return data;
    }

    /**
     * Normalise les donn√©es brutes selon le sch√©ma cible.
     * Note: Cette fonction sera enrichie avec la logique de fuzzy matching en Phase 2.
     */
    async standardizeData(id: string) {
        const { data: staged, error: fetchError } = await (this.supabase as any)
            .from('imports_staging')
            .select('*')
            .eq('id', id)
            .single();

        if (fetchError || !staged) throw fetchError || new Error('Not found');

        const raw = (staged as any).raw_data as any;
        let standardized: any = {};

        if ((staged as any).resource_type === 'expense') {
            standardized = {
                amount: parseFloat(raw.montant || raw.amount || 0),
                description: raw.description || raw.libelle || 'Import sans description',
                expense_date: raw.date || new Date().toISOString(),
                category: raw.categorie || raw.category || 'other',
                team_id: (staged as any).team_id,
                meta: {
                    import_source: 'staging',
                    original_data: raw
                }
            };
        }

        const { error: updateError } = await (this.supabase as any)
            .from('imports_staging')
            .update({
                standardized_data: standardized,
                status: 'validated'
            })
            .eq('id', id);

        if (updateError) throw updateError;
    }

    /**
     * Effectue un rapprochement flou (Fuzzy Match) pour trouver le bail ou le bien le plus probable.
     * Thresholds: > 0.95 (Auto-match), > 0.75 (Suggestion)
     */
    async performFuzzyMatch(stagingId: string) {
        const { data: staged, error: fetchError } = await (this.supabase as any)
            .from('imports_staging')
            .select('*')
            .eq('id', stagingId)
            .single();

        if (fetchError || !staged) throw fetchError || new Error('Not found');

        const raw = (staged as any).raw_data as any;
        const searchText = `${raw.description || ''} ${raw.property_name || ''} ${raw.tenant_name || ''}`;

        // 1. R√©cup√©rer les baux et biens de l'√©quipe pour le matching
        const { data: leases } = await this.supabase
            .from('leases')
            .select('id, tenant_name, property_address')
            .eq('team_id', (staged as any).team_id);

        if (!leases || leases.length === 0) return null;

        // 2. Configurer Fuse.js
        const fuse = new Fuse(leases, {
            keys: ['tenant_name', 'property_address'],
            includeScore: true,
            threshold: 0.4 // 1 - 0.4 = 0.6 match minimum pour Fuse (on affinera apr√®s)
        });

        const results = fuse.search(searchText);

        if (results.length > 0) {
            const bestMatch = results[0];
            const matchScore = 1 - (bestMatch.score || 1); // Normaliser 0-1

            // 3. Mise √† jour avec le meilleur match
            const updateData: any = {
                match_score: matchScore,
                matched_resource_id: bestMatch.item.id
            };

            // Auto-validation si score > 0.95
            if (matchScore > 0.95) {
                updateData.status = 'validated';
                // Mettre √† jour standardized_data avec le lease_id match√©
                const currentStandardized = (staged as any).standardized_data as any;
                if (currentStandardized) {
                    updateData.standardized_data = {
                        ...currentStandardized,
                        lease_id: bestMatch.item.id
                    };
                }
            }

            await (this.supabase as any)
                .from('imports_staging')
                .update(updateData)
                .eq('id', stagingId);

            return bestMatch;
        }

        return null;
    }

    /**
     * Transf√®re les donn√©es valid√©es du staging vers la production.
     */
    async commitImport(teamId: string, resourceType: ResourceType) {
        const { data: validatedEntries, error: fetchError } = await (this.supabase as any)
            .from('imports_staging')
            .select('*')
            .eq('team_id', teamId)
            .eq('resource_type', resourceType)
            .eq('status', 'validated');

        if (fetchError) throw fetchError;
        if (!validatedEntries || validatedEntries.length === 0) return { count: 0 };

        const table = resourceType === 'expense' ? 'expenses' :
            resourceType === 'lease' ? 'leases' : 'rental_transactions';

        const productionData = validatedEntries.map((e: any) => e.standardized_data);

        // Insertion atomique en production
        const { error: insertError } = await this.supabase
            .from(table as any)
            .insert(productionData);

        if (insertError) throw insertError;

        // Marquer comme 'committed'
        const { error: finalizeError } = await (this.supabase as any)
            .from('imports_staging')
            .update({ status: 'committed' })
            .in('id', validatedEntries.map((e: any) => e.id));

        if (finalizeError) throw finalizeError;

        return { count: validatedEntries.length };
    }

    /**
     * Action manuelle : Lie une d√©pense orpheline √† un bail sp√©cifique.
     * Ajoute une trace dans les metadata.
     */
    async linkExpenseToLease(expenseId: string, leaseId: string, userId: string) {
        // 1. R√©cup√©rer les infos du bail pour le team_id
        const { data: lease, error: leaseError } = await this.supabase
            .from('leases')
            .select('team_id')
            .eq('id', leaseId)
            .single();

        if (leaseError || !lease) throw leaseError || new Error('Lease not found');

        // 2. Mettre √† jour la d√©pense
        const { data: expense, error: fetchError } = await (this.supabase as any)
            .from('expenses')
            .select('meta')
            .eq('id', expenseId)
            .single();

        if (fetchError) throw fetchError;

        const currentMeta = ((expense as any)?.meta as any) || {};
        const correction = {
            type: 'manual_link',
            lease_id: leaseId,
            user_id: userId,
            timestamp: new Date().toISOString()
        };

        const newMeta = {
            ...currentMeta,
            user_corrections: [...(currentMeta.user_corrections || []), correction]
        };

        const { error: updateError } = await this.supabase
            .from('expenses')
            .update({
                lease_id: leaseId,
                team_id: lease.team_id,
                meta: newMeta
            })
            .eq('id', expenseId);

        if (updateError) throw updateError;
        return { success: true };
    }
}

import {
  sendEmail as sendEmailGmail,
  sendInvoiceEmail as sendInvoiceEmailGmail,
  getAdminEmail as getAdminEmailGmail,
  sendActivationApprovedEmail as sendActivationApprovedEmailGmail,
  sendActivationRejectedEmail as sendActivationRejectedEmailGmail
} from "./mail-gmail";
import { render } from "@react-email/render";
import { createClient as createSupabaseClient } from "@supabase/supabase-js";
import React from "react";

const ADMIN_EMAIL = process.env.ADMIN_EMAIL;
const SUPABASE_URL = process.env.NEXT_PUBLIC_SUPABASE_URL;
const SERVICE_ROLE_KEY = process.env.SUPABASE_SERVICE_ROLE_KEY;
const ADMIN_ROLES = ["admin", "moderateur", "superadmin"];

type SendEmailOptions = {
  to: string | string[];
  subject: string;
  react?: React.ReactElement;
  html?: string;
  from?: string; // Conserv√© pour compatibilit√©
  fromName?: string;
  user_id?: string | null;
  attachments?: Array<{
    filename: string;
    content: Buffer | string;
    contentType?: string;
  }>;
};

/**
 * Fonction g√©n√©rique pour envoyer des emails via Gmail (Nodemailer)
 * Utilise GMAIL_USER et GMAIL_APP_PASSWORD depuis les variables d'environnement
 */
export async function sendEmail({
  to,
  subject,
  react,
  html,
  fromName = "Dousell Immo",
  user_id = null,
  attachments,
}: SendEmailOptions) {
  // Si react est fourni, le convertir en HTML
  let emailHtml = html;
  if (react && !html) {
    emailHtml = await render(react);
  }

  return sendEmailGmail({
    to,
    subject,
    html: emailHtml,
    attachments,
  });
}

/**
 * Fonction pour envoyer une facture par email
 * @param to - Adresse email du destinataire
 * @param clientName - Nom du client
 * @param pdfBuffer - Buffer du PDF de la facture
 * @param invoiceNumber - Num√©ro de facture (optionnel)
 * @param amount - Montant de la facture (optionnel)
 */
export async function sendInvoiceEmail({
  to,
  clientName,
  pdfBuffer,
  invoiceNumber,
  amount,
}: {
  to: string;
  clientName: string;
  pdfBuffer: Buffer;
  invoiceNumber?: string;
  amount?: number;
}) {
  return sendInvoiceEmailGmail({
    to,
    clientName,
