    amount,
  });
}

/**
 * Email de l'admin (pour les notifications)
 */
export function getAdminEmail() {
  return getAdminEmailGmail() || ADMIN_EMAIL;
}

/**
 * Retourne la liste des emails des admins et mod√©rateurs pour les notifications critiques.
 * Fallback sur l'email admin principal si la configuration service role n'est pas disponible.
 */
export async function getAdminNotificationEmails(): Promise<string[]> {
  const fallback = [getAdminEmail()].filter((email): email is string => Boolean(email));

  if (!SUPABASE_URL || !SERVICE_ROLE_KEY) {
    console.warn("‚ö†Ô∏è Impossible de r√©cup√©rer les emails des admins/mod√©rateurs sans SUPABASE_SERVICE_ROLE_KEY");
    return fallback;
  }

  try {
    const supabase = createSupabaseClient(SUPABASE_URL, SERVICE_ROLE_KEY);

    const { data: roleRows, error: roleError } = await supabase
      .from("user_roles")
      .select("user_id, role")
      .in("role", ADMIN_ROLES);

    if (roleError) {
      console.error("‚ùå Erreur lors de la r√©cup√©ration des r√¥les admin/mod√©rateur:", roleError);
      return fallback;
    }

    const userIds = Array.from(
      new Set<string>(
        (roleRows ?? [])
          .map((row) => row.user_id)
          .filter((id): id is string => Boolean(id))
      )
    );

    if (userIds.length === 0) {
      console.warn("‚ö†Ô∏è Aucun utilisateur avec un r√¥le admin/mod√©rateur trouv√©.");
      return fallback;
    }

    const uniqueEmails = new Set<string>(fallback as string[]);
    const perPage = 200;
    let page = 1;
    let fetchedAll = false;

    while (!fetchedAll) {
      const { data, error } = await supabase.auth.admin.listUsers({
        page,
        perPage,
      });

      if (error) {
        console.error("‚ùå Erreur lors de la r√©cup√©ration des utilisateurs admin/mod√©rateur:", error);
        break;
      }

      const users = data?.users ?? [];
      users.forEach((user) => {
        if (user.email && userIds.includes(user.id)) {
          uniqueEmails.add(user.email.toLowerCase());
        }
      });

      fetchedAll = !users.length || users.length < perPage || uniqueEmails.size >= userIds.length;
      page += 1;
    }

    return Array.from(uniqueEmails);
  } catch (error) {
    console.error("‚ùå Erreur inattendue dans getAdminNotificationEmails:", error);
    return fallback;
  }
}

