    await sendEmail({
      to: userInfo.email,
      subject: `‚è∞ Votre acc√®s temporaire expire dans ${hoursRemaining}h`,
      react: AccessExpiring({
        userName: userInfo.name,
        permission,
        expiresAt,
        hoursRemaining,
        teamName,
        requestUrl: `${BASE_URL}/gestion`,
      }),
    });

    return { success: true };
  } catch (error: any) {
    console.error("[Access Expiring] Error:", error);
    return { success: false };
  }
}

import { ContractData } from './contract-template';
import { format } from 'date-fns';
import { fr } from 'date-fns/locale';

/**
 * Remplace les placeholders dans le texte par les valeurs du contrat
 * Format support√©: {{objet.propriete}} ou {{objet.sous_objet.propriete}}
 * Ex: {{tenant.firstName}}, {{lease.monthlyRent}}
 */
export function replacePlaceholders(template: string, data: ContractData): string {
    if (!template) return '';

    // Fonction r√©cursive pour acc√©der aux propri√©t√©s imbriqu√©es
    const getValue = (obj: any, path: string): any => {
        return path.split('.').reduce((acc, part) => acc && acc[part], obj);
    };

    // Formatage sp√©cial pour certains champs
    const formatValue = (value: any, key: string): string => {
        if (value instanceof Date) {
            return format(value, 'dd MMMM yyyy', { locale: fr });
        }

        if (typeof value === 'number') {
            // Format mon√©taire pour les champs li√©s √† l'argent
            if (key.toLowerCase().includes('rent') ||
                key.toLowerCase().includes('amount') ||
                key.toLowerCase().includes('price') ||
                key.toLowerCase().includes('deposit') ||
                key.toLowerCase().includes('charges')) {
                return value.toLocaleString('fr-FR');
            }
            return value.toString();
        }

        return value || '';
    };

    // Remplacement des patterns {{...}}
    return template.replace(/\{\{([^}]+)\}\}/g, (match, key) => {
        const trimmedKey = key.trim();
        const value = getValue(data, trimmedKey);

        // Si la valeur existe, on la formate, sinon on laisse une chaine vide (ou le placeholder pour debug)
        if (value !== undefined && value !== null) {
            return formatValue(value, trimmedKey);
        }

        // Cas sp√©ciaux calcul√©s
        if (trimmedKey === 'lease.totalRent') {
            const rent = data.lease.monthlyRent || 0;
            const charges = data.lease.charges || 0;
            return formatValue(rent + charges, 'totalRent');
        }

        if (trimmedKey === 'landlord.fullName') {
            if (data.landlord.companyName) return data.landlord.companyName;
            return `${data.landlord.firstName} ${data.landlord.lastName}`;
        }

        if (trimmedKey === 'tenant.fullName') {
            return `${data.tenant.firstName} ${data.tenant.lastName}`;
        }

        // Mappings sp√©cifiques pour les templates fran√ßais (contract-defaults.ts uses French keys)
        const frenchMappings: Record<string, string> = {
            'montant_caution': 'lease.securityDeposit',
            'montant_loyer': 'lease.monthlyRent',
            'duree_bail': 'lease.duration',
            'date_debut': 'lease.startDate',
            'jour_paiement': 'lease.billingDay',
            'adresse_bien': 'property.address',
            'description_bien': 'property.description',
            'r√©partition_frais': 'lease.frais' // Pas standard, mais au cas o√π
        };

        if (frenchMappings[trimmedKey]) {
            const mappedPath = frenchMappings[trimmedKey];
            const mappedValue = getValue(data, mappedPath);
            if (mappedValue !== undefined && mappedValue !== null) {
                return formatValue(mappedValue, mappedPath);
            }
        }

        return ''; // Ou `[${trimmedKey}]` pour voir les manquants
    });
}

/**
 * G√©n√©rateur de PDF pour Contrats de Bail
 * Utilise pdf-lib pour cr√©er des PDFs professionnels
 *
 * Features:
 * - Mise en page professionnelle avec marges
 * - Support des signatures num√©riques (images)
 * - Logo du propri√©taire
 * - Conformit√© visuelle S√©n√©gal
 */

import { PDFDocument, rgb, StandardFonts, PDFFont, PDFPage, degrees } from 'pdf-lib';
import { generateContractText, ContractData, validateContractData } from './contract-template';
import { DEFAULT_CONTRACT_TEXTS, ContractCustomTexts } from './contract-defaults';
import { replacePlaceholders } from './pdf-generator-helper';
import { format } from 'date-fns';
import { fr } from 'date-fns/locale';

const COLORS = {
  primary: rgb(0.2, 0.2, 0.6),
  secondary: rgb(0.4, 0.4, 0.4),
  text: rgb(0, 0, 0),
  lightGray: rgb(0.95, 0.95, 0.95),
  mediumGray: rgb(0.8, 0.8, 0.8),
};

interface PDFGenerationOptions {
  includeWatermark?: boolean;
  watermarkText?: string;
  logoUrl?: string;
}

interface PDFGenerationResult {
  success: boolean;
  pdfBytes?: Uint8Array;
  error?: string;
}

/**
 * G√©n√®re un PDF de contrat de bail √† partir des donn√©es
 */
/**
 * G√©n√®re un PDF de contrat de bail √† partir des donn√©es
 */
export async function generateLeasePDF(
  data: ContractData,
  customTexts: ContractCustomTexts = {},
  options: PDFGenerationOptions = {}
): Promise<PDFGenerationResult> {
  try {
    // 1. Validation des donn√©es
    const validation = validateContractData(data);
    if (!validation.valid) {
      return {
        success: false,
        error: `Donn√©es invalides: ${validation.errors.join(', ')}`
      };
    }

    // 2. Cr√©er un nouveau document PDF
    const pdfDoc = await PDFDocument.create();

    // 3. Charger les polices
    const fontBold = await pdfDoc.embedFont(StandardFonts.HelveticaBold);
    const fontRegular = await pdfDoc.embedFont(StandardFonts.Helvetica);

    // 4. Contenu du contrat (Header, Parties, Articles via CMS)
    await addContractContent(pdfDoc, data, customTexts, fontRegular, fontBold, options);

    // 6. Signatures int√©gr√©es dans addContractContent

    // 7. Ajouter le watermark si demand√© (brouillon, etc.)
    if (options.includeWatermark && options.watermarkText) {
      await addWatermark(pdfDoc, options.watermarkText);
    }

    // 8. Sauvegarder et retourner
    const pdfBytes = await pdfDoc.save();

    return {
      success: true,
      pdfBytes
    };

  } catch (error) {
    console.error('Erreur g√©n√©ration PDF:', error);
    return {
      success: false,
      error: error instanceof Error ? error.message : 'Erreur inconnue'
    };
  }
}

/**
 * Ajoute le contenu structur√© du contrat : Header, Grille Parties, Articles
 */
async function addContractContent(
  pdfDoc: PDFDocument,
  data: ContractData,
  customTexts: ContractCustomTexts,
  fontRegular: PDFFont,
  fontBold: PDFFont,
  options: PDFGenerationOptions
): Promise<void> {
  const pageWidth = 595.28; // A4 width
  const pageHeight = 841.89; // A4 height
  const margin = 50;

  // Cr√©ation de la premi√®re page
  let currentPage = pdfDoc.addPage([pageWidth, pageHeight]);
  let currentY = pageHeight - margin;

  // --- 1. HEADER (Logo + Titre) ---
  currentY = await drawHeader(pdfDoc, currentPage, data, options, currentY, pageWidth, margin, fontBold);

  // --- 2. PARTIES (Grille Bailleur / Locataire) ---
  currentY = drawPartiesGrid(currentPage, data, currentY, pageWidth, margin, fontRegular, fontBold);

  // Espace apr√®s la grille
  currentY -= 30;

  // --- 3. CORPS (Articles CMS) ---
  const lineSpacing = 14;
  const maxWidth = pageWidth - (2 * margin);

  // Fusion des textes
  const finalTexts = { ...DEFAULT_CONTRACT_TEXTS, ...customTexts };

  const articles = [
    { title: "ARTICLE 1 : OBJET DU BAIL ET DESCRIPTION DU BIEN", content: finalTexts.article_1_objet },
    { title: "ARTICLE 2 : DESTINATION DES LIEUX", content: finalTexts.article_2_destination },
    { title: "ARTICLE 3 : DUR√âE DU BAIL", content: finalTexts.article_3_duree },
    { title: "ARTICLE 4 : LOYER", content: finalTexts.article_4_loyer },
    { title: "ARTICLE 5 : CHARGES ET CONSOMMATIONS", content: finalTexts.article_5_charges },
    { title: "ARTICLE 6 : D√âP√îT DE GARANTIE (CAUTION)", content: finalTexts.article_6_caution },
    { title: "ARTICLE 7 : OBLIGATIONS DU PRENEUR", content: finalTexts.article_7_obligations_preneur },
    { title: "ARTICLE 8 : OBLIGATIONS DU BAILLEUR", content: finalTexts.article_8_obligations_bailleur },
    { title: "ARTICLE 9 : R√âSILIATION ET PR√âAVIS", content: finalTexts.article_9_resiliation },
    { title: "ARTICLE 10 : CLAUSE R√âSOLUTOIRE", content: finalTexts.article_10_clause_resolutoire },
    { title: "ARTICLE 11 : √âLECTION DE DOMICILE ET JURIDICTION COMP√âTENTE", content: finalTexts.article_11_election_domicile },
    { title: "ARTICLE 12 : FRAIS ET EXEMPLAIRES", content: finalTexts.article_12_frais },
  ];

  // Titre introductif
  currentPage.drawText("IL A ETE CONVENU ET ARRETE CE QUI SUIT :", {
    x: margin,
    y: currentY,
    size: 10,
    font: fontBold,
    color: COLORS.text,
  });
  currentY -= (lineSpacing * 2);

  // Boucle de rendu des articles
  for (const art of articles) {
    // V√©rif nouvelle page (avec marge de s√©curit√©)
    if (currentY < 100) {
      drawFooter(currentPage, pdfDoc.getPageCount(), pageWidth, fontRegular);
      currentPage = pdfDoc.addPage([pageWidth, pageHeight]);
      currentY = pageHeight - margin;
    }

    // Titre de l'article
    currentPage.drawText(art.title, { x: margin, y: currentY, size: 11, font: fontBold, color: COLORS.text });

    // Soulignage du titre
    const titleWidth = fontBold.widthOfTextAtSize(art.title, 11);
    currentPage.drawLine({
      start: { x: margin, y: currentY - 2 },
      end: { x: margin + titleWidth, y: currentY - 2 },
      thickness: 1,
      color: COLORS.text,
    });

    currentY -= 15;

    // Remplacement des variables
    const contentWithVars = replacePlaceholders(art.content || "", data);

    // Contenu (Wrapp√©)
    const lines = wrapText(contentWithVars, fontRegular, 10, maxWidth);
    for (const line of lines) {
      // V√©rif saut de page intra-article
      if (currentY < margin + 20) {
        drawFooter(currentPage, pdfDoc.getPageCount(), pageWidth, fontRegular);
        currentPage = pdfDoc.addPage([pageWidth, pageHeight]);
        currentY = pageHeight - margin;
      }

      currentPage.drawText(line, { x: margin, y: currentY, size: 10, font: fontRegular, color: rgb(0.1, 0.1, 0.1) });
      currentY -= lineSpacing;
    }
    currentY -= 15; // Espace entre articles
  }

  // --- CLAUSES PARTICULI√àRES (AJOUT) ---
  if (customTexts.custom_clauses && customTexts.custom_clauses.trim() !== "") {
    if (currentY < 100) {
      drawFooter(currentPage, pdfDoc.getPageCount(), pageWidth, fontRegular);
      currentPage = pdfDoc.addPage([pageWidth, pageHeight]);
      currentY = pageHeight - margin;
    }

    const customTitle = "CONDITIONS PARTICULI√àRES";
    currentPage.drawText(customTitle, { x: margin, y: currentY, size: 11, font: fontBold, color: COLORS.text });

    const cw = fontBold.widthOfTextAtSize(customTitle, 11);
    currentPage.drawLine({
      start: { x: margin, y: currentY - 2 },
      end: { x: margin + cw, y: currentY - 2 },
      thickness: 1,
      color: COLORS.text,
    });

    currentY -= 15;

    const lines = wrapText(customTexts.custom_clauses, fontRegular, 10, maxWidth);
    for (const line of lines) {
      if (currentY < margin + 20) {
        drawFooter(currentPage, pdfDoc.getPageCount(), pageWidth, fontRegular);
        currentPage = pdfDoc.addPage([pageWidth, pageHeight]);
        currentY = pageHeight - margin;
      }
      currentPage.drawText(line, { x: margin, y: currentY, size: 10, font: fontRegular, color: rgb(0.1, 0.1, 0.1) });
      currentY -= lineSpacing;
    }
    currentY -= 20;
  }

  // --- SECTION SIGNATURES ---
  // On s'assure d'avoir assez de place (150px min), sinon nouvelle page
  if (currentY < 150) {
    drawFooter(currentPage, pdfDoc.getPageCount(), pageWidth, fontRegular);
    currentPage = pdfDoc.addPage([pageWidth, pageHeight]);
    currentY = pageHeight - margin;
  }

  currentY -= 30; // Petit espace avant la zone

  // Titre de section
  currentPage.drawText("SIGNATURES", { x: margin, y: currentY, size: 12, font: fontBold, color: COLORS.primary });
  currentPage.drawLine({
    start: { x: margin, y: currentY - 5 },
    end: { x: pageWidth - margin, y: currentY - 5 },
    thickness: 1,
    color: COLORS.primary // Primary color
  });

  currentY -= 40; // Espace sous la ligne

  // --- COLONNE GAUCHE : BAILLEUR ---
  const leftColX = margin;
  currentPage.drawText("LE BAILLEUR (Propri√©taire)", { x: leftColX, y: currentY, size: 10, font: fontBold, color: COLORS.primary });
  currentPage.drawText("Lu et approuv√©", { x: leftColX, y: currentY - 15, size: 9, font: fontRegular, color: COLORS.secondary });

  // Signature Bailleur
  if (data.signatures.landlordSignatureUrl) {
    try {
      const sigImage = await fetchAndEmbedImage(pdfDoc, data.signatures.landlordSignatureUrl);
      if (sigImage) {
        const sigWidth = 120;
        const sigHeight = 60;
        const scaledDims = sigImage.scale(sigWidth / sigImage.width);
        currentPage.drawImage(sigImage, {
          x: leftColX,
          y: currentY - 80,
          width: Math.min(scaledDims.width, sigWidth),
          height: Math.min(scaledDims.height, sigHeight),
        });
      }
    } catch (e) { console.warn("Erreur signature bailleur", e); }
  }


  // --- COLONNE DROITE : PRENEUR ---
  const rightColX = pageWidth / 2 + 20; // On commence apr√®s le milieu
  currentPage.drawText("LE PRENEUR (Locataire)", { x: rightColX, y: currentY, size: 10, font: fontBold, color: COLORS.primary });
  currentPage.drawText("Lu et approuv√©", { x: rightColX, y: currentY - 15, size: 9, font: fontRegular, color: COLORS.secondary });

  // Signature Preneur
  if (data.signatures.tenantSignatureUrl) {
    try {
      const sigImage = await fetchAndEmbedImage(pdfDoc, data.signatures.tenantSignatureUrl);
      if (sigImage) {
        const sigWidth = 120;
        const sigHeight = 60;
        const scaledDims = sigImage.scale(sigWidth / sigImage.width);
        currentPage.drawImage(sigImage, {
          x: rightColX,
          y: currentY - 80,
          width: Math.min(scaledDims.width, sigWidth),
          height: Math.min(scaledDims.height, sigHeight),
        });
      }
    } catch (_e) {
      console.warn("Pas de signature locataire charg√©e");
    }
  }


  // Footer pour la derni√®re page
  drawFooter(currentPage, pdfDoc.getPageCount(), pageWidth, fontRegular);

  // Mettre √† jour les num√©ros de page
  const totalPages = pdfDoc.getPageCount();
  const pages = pdfDoc.getPages();
  pages.forEach((page, idx) => {
    page.drawText(`Page ${idx + 1} sur ${totalPages}`, {
      x: pageWidth - margin - 80,
      y: 30,
      size: 9,
      font: fontRegular,
      color: COLORS.secondary,
    });

    page.drawLine({
      start: { x: margin, y: 45 },
      end: { x: pageWidth - margin, y: 45 },
      thickness: 0.5,
      color: COLORS.mediumGray,
    });
  });
}

// --- HELPERS VISUELS ---

/** Dessine l'en-t√™te avec Logo et Titre Encadr√© (Empil√©s) */
async function drawHeader(
  pdfDoc: PDFDocument,
  page: PDFPage,
  data: ContractData,
  options: PDFGenerationOptions,
  startY: number,
  pageWidth: number,
  margin: number,
  fontBold: PDFFont
): Promise<number> {
  let currentY = startY;

  // 1. Logo √† Gauche (s'il existe)
  if (options.logoUrl) {
    try {
      const logoImage = await fetchAndEmbedImage(pdfDoc, options.logoUrl);
      if (logoImage) {
        const logoHeight = 40;
        const logoDims = logoImage.scale(logoHeight / logoImage.height);
        page.drawImage(logoImage, {
          x: margin, // Gauche
          y: currentY - logoHeight,
          width: logoDims.width,
          height: logoDims.height,
        });

        // On descend le curseur Y sous le logo + marge
        currentY -= (logoHeight + 20);
      }
    } catch (e) { console.warn("Erreur logo", e); }
  }

  // 2. Titre Centr√© et Encadr√© (Sous le logo)
  const title = "CONTRAT DE BAIL A USAGE D'HABITATION";
  const subTitle = "R√©publique du S√©n√©gal";
  const textSize = 14;
  const titleWidth = fontBold.widthOfTextAtSize(title, textSize);
  const boxWidth = titleWidth + 40;
  const boxHeight = 50;
  const boxX = (pageWidth - boxWidth) / 2;
  const boxY = currentY - boxHeight; // Positionn√© par rapport au nouveau currentY

  // Fond gris l√©ger
  page.drawRectangle({
    x: boxX,
    y: boxY,
    width: boxWidth,
    height: boxHeight,
    color: COLORS.lightGray,
    borderColor: COLORS.mediumGray,
    borderWidth: 1,
  });

  // Texte Titre
  page.drawText(title, {
    x: (pageWidth - titleWidth) / 2,
    y: boxY + 25,
    size: textSize,
    font: fontBold,
    color: COLORS.primary, // Primary Color
  });

  // Sous-titre
  const subSize = 10;
  const subWidth = fontBold.widthOfTextAtSize(subTitle, subSize);
  page.drawText(subTitle, {
    x: (pageWidth - subWidth) / 2,
    y: boxY + 10,
    size: subSize,
    font: fontBold,
    color: COLORS.secondary,
  });

  return boxY - 30; // Espace apr√®s header
}

/** Dessine la Grille des Parties (Bailleur / Preneur) */
function drawPartiesGrid(
  page: PDFPage,
  data: ContractData,
  startY: number,
  pageWidth: number,
  margin: number,
  fontRegular: PDFFont,
  fontBold: PDFFont
): number {
  const { landlord, tenant } = data;
  const boxHeight = 160;
  const colWidth = (pageWidth - (2 * margin) - 10) / 2; // -10 pour gap au milieu
  const col1X = margin;
  const col2X = margin + colWidth + 10;

  // Fond global gris tr√®s clair pour la zone
  page.drawRectangle({
    x: margin,
    y: startY - boxHeight,
    width: pageWidth - (2 * margin),
    height: boxHeight,
    color: rgb(0.97, 0.97, 0.97),
    borderColor: rgb(0.9, 0.9, 0.9),
    borderWidth: 1,
  }); // Erreur possible si boxHeight trop petit pour le contenu, on assume A4 fixe

  let textY = startY - 20;

  // --- COLONNE 1 : BAILLEUR ---
  page.drawText("LE BAILLEUR", { x: col1X + 10, y: textY, size: 10, font: fontBold, color: rgb(0.2, 0.2, 0.6) });
  textY -= 15;

  const lLines = [
    landlord.companyName ? `Soci√©t√© : ${landlord.companyName}` : `M./Mme : ${landlord.firstName} ${landlord.lastName}`,
    landlord.companyName && landlord.ninea ? `NINEA : ${landlord.ninea}` : '',
    `Adresse : ${landlord.address}`,
    `Tel : ${landlord.phone}`,
    landlord.email ? `Email : ${landlord.email}` : ''
  ].filter(l => l);

  let lY = textY;
  for (const l of lLines) {
    // Wrap si n√©cessaire (adresse longue)
    const wrapped = wrapText(l, fontRegular, 9, colWidth - 20);
    for (const wL of wrapped) {
      page.drawText(wL, { x: col1X + 10, y: lY, size: 9, font: fontRegular, color: rgb(0.1, 0.1, 0.1) });
      lY -= 12;
    }
  }

  // --- COLONNE 2 : PRENEUR ---
  textY = startY - 20; // Reset Y pour colonne 2
  page.drawText("LE PRENEUR", { x: col2X + 10, y: textY, size: 10, font: fontBold, color: rgb(0.2, 0.2, 0.6) });
  textY -= 15;

  const tLines = [
    `M./Mme : ${tenant.firstName} ${tenant.lastName}`,
    tenant.nationalId ? `CNI / Passeport : ${tenant.nationalId}` : '',
    `Tel : ${tenant.phone}`,
    tenant.email ? `Email : ${tenant.email}` : ''
  ].filter(l => l);

  let tY = textY;
  for (const l of tLines) {
    const wrapped = wrapText(l, fontRegular, 9, colWidth - 20);
    for (const wL of wrapped) {
      page.drawText(wL, { x: col2X + 10, y: tY, size: 9, font: fontRegular, color: rgb(0.1, 0.1, 0.1) });
      tY -= 12;
    }
  }

  // Ligne verticale de s√©paration
  page.drawLine({
    start: { x: margin + colWidth + 5, y: startY - 10 },
    end: { x: margin + colWidth + 5, y: startY - boxHeight + 10 },
    color: rgb(0.8, 0.8, 0.8),
