/**
 * Wrapper le texte pour qu'il tienne dans la largeur maximale, en respectant les sauts de ligne
 */
function wrapText(text: string, font: PDFFont, fontSize: number, maxWidth: number): string[] {
  // 1. Normaliser les sauts de ligne et ASSAINIR le texte (Char non support√©s par WinAnsi - ex 0x202f)
  const normalizedText = text
    .replace(/\r\n/g, '\n')
    .replace(/\r/g, '\n')
    // Espace inc√©sable √©troit (0x202f) et autres espaces bizarres -> espace simple
    .replace(/[\u00A0\u2000-\u200B\u202F\u205F\u3000]/g, ' ')
    // Guillemets et apostrophes
    .replace(/[¬´¬ª‚Äú‚Äù]/g, '"')
    .replace(/[‚Äò‚Äô]/g, "'")
    // Tirets
    .replace(/[‚Äì‚Äî]/g, '-')
    // Points de suspension
    .replace(/‚Ä¶/g, '...');

  // 2. D√©couper en paragraphes
  const paragraphs = normalizedText.split('\n');
  const allLines: string[] = [];

  for (const paragraph of paragraphs) {
    if (paragraph.trim() === '') {
      // Conserver les sauts de ligne vides (paragraphes vides)
      // On ajoute une ligne vide si ce n'est pas la toute premi√®re (√©vite marge top excessive)
      // mais ici on veut respecter le format user.
      allLines.push('');
      continue;
    }

    const words = paragraph.split(' ');
    let currentLine = '';

    for (const word of words) {
      const testLine = currentLine ? `${currentLine} ${word}` : word;
      const width = font.widthOfTextAtSize(testLine, fontSize);

      if (width <= maxWidth) {
        currentLine = testLine;
      } else {
        if (currentLine) {
          allLines.push(currentLine);
        }
        currentLine = word;
      }
    }

    if (currentLine) {
      allLines.push(currentLine);
    }
  }

  return allLines.length > 0 ? allLines : [''];
}

