/**
 * R√©cup√®re et embed une image depuis une URL
 */
async function fetchAndEmbedImage(pdfDoc: PDFDocument, imageUrl: string) {
  try {
    const response = await fetch(imageUrl);
    const imageBytes = await response.arrayBuffer();

    // D√©terminer le type d'image
    if (imageUrl.toLowerCase().endsWith('.png')) {
      return await pdfDoc.embedPng(new Uint8Array(imageBytes) as any);
    } else if (imageUrl.toLowerCase().endsWith('.jpg') || imageUrl.toLowerCase().endsWith('.jpeg')) {
      return await pdfDoc.embedJpg(new Uint8Array(imageBytes) as any);
    } else {
      console.warn('Format d\'image non support√©:', imageUrl);
      return null;
    }
  } catch (err) {
    console.error('Erreur chargement image:', err);
    return null;
  }
}

/**
 * Sauvegarde le PDF en fichier (pour Node.js)
 */
export async function savePDFToFile(pdfBytes: Uint8Array, filename: string): Promise<void> {
  if (typeof window === 'undefined') {
    // Environnement Node.js
    const fs = await import('fs');
    fs.writeFileSync(filename, pdfBytes);
  } else {
    // Environnement navigateur
    const blob = new Blob([pdfBytes as any], { type: 'application/pdf' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = filename;
    link.click();
    URL.revokeObjectURL(url);
  }
}

/**
 * Upload le PDF vers Supabase Storage
 */
export async function uploadPDFToStorage(
  pdfBytes: Uint8Array,
  filename: string,
  supabaseClient: any
): Promise<{ success: boolean; url?: string; error?: string }> {
  try {
    const { _data, error } = await supabaseClient.storage
      .from('lease-contracts') // Bucket priv√©
      .upload(filename, pdfBytes, {
        contentType: 'application/pdf',
        upsert: true
      });

    if (error) {
      return { success: false, error: error.message };
    }

    // Cr√©er une URL sign√©e (valide 1 an = 31536000 secondes) pour bucket priv√©
    const { data: signedData, error: signedError } = await supabaseClient.storage
      .from('lease-contracts')
      .createSignedUrl(filename, 31536000);

    if (signedError) {
      console.error('Erreur cr√©ation URL sign√©e:', signedError);
      // Fallback: retourner le chemin du fichier √† la place
      return {
        success: true,
        url: `storage://lease-contracts/${filename}` // URL interne pour r√©f√©rence
      };
    }

    return {
      success: true,
      url: signedData.signedUrl
    };

  } catch (error) {
    return {
      success: false,
      error: error instanceof Error ? error.message : 'Erreur inconnue'
    };
  }
}

/**
 * AI Rate Limiter avec Redis ZSET (Atomic Operations)
 * 
 * Impl√©mentation robuste avec:
 * - Sliding window algorithm
 * - Op√©rations atomiques via ZSET (√©vite race conditions)
 * - Fallback String/JSON si ZSET non disponible
 * - Fail-open strategy (autoriser si Redis down)
 * 
 * @see implementation_plan.md pour d√©tails techniques
 */

import { redis, getRedisClient } from '@/lib/cache/redis-client';
import { AI_RATE_LIMIT_CONFIG, type RateLimitResult } from './types';

const REDIS_KEY_PREFIX = 'ratelimit:ai:';

/**
 * V√©rifie si l'√©quipe peut faire un appel IA
 * 
 * @param teamId - ID de l'√©quipe
 * @returns RateLimitResult avec allowed, remaining, resetAt
 * 
 * @example
 * const result = await checkAIRateLimit('team-123');
 * if (!result.allowed) {
 *   return { error: `Limite atteinte. ${result.remaining} appels restants.` };
 * }
 */
export async function checkAIRateLimit(teamId: string): Promise<RateLimitResult> {
    const key = `${REDIS_KEY_PREFIX}${teamId}`;
    const now = Date.now();
    const windowStart = now - (AI_RATE_LIMIT_CONFIG.windowSeconds * 1000);
    const { maxRequests } = AI_RATE_LIMIT_CONFIG;

    try {
        const client = getRedisClient();

        // ‚úÖ Fail-open: Si Redis indisponible, autoriser
        if (!client) {
            console.warn('‚ö†Ô∏è Redis unavailable. AI rate limit bypassed (fail-open).');
            return {
                allowed: true,
                remaining: maxRequests,
                resetAt: new Date(now + AI_RATE_LIMIT_CONFIG.windowSeconds * 1000),
            };
        }

        // D√©terminer la strat√©gie (ZSET ou String/JSON)
        const isUpstash = !!process.env.UPSTASH_REDIS_REST_URL;
        const supportsZSET = !isUpstash; // ioredis supporte ZSET nativement

        if (supportsZSET) {
            return await checkWithZSET(client, key, now, windowStart, maxRequests);
        } else {
            return await checkWithStringFallback(key, now, windowStart, maxRequests);
        }
    } catch (error) {
        // ‚úÖ Fail-open: En cas d'erreur, autoriser mais logger
        console.error('‚ùå AI Rate Limit error (fail-open):', error);
        return {
            allowed: true,
            remaining: maxRequests,
            resetAt: new Date(now + AI_RATE_LIMIT_CONFIG.windowSeconds * 1000),
            error: 'Rate limit check failed (allowed by default)',
        };
    }
}

/**
 * Strat√©gie ZSET (Atomic) - Recommand√©e pour ioredis
 * 
 * Op√©rations:
 * 1. ZREMRANGEBYSCORE - Nettoyer timestamps expir√©s
 * 2. ZCARD - Compter appels dans fen√™tre
 * 3. ZADD - Ajouter nouveau timestamp si autoris√©
 * 4. EXPIRE - Auto-nettoyage apr√®s 1h
 */
async function checkWithZSET(
    client: any,
    key: string,
    now: number,
    windowStart: number,
    maxRequests: number
): Promise<RateLimitResult> {
    // 1. Nettoyer les vieux timestamps (hors fen√™tre)
    await client.zremrangebyscore(key, '-inf', windowStart);

    // 2. Compter les appels dans la fen√™tre
    const count = await client.zcard(key);

    // 3. V√©rifier la limite
    if (count >= maxRequests) {
        // Bloquer: limite atteinte
        const oldestTimestamp = await client.zrange(key, 0, 0, 'WITHSCORES');
        const resetAt = oldestTimestamp[1]
            ? new Date(parseInt(oldestTimestamp[1]) + AI_RATE_LIMIT_CONFIG.windowSeconds * 1000)
            : new Date(now + AI_RATE_LIMIT_CONFIG.windowSeconds * 1000);

        console.warn(`‚ö†Ô∏è AI Rate limit exceeded for team ${key.replace(REDIS_KEY_PREFIX, '')} (${count}/${maxRequests})`);

        return {
            allowed: false,
            remaining: 0,
            resetAt,
            error: `Limite d'appels IA atteinte (${count}/${maxRequests})`,
        };
    }

    // 4. Autoriser: ajouter le timestamp actuel
    const uniqueId = `${now}-${Math.random().toString(36).substring(7)}`;
    await client.zadd(key, now, uniqueId);

    // 5. D√©finir expiration (auto-cleanup apr√®s 1h)
    await client.expire(key, AI_RATE_LIMIT_CONFIG.windowSeconds);

    console.log(`‚úÖ AI call allowed (${count + 1}/${maxRequests}) for team ${key.replace(REDIS_KEY_PREFIX, '')}`);

    return {
        allowed: true,
        remaining: maxRequests - count - 1,
        resetAt: new Date(now + AI_RATE_LIMIT_CONFIG.windowSeconds * 1000),
    };
}

/**
 * Strat√©gie String/JSON (Fallback) - Pour Upstash ou environnements limit√©s
 * 
 * Note: Pas atomique, potentielle race condition, mais acceptable pour faible volume
 */
async function checkWithStringFallback(
    key: string,
    now: number,
    windowStart: number,
    maxRequests: number
): Promise<RateLimitResult> {
    // 1. R√©cup√©rer les timestamps existants
    const raw = await redis.get(key);
    const timestamps = raw ? JSON.parse(raw) : [];

    // 2. Filtrer les timestamps dans la fen√™tre
    const validTimestamps = timestamps.filter((ts: number) => ts > windowStart);

    // 3. V√©rifier la limite
    if (validTimestamps.length >= maxRequests) {
        const resetAt = new Date(Math.min(...validTimestamps) + AI_RATE_LIMIT_CONFIG.windowSeconds * 1000);

        console.warn(`‚ö†Ô∏è AI Rate limit exceeded (fallback) for ${key.replace(REDIS_KEY_PREFIX, '')} (${validTimestamps.length}/${maxRequests})`);

        return {
            allowed: false,
            remaining: 0,
            resetAt,
            error: `Limite d'appels IA atteinte (${validTimestamps.length}/${maxRequests})`,
        };
    }

    // 4. Autoriser: ajouter timestamp actuel
    validTimestamps.push(now);
    await redis.set(key, JSON.stringify(validTimestamps), AI_RATE_LIMIT_CONFIG.windowSeconds);

    console.log(`‚úÖ AI call allowed (fallback) (${validTimestamps.length}/${maxRequests}) for ${key.replace(REDIS_KEY_PREFIX, '')}`);

    return {
        allowed: true,
        remaining: maxRequests - validTimestamps.length,
        resetAt: new Date(now + AI_RATE_LIMIT_CONFIG.windowSeconds * 1000),
    };
}

/**
 * R√©initialise le rate limit pour une √©quipe (utile pour tests ou admin)
 */
export async function resetAIRateLimit(teamId: string): Promise<void> {
    const key = `${REDIS_KEY_PREFIX}${teamId}`;
    await redis.del(key);
    console.log(`üîÑ AI rate limit reset for team ${teamId}`);
}

/**
 * R√©cup√®re le statut actuel du rate limit sans le modifier
 */
export async function getAIRateLimitStatus(teamId: string): Promise<{
    count: number;
    remaining: number;
    resetAt: Date;
}> {
    const key = `${REDIS_KEY_PREFIX}${teamId}`;
    const now = Date.now();
    const windowStart = now - (AI_RATE_LIMIT_CONFIG.windowSeconds * 1000);
    const { maxRequests } = AI_RATE_LIMIT_CONFIG;

    try {
        const client = await getRedisClient();
        if (!client) {
            return {
                count: 0,
                remaining: maxRequests,
                resetAt: new Date(now + AI_RATE_LIMIT_CONFIG.windowSeconds * 1000),
            };
        }

        const isUpstash = !!process.env.UPSTASH_REDIS_REST_URL;

        if (!isUpstash) {
            // ZSET
            await client.zremrangebyscore(key, '-inf', windowStart);
            const count = await client.zcard(key);
            return {
                count,
                remaining: Math.max(0, maxRequests - count),
                resetAt: new Date(now + AI_RATE_LIMIT_CONFIG.windowSeconds * 1000),
            };
        } else {
            // String/JSON
            const raw = await redis.get(key);
            const timestamps = raw ? JSON.parse(raw) : [];
            const validTimestamps = timestamps.filter((ts: number) => ts > windowStart);
            return {
                count: validTimestamps.length,
                remaining: Math.max(0, maxRequests - validTimestamps.length),
                resetAt: new Date(now + AI_RATE_LIMIT_CONFIG.windowSeconds * 1000),
            };
        }
    } catch (error) {
        console.error('‚ùå Error getting rate limit status:', error);
        return {
            count: 0,
            remaining: maxRequests,
            resetAt: new Date(now + AI_RATE_LIMIT_CONFIG.windowSeconds * 1000),
        };
    }
}

import { SupabaseClient } from "@supabase/supabase-js";
import { differenceInDays } from "date-fns";
import { sendEmail } from "@/lib/mail";

// Types
export interface ReminderResult {
    count: number;
    message: string;
    errors?: Error[];
}

/**
 * Core logic to process reminders.
 * Accepts a Supabase client (either User scoped or Admin scoped).
 */
export async function internalProcessReminders(supabase: SupabaseClient): Promise<ReminderResult> {
    console.log("üïµÔ∏è Recherche des impay√©s de +5 jours...");

    const today = new Date();

    // Fetch ALL unpaid transactions that haven't been reminded yet
    // We'll calculate the actual due date using billing_day and filter in code
    // This ensures we respect the billing_day configured in the UI modal
    const { data: transactions, error } = await supabase
        .from("rental_transactions")
        .select(`
      id,
      amount_due,
      status,
      period_month,
      period_year,
      period_start,
      reminder_sent,
      leases (
        id,
        billing_day,
        tenant_email,
        tenant_name,
        property_id,
        owner_id
      )
    `)
        .neq("status", "paid") // Not paid
        .eq("reminder_sent", false) // Not reminded
        // Only fetch recent transactions (current year and previous year)
        .gte("period_year", today.getFullYear() - 1);

    if (error) {
        console.error("Error fetching transactions:", error);
        return { count: 0, message: "Erreur lors de la r√©cup√©ration des transactions.", errors: [error] };
    }

    console.log(`Found ${transactions?.length || 0} candidate transactions from DB.`);

    if (!transactions || transactions.length === 0) {
        return { count: 0, message: "Aucun paiement en retard d√©tect√©." };
    }

    let emailsSent = 0;
    const errors: Error[] = [];

    for (const tx of transactions) {
        console.log(`Checking transaction ${tx.id}: Status=${tx.status}, Period=${tx.period_month}/${tx.period_year}`);

        const lease = tx.leases;
        // Handle case where lease might be an array or null
        // Supabase can return array for relations depending on schema checks/client version
        const leaseData = Array.isArray(lease) ? lease[0] : lease;

        if (!leaseData) {
            console.log(`-> Skipped: No lease data found.`);
            continue;
        }

        const billingDay = leaseData.billing_day || 5;
        const periodYear = tx.period_year;
        const periodMonth = tx.period_month;

        let dueDate: Date;
        try {
            // Month in Date constructor is 0-indexed (0=Jan, 11=Dec)
            dueDate = new Date(periodYear, periodMonth - 1, billingDay);
        } catch (e) {
            console.warn(`-> Skipped: Invalid date for tx ${tx.id}`, e);
            continue;
        }

        const daysOverdue = differenceInDays(today, dueDate);
        console.log(`-> Due Date: ${dueDate.toISOString().split('T')[0]}, Days Overdue: ${daysOverdue}`);

        // "Relance Automatique J+5": Trigger if 5 or more days have passed since due date.
        if (daysOverdue >= 5) {
            if (leaseData.tenant_email) {
                try {
                    // R√©cup√©rer l'email du propri√©taire
                    let ownerEmail: string | undefined;
                    if (leaseData.owner_id) {
                        const { data: ownerData } = await supabase.auth.admin.getUserById(leaseData.owner_id);
                        ownerEmail = ownerData?.user?.email;
                    }

                    await sendReminderEmail(
                        leaseData.tenant_email,
                        leaseData.tenant_name || "Locataire",
                        tx.amount_due,
                        dueDate,
                        ownerEmail // Ajout du CC propri√©taire
                    );

                    const { error: updateError } = await supabase
                        .from("rental_transactions")
                        .update({ reminder_sent: true })
                        .eq("id", tx.id);

                    if (updateError) {
                        console.error(`Failed to update tx ${tx.id}`, updateError);
                        errors.push(updateError instanceof Error ? updateError : new Error(JSON.stringify(updateError)));
                    } else {
                        emailsSent++;
                    }
                } catch (err) {
                    console.error(`Failed to send email to ${leaseData.tenant_email}`, err);
                    errors.push(err instanceof Error ? err : new Error(String(err)));
                }
            }
        }
    }

    return {
        count: emailsSent,
        message: `${emailsSent} relances envoy√©es avec succ√®s.`,
        errors: errors.length > 0 ? errors : undefined
    };
}

/**
 * Real Email Sender using project's existing mailer
 */
async function sendReminderEmail(email: string, name: string, amount: number, date: Date, ownerEmail?: string) {
    const formattedDate = date.toLocaleDateString("fr-FR", { year: 'numeric', month: 'long', day: 'numeric' });
    const formattedAmount = amount.toLocaleString("fr-SN");

    const emailHtml = `
    <!DOCTYPE html>
    <html>
    <head>
      <style>
